(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([[41],{"PM/f":function(s,n){s.exports='<h1 id="let-\u548c-const-\u547d\u4ee4">let \u548c const \u547d\u4ee4</h1>\n<h2 id="let-\u547d\u4ee4">let \u547d\u4ee4</h2>\n<h3 id="\u57fa\u672c\u7528\u6cd5">\u57fa\u672c\u7528\u6cd5</h3>\n<p>ES6 \u65b0\u589e\u4e86<code>let</code>\u547d\u4ee4\uff0c\u7528\u6765\u58f0\u660e\u53d8\u91cf\u3002\u5b83\u7684\u7528\u6cd5\u7c7b\u4f3c\u4e8e<code>var</code>\uff0c\u4f46\u662f\u6240\u58f0\u660e\u7684\u53d8\u91cf\uff0c\u53ea\u5728<code>let</code>\u547d\u4ee4\u6240\u5728\u7684\u4ee3\u7801\u5757\u5185\u6709\u6548\u3002</p>\n<pre><code class="language-javascript">{\n  let a = <span class="hljs-number">10</span>;\n  var b = <span class="hljs-number">1</span>;\n}\n\na <span class="hljs-comment">// ReferenceError: a is not defined.</span>\nb <span class="hljs-comment">// 1</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u5728\u4ee3\u7801\u5757\u4e4b\u4e2d\uff0c\u5206\u522b\u7528<code>let</code>\u548c<code>var</code>\u58f0\u660e\u4e86\u4e24\u4e2a\u53d8\u91cf\u3002\u7136\u540e\u5728\u4ee3\u7801\u5757\u4e4b\u5916\u8c03\u7528\u8fd9\u4e24\u4e2a\u53d8\u91cf\uff0c\u7ed3\u679c<code>let</code>\u58f0\u660e\u7684\u53d8\u91cf\u62a5\u9519\uff0c<code>var</code>\u58f0\u660e\u7684\u53d8\u91cf\u8fd4\u56de\u4e86\u6b63\u786e\u7684\u503c\u3002\u8fd9\u8868\u660e\uff0c<code>let</code>\u58f0\u660e\u7684\u53d8\u91cf\u53ea\u5728\u5b83\u6240\u5728\u7684\u4ee3\u7801\u5757\u6709\u6548\u3002</p>\n<p><code>for</code>\u5faa\u73af\u7684\u8ba1\u6570\u5668\uff0c\u5c31\u5f88\u5408\u9002\u4f7f\u7528<code>let</code>\u547d\u4ee4\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">for</span> (let i = <span class="hljs-number">0</span>; i &lt; <span class="hljs-number">10</span>; i++) {\n  <span class="hljs-comment">// ...</span>\n}\n\nconsole.log(i);\n<span class="hljs-comment">// ReferenceError: i is not defined</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u8ba1\u6570\u5668<code>i</code>\u53ea\u5728<code>for</code>\u5faa\u73af\u4f53\u5185\u6709\u6548\uff0c\u5728\u5faa\u73af\u4f53\u5916\u5f15\u7528\u5c31\u4f1a\u62a5\u9519\u3002</p>\n<p>\u4e0b\u9762\u7684\u4ee3\u7801\u5982\u679c\u4f7f\u7528<code>var</code>\uff0c\u6700\u540e\u8f93\u51fa\u7684\u662f<code>10</code>\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> a = [];\n<span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> i = <span class="hljs-number">0</span>; i &lt; <span class="hljs-number">10</span>; i++) {\n  a[i] = <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n    <span class="hljs-built_in">console</span>.log(i);\n  };\n}\na[<span class="hljs-number">6</span>](); <span class="hljs-comment">// 10</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>i</code>\u662f<code>var</code>\u547d\u4ee4\u58f0\u660e\u7684\uff0c\u5728\u5168\u5c40\u8303\u56f4\u5185\u90fd\u6709\u6548\uff0c\u6240\u4ee5\u5168\u5c40\u53ea\u6709\u4e00\u4e2a\u53d8\u91cf<code>i</code>\u3002\u6bcf\u4e00\u6b21\u5faa\u73af\uff0c\u53d8\u91cf<code>i</code>\u7684\u503c\u90fd\u4f1a\u53d1\u751f\u6539\u53d8\uff0c\u800c\u5faa\u73af\u5185\u88ab\u8d4b\u7ed9\u6570\u7ec4<code>a</code>\u7684\u51fd\u6570\u5185\u90e8\u7684<code>console.log(i)</code>\uff0c\u91cc\u9762\u7684<code>i</code>\u6307\u5411\u7684\u5c31\u662f\u5168\u5c40\u7684<code>i</code>\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u6240\u6709\u6570\u7ec4<code>a</code>\u7684\u6210\u5458\u91cc\u9762\u7684<code>i</code>\uff0c\u6307\u5411\u7684\u90fd\u662f\u540c\u4e00\u4e2a<code>i</code>\uff0c\u5bfc\u81f4\u8fd0\u884c\u65f6\u8f93\u51fa\u7684\u662f\u6700\u540e\u4e00\u8f6e\u7684<code>i</code>\u7684\u503c\uff0c\u4e5f\u5c31\u662f 10\u3002</p>\n<p>\u5982\u679c\u4f7f\u7528<code>let</code>\uff0c\u58f0\u660e\u7684\u53d8\u91cf\u4ec5\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u5185\u6709\u6548\uff0c\u6700\u540e\u8f93\u51fa\u7684\u662f 6\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> a = [];\n<span class="hljs-keyword">for</span> (<span class="hljs-keyword">let</span> i = <span class="hljs-number">0</span>; i &lt; <span class="hljs-number">10</span>; i++) {\n  a[i] = <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n    <span class="hljs-built_in">console</span>.log(i);\n  };\n}\na[<span class="hljs-number">6</span>](); <span class="hljs-comment">// 6</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>i</code>\u662f<code>let</code>\u58f0\u660e\u7684\uff0c\u5f53\u524d\u7684<code>i</code>\u53ea\u5728\u672c\u8f6e\u5faa\u73af\u6709\u6548\uff0c\u6240\u4ee5\u6bcf\u4e00\u6b21\u5faa\u73af\u7684<code>i</code>\u5176\u5b9e\u90fd\u662f\u4e00\u4e2a\u65b0\u7684\u53d8\u91cf\uff0c\u6240\u4ee5\u6700\u540e\u8f93\u51fa\u7684\u662f<code>6</code>\u3002\u4f60\u53ef\u80fd\u4f1a\u95ee\uff0c\u5982\u679c\u6bcf\u4e00\u8f6e\u5faa\u73af\u7684\u53d8\u91cf<code>i</code>\u90fd\u662f\u91cd\u65b0\u58f0\u660e\u7684\uff0c\u90a3\u5b83\u600e\u4e48\u77e5\u9053\u4e0a\u4e00\u8f6e\u5faa\u73af\u7684\u503c\uff0c\u4ece\u800c\u8ba1\u7b97\u51fa\u672c\u8f6e\u5faa\u73af\u7684\u503c\uff1f\u8fd9\u662f\u56e0\u4e3a JavaScript \u5f15\u64ce\u5185\u90e8\u4f1a\u8bb0\u4f4f\u4e0a\u4e00\u8f6e\u5faa\u73af\u7684\u503c\uff0c\u521d\u59cb\u5316\u672c\u8f6e\u7684\u53d8\u91cf<code>i</code>\u65f6\uff0c\u5c31\u5728\u4e0a\u4e00\u8f6e\u5faa\u73af\u7684\u57fa\u7840\u4e0a\u8fdb\u884c\u8ba1\u7b97\u3002</p>\n<p>\u53e6\u5916\uff0c<code>for</code>\u5faa\u73af\u8fd8\u6709\u4e00\u4e2a\u7279\u522b\u4e4b\u5904\uff0c\u5c31\u662f\u8bbe\u7f6e\u5faa\u73af\u53d8\u91cf\u7684\u90a3\u90e8\u5206\u662f\u4e00\u4e2a\u7236\u4f5c\u7528\u57df\uff0c\u800c\u5faa\u73af\u4f53\u5185\u90e8\u662f\u4e00\u4e2a\u5355\u72ec\u7684\u5b50\u4f5c\u7528\u57df\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">for</span> (<span class="hljs-keyword">let</span> i = <span class="hljs-number">0</span>; i &lt; <span class="hljs-number">3</span>; i++) {\n  <span class="hljs-keyword">let</span> i = <span class="hljs-string">\'abc\'</span>;\n  <span class="hljs-built_in">console</span>.log(i);\n}\n<span class="hljs-comment">// abc</span>\n<span class="hljs-comment">// abc</span>\n<span class="hljs-comment">// abc</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u6b63\u786e\u8fd0\u884c\uff0c\u8f93\u51fa\u4e86 3 \u6b21<code>abc</code>\u3002\u8fd9\u8868\u660e\u51fd\u6570\u5185\u90e8\u7684\u53d8\u91cf<code>i</code>\u4e0e\u5faa\u73af\u53d8\u91cf<code>i</code>\u4e0d\u5728\u540c\u4e00\u4e2a\u4f5c\u7528\u57df\uff0c\u6709\u5404\u81ea\u5355\u72ec\u7684\u4f5c\u7528\u57df\u3002</p>\n<h3 id="\u4e0d\u5b58\u5728\u53d8\u91cf\u63d0\u5347">\u4e0d\u5b58\u5728\u53d8\u91cf\u63d0\u5347</h3>\n<p><code>var</code>\u547d\u4ee4\u4f1a\u53d1\u751f\u201d\u53d8\u91cf\u63d0\u5347\u201c\u73b0\u8c61\uff0c\u5373\u53d8\u91cf\u53ef\u4ee5\u5728\u58f0\u660e\u4e4b\u524d\u4f7f\u7528\uff0c\u503c\u4e3a<code>undefined</code>\u3002\u8fd9\u79cd\u73b0\u8c61\u591a\u591a\u5c11\u5c11\u662f\u6709\u4e9b\u5947\u602a\u7684\uff0c\u6309\u7167\u4e00\u822c\u7684\u903b\u8f91\uff0c\u53d8\u91cf\u5e94\u8be5\u5728\u58f0\u660e\u8bed\u53e5\u4e4b\u540e\u624d\u53ef\u4ee5\u4f7f\u7528\u3002</p>\n<p>\u4e3a\u4e86\u7ea0\u6b63\u8fd9\u79cd\u73b0\u8c61\uff0c<code>let</code>\u547d\u4ee4\u6539\u53d8\u4e86\u8bed\u6cd5\u884c\u4e3a\uff0c\u5b83\u6240\u58f0\u660e\u7684\u53d8\u91cf\u4e00\u5b9a\u8981\u5728\u58f0\u660e\u540e\u4f7f\u7528\uff0c\u5426\u5219\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// var \u7684\u60c5\u51b5</span>\n<span class="hljs-built_in">console</span>.log(foo); <span class="hljs-comment">// \u8f93\u51faundefined</span>\n<span class="hljs-keyword">var</span> foo = <span class="hljs-number">2</span>;\n\n<span class="hljs-comment">// let \u7684\u60c5\u51b5</span>\n<span class="hljs-built_in">console</span>.log(bar); <span class="hljs-comment">// \u62a5\u9519ReferenceError</span>\n<span class="hljs-keyword">let</span> bar = <span class="hljs-number">2</span>;</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>foo</code>\u7528<code>var</code>\u547d\u4ee4\u58f0\u660e\uff0c\u4f1a\u53d1\u751f\u53d8\u91cf\u63d0\u5347\uff0c\u5373\u811a\u672c\u5f00\u59cb\u8fd0\u884c\u65f6\uff0c\u53d8\u91cf<code>foo</code>\u5df2\u7ecf\u5b58\u5728\u4e86\uff0c\u4f46\u662f\u6ca1\u6709\u503c\uff0c\u6240\u4ee5\u4f1a\u8f93\u51fa<code>undefined</code>\u3002\u53d8\u91cf<code>bar</code>\u7528<code>let</code>\u547d\u4ee4\u58f0\u660e\uff0c\u4e0d\u4f1a\u53d1\u751f\u53d8\u91cf\u63d0\u5347\u3002\u8fd9\u8868\u793a\u5728\u58f0\u660e\u5b83\u4e4b\u524d\uff0c\u53d8\u91cf<code>bar</code>\u662f\u4e0d\u5b58\u5728\u7684\uff0c\u8fd9\u65f6\u5982\u679c\u7528\u5230\u5b83\uff0c\u5c31\u4f1a\u629b\u51fa\u4e00\u4e2a\u9519\u8bef\u3002</p>\n<h3 id="\u6682\u65f6\u6027\u6b7b\u533a">\u6682\u65f6\u6027\u6b7b\u533a</h3>\n<p>\u53ea\u8981\u5757\u7ea7\u4f5c\u7528\u57df\u5185\u5b58\u5728<code>let</code>\u547d\u4ee4\uff0c\u5b83\u6240\u58f0\u660e\u7684\u53d8\u91cf\u5c31\u201c\u7ed1\u5b9a\u201d\uff08binding\uff09\u8fd9\u4e2a\u533a\u57df\uff0c\u4e0d\u518d\u53d7\u5916\u90e8\u7684\u5f71\u54cd\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> tmp = <span class="hljs-number">123</span>;\n\n<span class="hljs-keyword">if</span> (<span class="hljs-literal">true</span>) {\n  tmp = <span class="hljs-string">\'abc\'</span>; <span class="hljs-comment">// ReferenceError</span>\n  <span class="hljs-keyword">let</span> tmp;\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b58\u5728\u5168\u5c40\u53d8\u91cf<code>tmp</code>\uff0c\u4f46\u662f\u5757\u7ea7\u4f5c\u7528\u57df\u5185<code>let</code>\u53c8\u58f0\u660e\u4e86\u4e00\u4e2a\u5c40\u90e8\u53d8\u91cf<code>tmp</code>\uff0c\u5bfc\u81f4\u540e\u8005\u7ed1\u5b9a\u8fd9\u4e2a\u5757\u7ea7\u4f5c\u7528\u57df\uff0c\u6240\u4ee5\u5728<code>let</code>\u58f0\u660e\u53d8\u91cf\u524d\uff0c\u5bf9<code>tmp</code>\u8d4b\u503c\u4f1a\u62a5\u9519\u3002</p>\n<p>ES6 \u660e\u786e\u89c4\u5b9a\uff0c\u5982\u679c\u533a\u5757\u4e2d\u5b58\u5728<code>let</code>\u548c<code>const</code>\u547d\u4ee4\uff0c\u8fd9\u4e2a\u533a\u5757\u5bf9\u8fd9\u4e9b\u547d\u4ee4\u58f0\u660e\u7684\u53d8\u91cf\uff0c\u4ece\u4e00\u5f00\u59cb\u5c31\u5f62\u6210\u4e86\u5c01\u95ed\u4f5c\u7528\u57df\u3002\u51e1\u662f\u5728\u58f0\u660e\u4e4b\u524d\u5c31\u4f7f\u7528\u8fd9\u4e9b\u53d8\u91cf\uff0c\u5c31\u4f1a\u62a5\u9519\u3002</p>\n<p>\u603b\u4e4b\uff0c\u5728\u4ee3\u7801\u5757\u5185\uff0c\u4f7f\u7528<code>let</code>\u547d\u4ee4\u58f0\u660e\u53d8\u91cf\u4e4b\u524d\uff0c\u8be5\u53d8\u91cf\u90fd\u662f\u4e0d\u53ef\u7528\u7684\u3002\u8fd9\u5728\u8bed\u6cd5\u4e0a\uff0c\u79f0\u4e3a\u201c\u6682\u65f6\u6027\u6b7b\u533a\u201d\uff08temporal dead zone\uff0c\u7b80\u79f0 TDZ\uff09\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">if</span> (<span class="hljs-literal">true</span>) {\n  <span class="hljs-comment">// TDZ\u5f00\u59cb</span>\n  tmp = <span class="hljs-string">\'abc\'</span>; <span class="hljs-comment">// ReferenceError</span>\n  <span class="hljs-built_in">console</span>.log(tmp); <span class="hljs-comment">// ReferenceError</span>\n\n  <span class="hljs-keyword">let</span> tmp; <span class="hljs-comment">// TDZ\u7ed3\u675f</span>\n  <span class="hljs-built_in">console</span>.log(tmp); <span class="hljs-comment">// undefined</span>\n\n  tmp = <span class="hljs-number">123</span>;\n  <span class="hljs-built_in">console</span>.log(tmp); <span class="hljs-comment">// 123</span>\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5728<code>let</code>\u547d\u4ee4\u58f0\u660e\u53d8\u91cf<code>tmp</code>\u4e4b\u524d\uff0c\u90fd\u5c5e\u4e8e\u53d8\u91cf<code>tmp</code>\u7684\u201c\u6b7b\u533a\u201d\u3002</p>\n<p>\u201c\u6682\u65f6\u6027\u6b7b\u533a\u201d\u4e5f\u610f\u5473\u7740<code>typeof</code>\u4e0d\u518d\u662f\u4e00\u4e2a\u767e\u5206\u4e4b\u767e\u5b89\u5168\u7684\u64cd\u4f5c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-built_in">typeof</span> <span class="hljs-symbol">x</span>; <span class="hljs-comment">// ReferenceError</span>\nlet <span class="hljs-symbol">x</span>;</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>x</code>\u4f7f\u7528<code>let</code>\u547d\u4ee4\u58f0\u660e\uff0c\u6240\u4ee5\u5728\u58f0\u660e\u4e4b\u524d\uff0c\u90fd\u5c5e\u4e8e<code>x</code>\u7684\u201c\u6b7b\u533a\u201d\uff0c\u53ea\u8981\u7528\u5230\u8be5\u53d8\u91cf\u5c31\u4f1a\u62a5\u9519\u3002\u56e0\u6b64\uff0c<code>typeof</code>\u8fd0\u884c\u65f6\u5c31\u4f1a\u629b\u51fa\u4e00\u4e2a<code>ReferenceError</code>\u3002</p>\n<p>\u4f5c\u4e3a\u6bd4\u8f83\uff0c\u5982\u679c\u4e00\u4e2a\u53d8\u91cf\u6839\u672c\u6ca1\u6709\u88ab\u58f0\u660e\uff0c\u4f7f\u7528<code>typeof</code>\u53cd\u800c\u4e0d\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">typeof</span> undeclared_variable <span class="hljs-comment">// "undefined"</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>undeclared_variable</code>\u662f\u4e00\u4e2a\u4e0d\u5b58\u5728\u7684\u53d8\u91cf\u540d\uff0c\u7ed3\u679c\u8fd4\u56de\u201cundefined\u201d\u3002\u6240\u4ee5\uff0c\u5728\u6ca1\u6709<code>let</code>\u4e4b\u524d\uff0c<code>typeof</code>\u8fd0\u7b97\u7b26\u662f\u767e\u5206\u4e4b\u767e\u5b89\u5168\u7684\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u62a5\u9519\u3002\u73b0\u5728\u8fd9\u4e00\u70b9\u4e0d\u6210\u7acb\u4e86\u3002\u8fd9\u6837\u7684\u8bbe\u8ba1\u662f\u4e3a\u4e86\u8ba9\u5927\u5bb6\u517b\u6210\u826f\u597d\u7684\u7f16\u7a0b\u4e60\u60ef\uff0c\u53d8\u91cf\u4e00\u5b9a\u8981\u5728\u58f0\u660e\u4e4b\u540e\u4f7f\u7528\uff0c\u5426\u5219\u5c31\u62a5\u9519\u3002</p>\n<p>\u6709\u4e9b\u201c\u6b7b\u533a\u201d\u6bd4\u8f83\u9690\u853d\uff0c\u4e0d\u592a\u5bb9\u6613\u53d1\u73b0\u3002</p>\n<pre><code class="language-javascript">function bar(<span class="hljs-symbol">x</span> = <span class="hljs-symbol">y</span>, <span class="hljs-symbol">y</span> = <span class="hljs-number">2</span>) {\n  <span class="hljs-keywords">return</span> [<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>];\n}\n\nbar(); <span class="hljs-comment">// \u62a5\u9519</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u8c03\u7528<code>bar</code>\u51fd\u6570\u4e4b\u6240\u4ee5\u62a5\u9519\uff08\u67d0\u4e9b\u5b9e\u73b0\u53ef\u80fd\u4e0d\u62a5\u9519\uff09\uff0c\u662f\u56e0\u4e3a\u53c2\u6570<code>x</code>\u9ed8\u8ba4\u503c\u7b49\u4e8e\u53e6\u4e00\u4e2a\u53c2\u6570<code>y</code>\uff0c\u800c\u6b64\u65f6<code>y</code>\u8fd8\u6ca1\u6709\u58f0\u660e\uff0c\u5c5e\u4e8e\u201d\u6b7b\u533a\u201c\u3002\u5982\u679c<code>y</code>\u7684\u9ed8\u8ba4\u503c\u662f<code>x</code>\uff0c\u5c31\u4e0d\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a\u6b64\u65f6<code>x</code>\u5df2\u7ecf\u58f0\u660e\u4e86\u3002</p>\n<pre><code class="language-javascript">function bar(<span class="hljs-symbol">x</span> = <span class="hljs-number">2</span>, <span class="hljs-symbol">y</span> = <span class="hljs-symbol">x</span>) {\n  <span class="hljs-keywords">return</span> [<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>];\n}\nbar(); <span class="hljs-comment">// [2, 2]</span></code></pre>\n<p>\u53e6\u5916\uff0c\u4e0b\u9762\u7684\u4ee3\u7801\u4e5f\u4f1a\u62a5\u9519\uff0c\u4e0e<code>var</code>\u7684\u884c\u4e3a\u4e0d\u540c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u4e0d\u62a5\u9519</span>\n<span class="hljs-keywords">var</span> <span class="hljs-symbol">x</span> = <span class="hljs-symbol">x</span>;\n\n<span class="hljs-comment">// \u62a5\u9519</span>\nlet <span class="hljs-symbol">x</span> = <span class="hljs-symbol">x</span>;\n<span class="hljs-comment">// ReferenceError: x is not defined</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u62a5\u9519\uff0c\u4e5f\u662f\u56e0\u4e3a\u6682\u65f6\u6027\u6b7b\u533a\u3002\u4f7f\u7528<code>let</code>\u58f0\u660e\u53d8\u91cf\u65f6\uff0c\u53ea\u8981\u53d8\u91cf\u5728\u8fd8\u6ca1\u6709\u58f0\u660e\u5b8c\u6210\u524d\u4f7f\u7528\uff0c\u5c31\u4f1a\u62a5\u9519\u3002\u4e0a\u9762\u8fd9\u884c\u5c31\u5c5e\u4e8e\u8fd9\u4e2a\u60c5\u51b5\uff0c\u5728\u53d8\u91cf<code>x</code>\u7684\u58f0\u660e\u8bed\u53e5\u8fd8\u6ca1\u6709\u6267\u884c\u5b8c\u6210\u524d\uff0c\u5c31\u53bb\u53d6<code>x</code>\u7684\u503c\uff0c\u5bfc\u81f4\u62a5\u9519\u201dx \u672a\u5b9a\u4e49\u201c\u3002</p>\n<p>ES6 \u89c4\u5b9a\u6682\u65f6\u6027\u6b7b\u533a\u548c<code>let</code>\u3001<code>const</code>\u8bed\u53e5\u4e0d\u51fa\u73b0\u53d8\u91cf\u63d0\u5347\uff0c\u4e3b\u8981\u662f\u4e3a\u4e86\u51cf\u5c11\u8fd0\u884c\u65f6\u9519\u8bef\uff0c\u9632\u6b62\u5728\u53d8\u91cf\u58f0\u660e\u524d\u5c31\u4f7f\u7528\u8fd9\u4e2a\u53d8\u91cf\uff0c\u4ece\u800c\u5bfc\u81f4\u610f\u6599\u4e4b\u5916\u7684\u884c\u4e3a\u3002\u8fd9\u6837\u7684\u9519\u8bef\u5728 ES5 \u662f\u5f88\u5e38\u89c1\u7684\uff0c\u73b0\u5728\u6709\u4e86\u8fd9\u79cd\u89c4\u5b9a\uff0c\u907f\u514d\u6b64\u7c7b\u9519\u8bef\u5c31\u5f88\u5bb9\u6613\u4e86\u3002</p>\n<p>\u603b\u4e4b\uff0c\u6682\u65f6\u6027\u6b7b\u533a\u7684\u672c\u8d28\u5c31\u662f\uff0c\u53ea\u8981\u4e00\u8fdb\u5165\u5f53\u524d\u4f5c\u7528\u57df\uff0c\u6240\u8981\u4f7f\u7528\u7684\u53d8\u91cf\u5c31\u5df2\u7ecf\u5b58\u5728\u4e86\uff0c\u4f46\u662f\u4e0d\u53ef\u83b7\u53d6\uff0c\u53ea\u6709\u7b49\u5230\u58f0\u660e\u53d8\u91cf\u7684\u90a3\u4e00\u884c\u4ee3\u7801\u51fa\u73b0\uff0c\u624d\u53ef\u4ee5\u83b7\u53d6\u548c\u4f7f\u7528\u8be5\u53d8\u91cf\u3002</p>\n<h3 id="\u4e0d\u5141\u8bb8\u91cd\u590d\u58f0\u660e">\u4e0d\u5141\u8bb8\u91cd\u590d\u58f0\u660e</h3>\n<p><code>let</code>\u4e0d\u5141\u8bb8\u5728\u76f8\u540c\u4f5c\u7528\u57df\u5185\uff0c\u91cd\u590d\u58f0\u660e\u540c\u4e00\u4e2a\u53d8\u91cf\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u62a5\u9519</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">func</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">let</span> a = <span class="hljs-number">10</span>;\n  <span class="hljs-keyword">var</span> a = <span class="hljs-number">1</span>;\n}\n\n<span class="hljs-comment">// \u62a5\u9519</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">func</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">let</span> a = <span class="hljs-number">10</span>;\n  <span class="hljs-keyword">let</span> a = <span class="hljs-number">1</span>;\n}</code></pre>\n<p>\u56e0\u6b64\uff0c\u4e0d\u80fd\u5728\u51fd\u6570\u5185\u90e8\u91cd\u65b0\u58f0\u660e\u53c2\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">func</span>(<span class="hljs-params">arg</span>) </span>{\n  <span class="hljs-keyword">let</span> arg; <span class="hljs-comment">// \u62a5\u9519</span>\n}\n\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">func</span>(<span class="hljs-params">arg</span>) </span>{\n  {\n    <span class="hljs-keyword">let</span> arg; <span class="hljs-comment">// \u4e0d\u62a5\u9519</span>\n  }\n}</code></pre>\n<h2 id="\u5757\u7ea7\u4f5c\u7528\u57df">\u5757\u7ea7\u4f5c\u7528\u57df</h2>\n<h3 id="\u4e3a\u4ec0\u4e48\u9700\u8981\u5757\u7ea7\u4f5c\u7528\u57df\uff1f">\u4e3a\u4ec0\u4e48\u9700\u8981\u5757\u7ea7\u4f5c\u7528\u57df\uff1f</h3>\n<p>ES5 \u53ea\u6709\u5168\u5c40\u4f5c\u7528\u57df\u548c\u51fd\u6570\u4f5c\u7528\u57df\uff0c\u6ca1\u6709\u5757\u7ea7\u4f5c\u7528\u57df\uff0c\u8fd9\u5e26\u6765\u5f88\u591a\u4e0d\u5408\u7406\u7684\u573a\u666f\u3002</p>\n<p>\u7b2c\u4e00\u79cd\u573a\u666f\uff0c\u5185\u5c42\u53d8\u91cf\u53ef\u80fd\u4f1a\u8986\u76d6\u5916\u5c42\u53d8\u91cf\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> tmp = <span class="hljs-keyword">new</span> <span class="hljs-built_in">Date</span>();\n\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-built_in">console</span>.log(tmp);\n  <span class="hljs-keyword">if</span> (<span class="hljs-literal">false</span>) {\n    <span class="hljs-keyword">var</span> tmp = <span class="hljs-string">\'hello world\'</span>;\n  }\n}\n\nf(); <span class="hljs-comment">// undefined</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684\u539f\u610f\u662f\uff0c<code>if</code>\u4ee3\u7801\u5757\u7684\u5916\u90e8\u4f7f\u7528\u5916\u5c42\u7684<code>tmp</code>\u53d8\u91cf\uff0c\u5185\u90e8\u4f7f\u7528\u5185\u5c42\u7684<code>tmp</code>\u53d8\u91cf\u3002\u4f46\u662f\uff0c\u51fd\u6570<code>f</code>\u6267\u884c\u540e\uff0c\u8f93\u51fa\u7ed3\u679c\u4e3a<code>undefined</code>\uff0c\u539f\u56e0\u5728\u4e8e\u53d8\u91cf\u63d0\u5347\uff0c\u5bfc\u81f4\u5185\u5c42\u7684<code>tmp</code>\u53d8\u91cf\u8986\u76d6\u4e86\u5916\u5c42\u7684<code>tmp</code>\u53d8\u91cf\u3002</p>\n<p>\u7b2c\u4e8c\u79cd\u573a\u666f\uff0c\u7528\u6765\u8ba1\u6570\u7684\u5faa\u73af\u53d8\u91cf\u6cc4\u9732\u4e3a\u5168\u5c40\u53d8\u91cf\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> s = <span class="hljs-string">\'hello\'</span>;\n\n<span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> i = <span class="hljs-number">0</span>; i &lt; s.length; i++) {\n  <span class="hljs-built_in">console</span>.log(s[i]);\n}\n\n<span class="hljs-built_in">console</span>.log(i); <span class="hljs-comment">// 5</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>i</code>\u53ea\u7528\u6765\u63a7\u5236\u5faa\u73af\uff0c\u4f46\u662f\u5faa\u73af\u7ed3\u675f\u540e\uff0c\u5b83\u5e76\u6ca1\u6709\u6d88\u5931\uff0c\u6cc4\u9732\u6210\u4e86\u5168\u5c40\u53d8\u91cf\u3002</p>\n<h3 id="es6-\u7684\u5757\u7ea7\u4f5c\u7528\u57df">ES6 \u7684\u5757\u7ea7\u4f5c\u7528\u57df</h3>\n<p><code>let</code>\u5b9e\u9645\u4e0a\u4e3a JavaScript \u65b0\u589e\u4e86\u5757\u7ea7\u4f5c\u7528\u57df\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f1</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">let</span> n = <span class="hljs-number">5</span>;\n  <span class="hljs-keyword">if</span> (<span class="hljs-literal">true</span>) {\n    <span class="hljs-keyword">let</span> n = <span class="hljs-number">10</span>;\n  }\n  <span class="hljs-built_in">console</span>.log(n); <span class="hljs-comment">// 5</span>\n}</code></pre>\n<p>\u4e0a\u9762\u7684\u51fd\u6570\u6709\u4e24\u4e2a\u4ee3\u7801\u5757\uff0c\u90fd\u58f0\u660e\u4e86\u53d8\u91cf<code>n</code>\uff0c\u8fd0\u884c\u540e\u8f93\u51fa 5\u3002\u8fd9\u8868\u793a\u5916\u5c42\u4ee3\u7801\u5757\u4e0d\u53d7\u5185\u5c42\u4ee3\u7801\u5757\u7684\u5f71\u54cd\u3002\u5982\u679c\u4e24\u6b21\u90fd\u4f7f\u7528<code>var</code>\u5b9a\u4e49\u53d8\u91cf<code>n</code>\uff0c\u6700\u540e\u8f93\u51fa\u7684\u503c\u624d\u662f 10\u3002</p>\n<p>ES6 \u5141\u8bb8\u5757\u7ea7\u4f5c\u7528\u57df\u7684\u4efb\u610f\u5d4c\u5957\u3002</p>\n<pre><code class="language-javascript">{{{{{let insane = \'Hello World\'}}}}}<span class="hljs-comment">;</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4f7f\u7528\u4e86\u4e00\u4e2a\u4e94\u5c42\u7684\u5757\u7ea7\u4f5c\u7528\u57df\u3002\u5916\u5c42\u4f5c\u7528\u57df\u65e0\u6cd5\u8bfb\u53d6\u5185\u5c42\u4f5c\u7528\u57df\u7684\u53d8\u91cf\u3002</p>\n<p>\u5757\u7ea7\u4f5c\u7528\u57df\u7684\u51fa\u73b0\uff0c\u5b9e\u9645\u4e0a\u4f7f\u5f97\u83b7\u5f97\u5e7f\u6cdb\u5e94\u7528\u7684\u7acb\u5373\u6267\u884c\u51fd\u6570\u8868\u8fbe\u5f0f\uff08IIFE\uff09\u4e0d\u518d\u5fc5\u8981\u4e86\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// IIFE \u5199\u6cd5</span>\n(function () {\n  <span class="hljs-built_in">var</span> tmp = <span class="hljs-params">...</span>;\n  <span class="hljs-params">...</span>\n}());\n\n<span class="hljs-comment">// \u5757\u7ea7\u4f5c\u7528\u57df\u5199\u6cd5</span>\n{\n  <span class="hljs-keyword">let</span> tmp = <span class="hljs-params">...</span>;\n  <span class="hljs-params">...</span>\n}</code></pre>\n<h3 id="\u5757\u7ea7\u4f5c\u7528\u57df\u4e0e\u51fd\u6570\u58f0\u660e">\u5757\u7ea7\u4f5c\u7528\u57df\u4e0e\u51fd\u6570\u58f0\u660e</h3>\n<p>\u51fd\u6570\u80fd\u4e0d\u80fd\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u4e4b\u4e2d\u58f0\u660e\uff1f\u8fd9\u662f\u4e00\u4e2a\u76f8\u5f53\u4ee4\u4eba\u6df7\u6dc6\u7684\u95ee\u9898\u3002</p>\n<p>ES5 \u89c4\u5b9a\uff0c\u51fd\u6570\u53ea\u80fd\u5728\u9876\u5c42\u4f5c\u7528\u57df\u548c\u51fd\u6570\u4f5c\u7528\u57df\u4e4b\u4e2d\u58f0\u660e\uff0c\u4e0d\u80fd\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u58f0\u660e\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u60c5\u51b5\u4e00</span>\n<span class="hljs-keyword">if</span> (<span class="hljs-literal">true</span>) {\n  <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span><span class="hljs-params">()</span> </span>{}\n}\n\n<span class="hljs-comment">// \u60c5\u51b5\u4e8c</span>\n<span class="hljs-keyword">try</span> {\n  <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span><span class="hljs-params">()</span> </span>{}\n} <span class="hljs-keyword">catch</span>(e) {\n  <span class="hljs-comment">// ...</span>\n}</code></pre>\n<p>\u4e0a\u9762\u4e24\u79cd\u51fd\u6570\u58f0\u660e\uff0c\u6839\u636e ES5 \u7684\u89c4\u5b9a\u90fd\u662f\u975e\u6cd5\u7684\u3002</p>\n<p>\u4f46\u662f\uff0c\u6d4f\u89c8\u5668\u6ca1\u6709\u9075\u5b88\u8fd9\u4e2a\u89c4\u5b9a\uff0c\u4e3a\u4e86\u517c\u5bb9\u4ee5\u524d\u7684\u65e7\u4ee3\u7801\uff0c\u8fd8\u662f\u652f\u6301\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u4e4b\u4e2d\u58f0\u660e\u51fd\u6570\uff0c\u56e0\u6b64\u4e0a\u9762\u4e24\u79cd\u60c5\u51b5\u5b9e\u9645\u90fd\u80fd\u8fd0\u884c\uff0c\u4e0d\u4f1a\u62a5\u9519\u3002</p>\n<p>ES6 \u5f15\u5165\u4e86\u5757\u7ea7\u4f5c\u7528\u57df\uff0c\u660e\u786e\u5141\u8bb8\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u4e4b\u4e2d\u58f0\u660e\u51fd\u6570\u3002ES6 \u89c4\u5b9a\uff0c\u5757\u7ea7\u4f5c\u7528\u57df\u4e4b\u4e2d\uff0c\u51fd\u6570\u58f0\u660e\u8bed\u53e5\u7684\u884c\u4e3a\u7c7b\u4f3c\u4e8e<code>let</code>\uff0c\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u4e4b\u5916\u4e0d\u53ef\u5f15\u7528\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{ <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'I am outside!\'</span>); }\n\n(<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">if</span> (<span class="hljs-literal">false</span>) {\n    <span class="hljs-comment">// \u91cd\u590d\u58f0\u660e\u4e00\u6b21\u51fd\u6570f</span>\n    <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{ <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'I am inside!\'</span>); }\n  }\n\n  f();\n}());</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u5728 ES5 \u4e2d\u8fd0\u884c\uff0c\u4f1a\u5f97\u5230\u201cI am inside!\u201d\uff0c\u56e0\u4e3a\u5728<code>if</code>\u5185\u58f0\u660e\u7684\u51fd\u6570<code>f</code>\u4f1a\u88ab\u63d0\u5347\u5230\u51fd\u6570\u5934\u90e8\uff0c\u5b9e\u9645\u8fd0\u884c\u7684\u4ee3\u7801\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// ES5 \u73af\u5883</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{ <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'I am outside!\'</span>); }\n\n(<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{ <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'I am inside!\'</span>); }\n  <span class="hljs-keyword">if</span> (<span class="hljs-literal">false</span>) {\n  }\n  f();\n}());</code></pre>\n<p>ES6 \u5c31\u5b8c\u5168\u4e0d\u4e00\u6837\u4e86\uff0c\u7406\u8bba\u4e0a\u4f1a\u5f97\u5230\u201cI am outside!\u201d\u3002\u56e0\u4e3a\u5757\u7ea7\u4f5c\u7528\u57df\u5185\u58f0\u660e\u7684\u51fd\u6570\u7c7b\u4f3c\u4e8e<code>let</code>\uff0c\u5bf9\u4f5c\u7528\u57df\u4e4b\u5916\u6ca1\u6709\u5f71\u54cd\u3002\u4f46\u662f\uff0c\u5982\u679c\u4f60\u771f\u7684\u5728 ES6 \u6d4f\u89c8\u5668\u4e2d\u8fd0\u884c\u4e00\u4e0b\u4e0a\u9762\u7684\u4ee3\u7801\uff0c\u662f\u4f1a\u62a5\u9519\u7684\uff0c\u8fd9\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f</p>\n<p>\u539f\u6765\uff0c\u5982\u679c\u6539\u53d8\u4e86\u5757\u7ea7\u4f5c\u7528\u57df\u5185\u58f0\u660e\u7684\u51fd\u6570\u7684\u5904\u7406\u89c4\u5219\uff0c\u663e\u7136\u4f1a\u5bf9\u8001\u4ee3\u7801\u4ea7\u751f\u5f88\u5927\u5f71\u54cd\u3002\u4e3a\u4e86\u51cf\u8f7b\u56e0\u6b64\u4ea7\u751f\u7684\u4e0d\u517c\u5bb9\u95ee\u9898\uff0cES6 \u5728<a href="http://www.ecma-international.org/ecma-262/6.0/index.html#sec-block-level-function-declarations-web-legacy-compatibility-semantics">\u9644\u5f55 B</a>\u91cc\u9762\u89c4\u5b9a\uff0c\u6d4f\u89c8\u5668\u7684\u5b9e\u73b0\u53ef\u4ee5\u4e0d\u9075\u5b88\u4e0a\u9762\u7684\u89c4\u5b9a\uff0c\u6709\u81ea\u5df1\u7684<a href="http://stackoverflow.com/questions/31419897/what-are-the-precise-semantics-of-block-level-functions-in-es6">\u884c\u4e3a\u65b9\u5f0f</a>\u3002</p>\n<ul>\n<li>\u5141\u8bb8\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u5185\u58f0\u660e\u51fd\u6570\u3002</li>\n<li>\u51fd\u6570\u58f0\u660e\u7c7b\u4f3c\u4e8e<code>var</code>\uff0c\u5373\u4f1a\u63d0\u5347\u5230\u5168\u5c40\u4f5c\u7528\u57df\u6216\u51fd\u6570\u4f5c\u7528\u57df\u7684\u5934\u90e8\u3002</li>\n<li>\u540c\u65f6\uff0c\u51fd\u6570\u58f0\u660e\u8fd8\u4f1a\u63d0\u5347\u5230\u6240\u5728\u7684\u5757\u7ea7\u4f5c\u7528\u57df\u7684\u5934\u90e8\u3002</li>\n</ul>\n<p>\u6ce8\u610f\uff0c\u4e0a\u9762\u4e09\u6761\u89c4\u5219\u53ea\u5bf9 ES6 \u7684\u6d4f\u89c8\u5668\u5b9e\u73b0\u6709\u6548\uff0c\u5176\u4ed6\u73af\u5883\u7684\u5b9e\u73b0\u4e0d\u7528\u9075\u5b88\uff0c\u8fd8\u662f\u5c06\u5757\u7ea7\u4f5c\u7528\u57df\u7684\u51fd\u6570\u58f0\u660e\u5f53\u4f5c<code>let</code>\u5904\u7406\u3002</p>\n<p>\u6839\u636e\u8fd9\u4e09\u6761\u89c4\u5219\uff0c\u5728\u6d4f\u89c8\u5668\u7684 ES6 \u73af\u5883\u4e2d\uff0c\u5757\u7ea7\u4f5c\u7528\u57df\u5185\u58f0\u660e\u7684\u51fd\u6570\uff0c\u884c\u4e3a\u7c7b\u4f3c\u4e8e<code>var</code>\u58f0\u660e\u7684\u53d8\u91cf\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u6d4f\u89c8\u5668\u7684 ES6 \u73af\u5883</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{ <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'I am outside!\'</span>); }\n\n(<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">if</span> (<span class="hljs-literal">false</span>) {\n    <span class="hljs-comment">// \u91cd\u590d\u58f0\u660e\u4e00\u6b21\u51fd\u6570f</span>\n    <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{ <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'I am inside!\'</span>); }\n  }\n\n  f();\n}());\n<span class="hljs-comment">// Uncaught TypeError: f is not a function</span></code></pre>\n<p>\u4e0a\u9762\u7684\u4ee3\u7801\u5728\u7b26\u5408 ES6 \u7684\u6d4f\u89c8\u5668\u4e2d\uff0c\u90fd\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a\u5b9e\u9645\u8fd0\u884c\u7684\u662f\u4e0b\u9762\u7684\u4ee3\u7801\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u6d4f\u89c8\u5668\u7684 ES6 \u73af\u5883</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{ <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'I am outside!\'</span>); }\n(<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">var</span> f = <span class="hljs-literal">undefined</span>;\n  <span class="hljs-keyword">if</span> (<span class="hljs-literal">false</span>) {\n    <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{ <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'I am inside!\'</span>); }\n  }\n\n  f();\n}());\n<span class="hljs-comment">// Uncaught TypeError: f is not a function</span></code></pre>\n<p>\u8003\u8651\u5230\u73af\u5883\u5bfc\u81f4\u7684\u884c\u4e3a\u5dee\u5f02\u592a\u5927\uff0c\u5e94\u8be5\u907f\u514d\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u5185\u58f0\u660e\u51fd\u6570\u3002\u5982\u679c\u786e\u5b9e\u9700\u8981\uff0c\u4e5f\u5e94\u8be5\u5199\u6210\u51fd\u6570\u8868\u8fbe\u5f0f\uff0c\u800c\u4e0d\u662f\u51fd\u6570\u58f0\u660e\u8bed\u53e5\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u51fd\u6570\u58f0\u660e\u8bed\u53e5</span>\n{\n  <span class="hljs-keyword">let</span> a = <span class="hljs-string">\'secret\'</span>;\n  <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{\n    <span class="hljs-keyword">return</span> a;\n  }\n}\n\n<span class="hljs-comment">// \u51fd\u6570\u8868\u8fbe\u5f0f</span>\n{\n  <span class="hljs-keyword">let</span> a = <span class="hljs-string">\'secret\'</span>;\n  <span class="hljs-keyword">let</span> f = <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n    <span class="hljs-keyword">return</span> a;\n  };\n}</code></pre>\n<p>\u53e6\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u9700\u8981\u6ce8\u610f\u7684\u5730\u65b9\u3002ES6 \u7684\u5757\u7ea7\u4f5c\u7528\u57df\u5141\u8bb8\u58f0\u660e\u51fd\u6570\u7684\u89c4\u5219\uff0c\u53ea\u5728\u4f7f\u7528\u5927\u62ec\u53f7\u7684\u60c5\u51b5\u4e0b\u6210\u7acb\uff0c\u5982\u679c\u6ca1\u6709\u4f7f\u7528\u5927\u62ec\u53f7\uff0c\u5c31\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u4e0d\u62a5\u9519</span>\n<span class="hljs-meta">\'use strict\'</span>;\n<span class="hljs-keyword">if</span> (<span class="hljs-literal">true</span>) {\n  <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{}\n}\n\n<span class="hljs-comment">// \u62a5\u9519</span>\n<span class="hljs-meta">\'use strict\'</span>;\n<span class="hljs-keyword">if</span> (<span class="hljs-literal">true</span>)\n  <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{}</code></pre>\n<h3 id="do-\u8868\u8fbe\u5f0f">do \u8868\u8fbe\u5f0f</h3>\n<p>\u672c\u8d28\u4e0a\uff0c\u5757\u7ea7\u4f5c\u7528\u57df\u662f\u4e00\u4e2a\u8bed\u53e5\uff0c\u5c06\u591a\u4e2a\u64cd\u4f5c\u5c01\u88c5\u5728\u4e00\u8d77\uff0c\u6ca1\u6709\u8fd4\u56de\u503c\u3002</p>\n<pre><code class="language-javascript">{\n  let <span class="hljs-built_in">t</span> = f();\n  <span class="hljs-built_in">t</span> = <span class="hljs-built_in">t</span> * <span class="hljs-built_in">t</span> + <span class="hljs-number">1</span>;\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5757\u7ea7\u4f5c\u7528\u57df\u5c06\u4e24\u4e2a\u8bed\u53e5\u5c01\u88c5\u5728\u4e00\u8d77\u3002\u4f46\u662f\uff0c\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u4ee5\u5916\uff0c\u6ca1\u6709\u529e\u6cd5\u5f97\u5230<code>t</code>\u7684\u503c\uff0c\u56e0\u4e3a\u5757\u7ea7\u4f5c\u7528\u57df\u4e0d\u8fd4\u56de\u503c\uff0c\u9664\u975e<code>t</code>\u662f\u5168\u5c40\u53d8\u91cf\u3002</p>\n<p>\u73b0\u5728\u6709\u4e00\u4e2a<a href="http://wiki.ecmascript.org/doku.php?id=strawman:do_expressions">\u63d0\u6848</a>\uff0c\u4f7f\u5f97\u5757\u7ea7\u4f5c\u7528\u57df\u53ef\u4ee5\u53d8\u4e3a\u8868\u8fbe\u5f0f\uff0c\u4e5f\u5c31\u662f\u8bf4\u53ef\u4ee5\u8fd4\u56de\u503c\uff0c\u529e\u6cd5\u5c31\u662f\u5728\u5757\u7ea7\u4f5c\u7528\u57df\u4e4b\u524d\u52a0\u4e0a<code>do</code>\uff0c\u4f7f\u5b83\u53d8\u4e3a<code>do</code>\u8868\u8fbe\u5f0f\uff0c\u7136\u540e\u5c31\u4f1a\u8fd4\u56de\u5185\u90e8\u6700\u540e\u6267\u884c\u7684\u8868\u8fbe\u5f0f\u7684\u503c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">let</span> <span class="hljs-attr">x</span> = do {\n  <span class="hljs-keyword">let</span> <span class="hljs-attr">t</span> = f();\n  t * t + <span class="hljs-number">1</span>;\n};</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>x</code>\u4f1a\u5f97\u5230\u6574\u4e2a\u5757\u7ea7\u4f5c\u7528\u57df\u7684\u8fd4\u56de\u503c\uff08<code>t * t + 1</code>\uff09\u3002</p>\n<h2 id="const-\u547d\u4ee4">const \u547d\u4ee4</h2>\n<h3 id="\u57fa\u672c\u7528\u6cd5-1">\u57fa\u672c\u7528\u6cd5</h3>\n<p><code>const</code>\u58f0\u660e\u4e00\u4e2a\u53ea\u8bfb\u7684\u5e38\u91cf\u3002\u4e00\u65e6\u58f0\u660e\uff0c\u5e38\u91cf\u7684\u503c\u5c31\u4e0d\u80fd\u6539\u53d8\u3002</p>\n<pre><code class="language-javascript">const <span class="hljs-literal">PI</span> = <span class="hljs-number">3.1415</span>;\n<span class="hljs-literal">PI</span> <span class="hljs-comment">// 3.1415</span>\n\n<span class="hljs-literal">PI</span> = <span class="hljs-number">3</span>;\n<span class="hljs-comment">// TypeError: Assignment to constant variable.</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u8868\u660e\u6539\u53d8\u5e38\u91cf\u7684\u503c\u4f1a\u62a5\u9519\u3002</p>\n<p><code>const</code>\u58f0\u660e\u7684\u53d8\u91cf\u4e0d\u5f97\u6539\u53d8\u503c\uff0c\u8fd9\u610f\u5473\u7740\uff0c<code>const</code>\u4e00\u65e6\u58f0\u660e\u53d8\u91cf\uff0c\u5c31\u5fc5\u987b\u7acb\u5373\u521d\u59cb\u5316\uff0c\u4e0d\u80fd\u7559\u5230\u4ee5\u540e\u8d4b\u503c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> foo<span class="hljs-comment">;</span>\n// SyntaxError: Missing initializer <span class="hljs-keyword">in</span> <span class="hljs-keyword">const</span> declaration</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u8868\u793a\uff0c\u5bf9\u4e8e<code>const</code>\u6765\u8bf4\uff0c\u53ea\u58f0\u660e\u4e0d\u8d4b\u503c\uff0c\u5c31\u4f1a\u62a5\u9519\u3002</p>\n<p><code>const</code>\u7684\u4f5c\u7528\u57df\u4e0e<code>let</code>\u547d\u4ee4\u76f8\u540c\uff1a\u53ea\u5728\u58f0\u660e\u6240\u5728\u7684\u5757\u7ea7\u4f5c\u7528\u57df\u5185\u6709\u6548\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">if</span> (<span class="hljs-literal">true</span>) {\n  <span class="hljs-keyword">const</span> <span class="hljs-built_in">MAX</span> = <span class="hljs-number">5</span>;\n}\n\n<span class="hljs-built_in">MAX</span> // Uncaught ReferenceError: <span class="hljs-built_in">MAX</span> is <span class="hljs-keyword">not</span> defined</code></pre>\n<p><code>const</code>\u547d\u4ee4\u58f0\u660e\u7684\u5e38\u91cf\u4e5f\u662f\u4e0d\u63d0\u5347\uff0c\u540c\u6837\u5b58\u5728\u6682\u65f6\u6027\u6b7b\u533a\uff0c\u53ea\u80fd\u5728\u58f0\u660e\u7684\u4f4d\u7f6e\u540e\u9762\u4f7f\u7528\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">if</span> (<span class="hljs-literal">true</span>) {\n  console.<span class="hljs-keyword">log</span>(<span class="hljs-keyword">MAX</span>); <span class="hljs-comment">// ReferenceError</span>\n  const <span class="hljs-keyword">MAX</span> = <span class="hljs-number">5</span>;\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u5728\u5e38\u91cf<code>MAX</code>\u58f0\u660e\u4e4b\u524d\u5c31\u8c03\u7528\uff0c\u7ed3\u679c\u62a5\u9519\u3002</p>\n<p><code>const</code>\u58f0\u660e\u7684\u5e38\u91cf\uff0c\u4e5f\u4e0e<code>let</code>\u4e00\u6837\u4e0d\u53ef\u91cd\u590d\u58f0\u660e\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> message = <span class="hljs-string">"Hello!"</span>;\n<span class="hljs-keyword">let</span> age = <span class="hljs-number">25</span>;\n\n<span class="hljs-comment">// \u4ee5\u4e0b\u4e24\u884c\u90fd\u4f1a\u62a5\u9519</span>\n<span class="hljs-keyword">const</span> message = <span class="hljs-string">"Goodbye!"</span>;\n<span class="hljs-keyword">const</span> age = <span class="hljs-number">30</span>;</code></pre>\n<h3 id="\u672c\u8d28">\u672c\u8d28</h3>\n<p><code>const</code>\u5b9e\u9645\u4e0a\u4fdd\u8bc1\u7684\uff0c\u5e76\u4e0d\u662f\u53d8\u91cf\u7684\u503c\u4e0d\u5f97\u6539\u52a8\uff0c\u800c\u662f\u53d8\u91cf\u6307\u5411\u7684\u90a3\u4e2a\u5185\u5b58\u5730\u5740\u4e0d\u5f97\u6539\u52a8\u3002\u5bf9\u4e8e\u7b80\u5355\u7c7b\u578b\u7684\u6570\u636e\uff08\u6570\u503c\u3001\u5b57\u7b26\u4e32\u3001\u5e03\u5c14\u503c\uff09\uff0c\u503c\u5c31\u4fdd\u5b58\u5728\u53d8\u91cf\u6307\u5411\u7684\u90a3\u4e2a\u5185\u5b58\u5730\u5740\uff0c\u56e0\u6b64\u7b49\u540c\u4e8e\u5e38\u91cf\u3002\u4f46\u5bf9\u4e8e\u590d\u5408\u7c7b\u578b\u7684\u6570\u636e\uff08\u4e3b\u8981\u662f\u5bf9\u8c61\u548c\u6570\u7ec4\uff09\uff0c\u53d8\u91cf\u6307\u5411\u7684\u5185\u5b58\u5730\u5740\uff0c\u4fdd\u5b58\u7684\u53ea\u662f\u4e00\u4e2a\u6307\u9488\uff0c<code>const</code>\u53ea\u80fd\u4fdd\u8bc1\u8fd9\u4e2a\u6307\u9488\u662f\u56fa\u5b9a\u7684\uff0c\u81f3\u4e8e\u5b83\u6307\u5411\u7684\u6570\u636e\u7ed3\u6784\u662f\u4e0d\u662f\u53ef\u53d8\u7684\uff0c\u5c31\u5b8c\u5168\u4e0d\u80fd\u63a7\u5236\u4e86\u3002\u56e0\u6b64\uff0c\u5c06\u4e00\u4e2a\u5bf9\u8c61\u58f0\u660e\u4e3a\u5e38\u91cf\u5fc5\u987b\u975e\u5e38\u5c0f\u5fc3\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> foo = {};\n\n<span class="hljs-comment">// \u4e3a foo \u6dfb\u52a0\u4e00\u4e2a\u5c5e\u6027\uff0c\u53ef\u4ee5\u6210\u529f</span>\nfoo.<span class="hljs-keyword">prop</span> = 123;\nfoo.<span class="hljs-keyword">prop</span> <span class="hljs-comment">// 123</span>\n\n<span class="hljs-comment">// \u5c06 foo \u6307\u5411\u53e6\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5c31\u4f1a\u62a5\u9519</span>\nfoo = {}; <span class="hljs-comment">// TypeError: "foo" is read-only</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5e38\u91cf<code>foo</code>\u50a8\u5b58\u7684\u662f\u4e00\u4e2a\u5730\u5740\uff0c\u8fd9\u4e2a\u5730\u5740\u6307\u5411\u4e00\u4e2a\u5bf9\u8c61\u3002\u4e0d\u53ef\u53d8\u7684\u53ea\u662f\u8fd9\u4e2a\u5730\u5740\uff0c\u5373\u4e0d\u80fd\u628a<code>foo</code>\u6307\u5411\u53e6\u4e00\u4e2a\u5730\u5740\uff0c\u4f46\u5bf9\u8c61\u672c\u8eab\u662f\u53ef\u53d8\u7684\uff0c\u6240\u4ee5\u4f9d\u7136\u53ef\u4ee5\u4e3a\u5176\u6dfb\u52a0\u65b0\u5c5e\u6027\u3002</p>\n<p>\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> a = [];\na.push(<span class="hljs-string">\'Hello\'</span>); <span class="hljs-comment">// \u53ef\u6267\u884c</span>\na.length = <span class="hljs-number">0</span>;    <span class="hljs-comment">// \u53ef\u6267\u884c</span>\na = [<span class="hljs-string">\'Dave\'</span>];    <span class="hljs-comment">// \u62a5\u9519</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5e38\u91cf<code>a</code>\u662f\u4e00\u4e2a\u6570\u7ec4\uff0c\u8fd9\u4e2a\u6570\u7ec4\u672c\u8eab\u662f\u53ef\u5199\u7684\uff0c\u4f46\u662f\u5982\u679c\u5c06\u53e6\u4e00\u4e2a\u6570\u7ec4\u8d4b\u503c\u7ed9<code>a</code>\uff0c\u5c31\u4f1a\u62a5\u9519\u3002</p>\n<p>\u5982\u679c\u771f\u7684\u60f3\u5c06\u5bf9\u8c61\u51bb\u7ed3\uff0c\u5e94\u8be5\u4f7f\u7528<code>Object.freeze</code>\u65b9\u6cd5\u3002</p>\n<pre><code class="language-javascript">const foo = <span class="hljs-keyword">Object</span>.<span class="hljs-keyword">freeze</span>({});\n\n// \u5e38\u89c4\u6a21\u5f0f\u65f6\uff0c\u4e0b\u9762\u4e00\u884c\u4e0d\u8d77\u4f5c\u7528\uff1b\n// \u4e25\u683c\u6a21\u5f0f\u65f6\uff0c\u8be5\u884c\u4f1a\u62a5\u9519\nfoo.prop = <span class="hljs-number">123</span>;</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5e38\u91cf<code>foo</code>\u6307\u5411\u4e00\u4e2a\u51bb\u7ed3\u7684\u5bf9\u8c61\uff0c\u6240\u4ee5\u6dfb\u52a0\u65b0\u5c5e\u6027\u4e0d\u8d77\u4f5c\u7528\uff0c\u4e25\u683c\u6a21\u5f0f\u65f6\u8fd8\u4f1a\u62a5\u9519\u3002</p>\n<p>\u9664\u4e86\u5c06\u5bf9\u8c61\u672c\u8eab\u51bb\u7ed3\uff0c\u5bf9\u8c61\u7684\u5c5e\u6027\u4e5f\u5e94\u8be5\u51bb\u7ed3\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u5c06\u5bf9\u8c61\u5f7b\u5e95\u51bb\u7ed3\u7684\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript">var constantize = (obj) =&gt; {\n  <span class="hljs-keyword">Object</span>.<span class="hljs-keyword">freeze</span>(obj);\n  <span class="hljs-keyword">Object</span>.keys(obj).<span class="hljs-keyword">forEach</span>( (key, i) =&gt; {\n    <span class="hljs-keyword">if</span> ( typeof obj[key] === <span class="hljs-string">\'object\'</span> ) {\n      constantize( obj[key] );\n    }\n  });\n};</code></pre>\n<h3 id="es6-\u58f0\u660e\u53d8\u91cf\u7684\u516d\u79cd\u65b9\u6cd5">ES6 \u58f0\u660e\u53d8\u91cf\u7684\u516d\u79cd\u65b9\u6cd5</h3>\n<p>ES5 \u53ea\u6709\u4e24\u79cd\u58f0\u660e\u53d8\u91cf\u7684\u65b9\u6cd5\uff1a<code>var</code>\u547d\u4ee4\u548c<code>function</code>\u547d\u4ee4\u3002ES6 \u9664\u4e86\u6dfb\u52a0<code>let</code>\u548c<code>const</code>\u547d\u4ee4\uff0c\u540e\u9762\u7ae0\u8282\u8fd8\u4f1a\u63d0\u5230\uff0c\u53e6\u5916\u4e24\u79cd\u58f0\u660e\u53d8\u91cf\u7684\u65b9\u6cd5\uff1a<code>import</code>\u547d\u4ee4\u548c<code>class</code>\u547d\u4ee4\u3002\u6240\u4ee5\uff0cES6 \u4e00\u5171\u6709 6 \u79cd\u58f0\u660e\u53d8\u91cf\u7684\u65b9\u6cd5\u3002</p>\n<h2 id="\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027">\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027</h2>\n<p>\u9876\u5c42\u5bf9\u8c61\uff0c\u5728\u6d4f\u89c8\u5668\u73af\u5883\u6307\u7684\u662f<code>window</code>\u5bf9\u8c61\uff0c\u5728 Node \u6307\u7684\u662f<code>global</code>\u5bf9\u8c61\u3002ES5 \u4e4b\u4e2d\uff0c\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\u4e0e\u5168\u5c40\u53d8\u91cf\u662f\u7b49\u4ef7\u7684\u3002</p>\n<pre><code class="language-javascript">window.a = <span class="hljs-number">1</span>;\na <span class="hljs-comment">// 1</span>\n\na = <span class="hljs-number">2</span>;\nwindow.a <span class="hljs-comment">// 2</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\u8d4b\u503c\u4e0e\u5168\u5c40\u53d8\u91cf\u7684\u8d4b\u503c\uff0c\u662f\u540c\u4e00\u4ef6\u4e8b\u3002</p>\n<p>\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\u4e0e\u5168\u5c40\u53d8\u91cf\u6302\u94a9\uff0c\u88ab\u8ba4\u4e3a\u662f JavaScript \u8bed\u8a00\u6700\u5927\u7684\u8bbe\u8ba1\u8d25\u7b14\u4e4b\u4e00\u3002\u8fd9\u6837\u7684\u8bbe\u8ba1\u5e26\u6765\u4e86\u51e0\u4e2a\u5f88\u5927\u7684\u95ee\u9898\uff0c\u9996\u5148\u662f\u6ca1\u6cd5\u5728\u7f16\u8bd1\u65f6\u5c31\u62a5\u51fa\u53d8\u91cf\u672a\u58f0\u660e\u7684\u9519\u8bef\uff0c\u53ea\u6709\u8fd0\u884c\u65f6\u624d\u80fd\u77e5\u9053\uff08\u56e0\u4e3a\u5168\u5c40\u53d8\u91cf\u53ef\u80fd\u662f\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\u521b\u9020\u7684\uff0c\u800c\u5c5e\u6027\u7684\u521b\u9020\u662f\u52a8\u6001\u7684\uff09\uff1b\u5176\u6b21\uff0c\u7a0b\u5e8f\u5458\u5f88\u5bb9\u6613\u4e0d\u77e5\u4e0d\u89c9\u5730\u5c31\u521b\u5efa\u4e86\u5168\u5c40\u53d8\u91cf\uff08\u6bd4\u5982\u6253\u5b57\u51fa\u9519\uff09\uff1b\u6700\u540e\uff0c\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\u662f\u5230\u5904\u53ef\u4ee5\u8bfb\u5199\u7684\uff0c\u8fd9\u975e\u5e38\u4e0d\u5229\u4e8e\u6a21\u5757\u5316\u7f16\u7a0b\u3002\u53e6\u4e00\u65b9\u9762\uff0c<code>window</code>\u5bf9\u8c61\u6709\u5b9e\u4f53\u542b\u4e49\uff0c\u6307\u7684\u662f\u6d4f\u89c8\u5668\u7684\u7a97\u53e3\u5bf9\u8c61\uff0c\u9876\u5c42\u5bf9\u8c61\u662f\u4e00\u4e2a\u6709\u5b9e\u4f53\u542b\u4e49\u7684\u5bf9\u8c61\uff0c\u4e5f\u662f\u4e0d\u5408\u9002\u7684\u3002</p>\n<p>ES6 \u4e3a\u4e86\u6539\u53d8\u8fd9\u4e00\u70b9\uff0c\u4e00\u65b9\u9762\u89c4\u5b9a\uff0c\u4e3a\u4e86\u4fdd\u6301\u517c\u5bb9\u6027\uff0c<code>var</code>\u547d\u4ee4\u548c<code>function</code>\u547d\u4ee4\u58f0\u660e\u7684\u5168\u5c40\u53d8\u91cf\uff0c\u4f9d\u65e7\u662f\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\uff1b\u53e6\u4e00\u65b9\u9762\u89c4\u5b9a\uff0c<code>let</code>\u547d\u4ee4\u3001<code>const</code>\u547d\u4ee4\u3001<code>class</code>\u547d\u4ee4\u58f0\u660e\u7684\u5168\u5c40\u53d8\u91cf\uff0c\u4e0d\u5c5e\u4e8e\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ece ES6 \u5f00\u59cb\uff0c\u5168\u5c40\u53d8\u91cf\u5c06\u9010\u6b65\u4e0e\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\u8131\u94a9\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> a = <span class="hljs-number">1</span>;\n<span class="hljs-comment">// \u5982\u679c\u5728 Node \u7684 REPL \u73af\u5883\uff0c\u53ef\u4ee5\u5199\u6210 global.a</span>\n<span class="hljs-comment">// \u6216\u8005\u91c7\u7528\u901a\u7528\u65b9\u6cd5\uff0c\u5199\u6210 this.a</span>\n<span class="hljs-built_in">window</span>.a <span class="hljs-comment">// 1</span>\n\n<span class="hljs-keyword">let</span> b = <span class="hljs-number">1</span>;\n<span class="hljs-built_in">window</span>.b <span class="hljs-comment">// undefined</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5168\u5c40\u53d8\u91cf<code>a</code>\u7531<code>var</code>\u547d\u4ee4\u58f0\u660e\uff0c\u6240\u4ee5\u5b83\u662f\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\uff1b\u5168\u5c40\u53d8\u91cf<code>b</code>\u7531<code>let</code>\u547d\u4ee4\u58f0\u660e\uff0c\u6240\u4ee5\u5b83\u4e0d\u662f\u9876\u5c42\u5bf9\u8c61\u7684\u5c5e\u6027\uff0c\u8fd4\u56de<code>undefined</code>\u3002</p>\n<h2 id="global-\u5bf9\u8c61">global \u5bf9\u8c61</h2>\n<p>ES5 \u7684\u9876\u5c42\u5bf9\u8c61\uff0c\u672c\u8eab\u4e5f\u662f\u4e00\u4e2a\u95ee\u9898\uff0c\u56e0\u4e3a\u5b83\u5728\u5404\u79cd\u5b9e\u73b0\u91cc\u9762\u662f\u4e0d\u7edf\u4e00\u7684\u3002</p>\n<ul>\n<li>\u6d4f\u89c8\u5668\u91cc\u9762\uff0c\u9876\u5c42\u5bf9\u8c61\u662f<code>window</code>\uff0c\u4f46 Node \u548c Web Worker \u6ca1\u6709<code>window</code>\u3002</li>\n<li>\u6d4f\u89c8\u5668\u548c Web Worker \u91cc\u9762\uff0c<code>self</code>\u4e5f\u6307\u5411\u9876\u5c42\u5bf9\u8c61\uff0c\u4f46\u662f Node \u6ca1\u6709<code>self</code>\u3002</li>\n<li>Node \u91cc\u9762\uff0c\u9876\u5c42\u5bf9\u8c61\u662f<code>global</code>\uff0c\u4f46\u5176\u4ed6\u73af\u5883\u90fd\u4e0d\u652f\u6301\u3002</li>\n</ul>\n<p>\u540c\u4e00\u6bb5\u4ee3\u7801\u4e3a\u4e86\u80fd\u591f\u5728\u5404\u79cd\u73af\u5883\uff0c\u90fd\u80fd\u53d6\u5230\u9876\u5c42\u5bf9\u8c61\uff0c\u73b0\u5728\u4e00\u822c\u662f\u4f7f\u7528<code>this</code>\u53d8\u91cf\uff0c\u4f46\u662f\u6709\u5c40\u9650\u6027\u3002</p>\n<ul>\n<li>\u5168\u5c40\u73af\u5883\u4e2d\uff0c<code>this</code>\u4f1a\u8fd4\u56de\u9876\u5c42\u5bf9\u8c61\u3002\u4f46\u662f\uff0cNode \u6a21\u5757\u548c ES6 \u6a21\u5757\u4e2d\uff0c<code>this</code>\u8fd4\u56de\u7684\u662f\u5f53\u524d\u6a21\u5757\u3002</li>\n<li>\u51fd\u6570\u91cc\u9762\u7684<code>this</code>\uff0c\u5982\u679c\u51fd\u6570\u4e0d\u662f\u4f5c\u4e3a\u5bf9\u8c61\u7684\u65b9\u6cd5\u8fd0\u884c\uff0c\u800c\u662f\u5355\u7eaf\u4f5c\u4e3a\u51fd\u6570\u8fd0\u884c\uff0c<code>this</code>\u4f1a\u6307\u5411\u9876\u5c42\u5bf9\u8c61\u3002\u4f46\u662f\uff0c\u4e25\u683c\u6a21\u5f0f\u4e0b\uff0c\u8fd9\u65f6<code>this</code>\u4f1a\u8fd4\u56de<code>undefined</code>\u3002</li>\n<li>\u4e0d\u7ba1\u662f\u4e25\u683c\u6a21\u5f0f\uff0c\u8fd8\u662f\u666e\u901a\u6a21\u5f0f\uff0c<code>new Function(&#39;return this&#39;)()</code>\uff0c\u603b\u662f\u4f1a\u8fd4\u56de\u5168\u5c40\u5bf9\u8c61\u3002\u4f46\u662f\uff0c\u5982\u679c\u6d4f\u89c8\u5668\u7528\u4e86 CSP\uff08Content Security Policy\uff0c\u5185\u5bb9\u5b89\u5168\u653f\u7b56\uff09\uff0c\u90a3\u4e48<code>eval</code>\u3001<code>new Function</code>\u8fd9\u4e9b\u65b9\u6cd5\u90fd\u53ef\u80fd\u65e0\u6cd5\u4f7f\u7528\u3002</li>\n</ul>\n<p>\u7efc\u4e0a\u6240\u8ff0\uff0c\u5f88\u96be\u627e\u5230\u4e00\u79cd\u65b9\u6cd5\uff0c\u53ef\u4ee5\u5728\u6240\u6709\u60c5\u51b5\u4e0b\uff0c\u90fd\u53d6\u5230\u9876\u5c42\u5bf9\u8c61\u3002\u4e0b\u9762\u662f\u4e24\u79cd\u52c9\u5f3a\u53ef\u4ee5\u4f7f\u7528\u7684\u65b9\u6cd5\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u65b9\u6cd5\u4e00</span>\n(<span class="hljs-keyword">typeof</span> <span class="hljs-built_in">window</span> !== <span class="hljs-string">\'undefined\'</span>\n   ? <span class="hljs-built_in">window</span>\n   : (<span class="hljs-keyword">typeof</span> process === <span class="hljs-string">\'object\'</span> &amp;&amp;\n      <span class="hljs-keyword">typeof</span> <span class="hljs-built_in">require</span> === <span class="hljs-string">\'function\'</span> &amp;&amp;\n      <span class="hljs-keyword">typeof</span> global === <span class="hljs-string">\'object\'</span>)\n     ? global\n     : <span class="hljs-keyword">this</span>);\n\n<span class="hljs-comment">// \u65b9\u6cd5\u4e8c</span>\n<span class="hljs-keyword">var</span> getGlobal = <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">if</span> (<span class="hljs-keyword">typeof</span> self !== <span class="hljs-string">\'undefined\'</span>) { <span class="hljs-keyword">return</span> self; }\n  <span class="hljs-keyword">if</span> (<span class="hljs-keyword">typeof</span> <span class="hljs-built_in">window</span> !== <span class="hljs-string">\'undefined\'</span>) { <span class="hljs-keyword">return</span> <span class="hljs-built_in">window</span>; }\n  <span class="hljs-keyword">if</span> (<span class="hljs-keyword">typeof</span> global !== <span class="hljs-string">\'undefined\'</span>) { <span class="hljs-keyword">return</span> global; }\n  <span class="hljs-keyword">throw</span> <span class="hljs-keyword">new</span> <span class="hljs-built_in">Error</span>(<span class="hljs-string">\'unable to locate global object\'</span>);\n};</code></pre>\n<p>\u73b0\u5728\u6709\u4e00\u4e2a<a href="https://github.com/tc39/proposal-global">\u63d0\u6848</a>\uff0c\u5728\u8bed\u8a00\u6807\u51c6\u7684\u5c42\u9762\uff0c\u5f15\u5165<code>global</code>\u4f5c\u4e3a\u9876\u5c42\u5bf9\u8c61\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5728\u6240\u6709\u73af\u5883\u4e0b\uff0c<code>global</code>\u90fd\u662f\u5b58\u5728\u7684\uff0c\u90fd\u53ef\u4ee5\u4ece\u5b83\u62ff\u5230\u9876\u5c42\u5bf9\u8c61\u3002</p>\n<p>\u57ab\u7247\u5e93<a href="https://github.com/ljharb/System.global"><code>system.global</code></a>\u6a21\u62df\u4e86\u8fd9\u4e2a\u63d0\u6848\uff0c\u53ef\u4ee5\u5728\u6240\u6709\u73af\u5883\u62ff\u5230<code>global</code>\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// CommonJS \u7684\u5199\u6cd5</span>\n<span class="hljs-built_in">require</span>(<span class="hljs-string">\'system.global/shim\'</span>)();\n\n<span class="hljs-comment">// ES6 \u6a21\u5757\u7684\u5199\u6cd5</span>\n<span class="hljs-keyword">import</span> shim <span class="hljs-keyword">from</span> <span class="hljs-string">\'system.global/shim\'</span>; shim();</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u53ef\u4ee5\u4fdd\u8bc1\u5404\u79cd\u73af\u5883\u91cc\u9762\uff0c<code>global</code>\u5bf9\u8c61\u90fd\u662f\u5b58\u5728\u7684\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// CommonJS \u7684\u5199\u6cd5</span>\n<span class="hljs-keyword">var</span> <span class="hljs-keyword">global</span> = <span class="hljs-keyword">require</span>(<span class="hljs-string">\'system.global\'</span>)();\n\n<span class="hljs-comment">// ES6 \u6a21\u5757\u7684\u5199\u6cd5</span>\nimport getGlobal <span class="hljs-keyword">from</span> <span class="hljs-string">\'system.global\'</span>;\n<span class="hljs-keyword">const</span> <span class="hljs-keyword">global</span> = getGlobal();</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u5c06\u9876\u5c42\u5bf9\u8c61\u653e\u5165\u53d8\u91cf<code>global</code>\u3002</p>\n'}}]);