(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([[45],{"7MLK":function(s,a){s.exports='<h1 id="\u51fd\u6570\u7684\u6269\u5c55">\u51fd\u6570\u7684\u6269\u5c55</h1>\n<h2 id="\u51fd\u6570\u53c2\u6570\u7684\u9ed8\u8ba4\u503c">\u51fd\u6570\u53c2\u6570\u7684\u9ed8\u8ba4\u503c</h2>\n<h3 id="\u57fa\u672c\u7528\u6cd5">\u57fa\u672c\u7528\u6cd5</h3>\n<p>ES6 \u4e4b\u524d\uff0c\u4e0d\u80fd\u76f4\u63a5\u4e3a\u51fd\u6570\u7684\u53c2\u6570\u6307\u5b9a\u9ed8\u8ba4\u503c\uff0c\u53ea\u80fd\u91c7\u7528\u53d8\u901a\u7684\u65b9\u6cd5\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">log</span><span class="hljs-params">(x, y)</span> {</span>\n  y = y || <span class="hljs-string">\'World\'</span>;\n  console.<span class="hljs-built_in">log</span>(x, y);\n}\n\n<span class="hljs-built_in">log</span>(<span class="hljs-string">\'Hello\'</span>) <span class="hljs-comment">// Hello World</span>\n<span class="hljs-built_in">log</span>(<span class="hljs-string">\'Hello\'</span>, <span class="hljs-string">\'China\'</span>) <span class="hljs-comment">// Hello China</span>\n<span class="hljs-built_in">log</span>(<span class="hljs-string">\'Hello\'</span>, <span class="hljs-string">\'\'</span>) <span class="hljs-comment">// Hello World</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u68c0\u67e5\u51fd\u6570<code>log</code>\u7684\u53c2\u6570<code>y</code>\u6709\u6ca1\u6709\u8d4b\u503c\uff0c\u5982\u679c\u6ca1\u6709\uff0c\u5219\u6307\u5b9a\u9ed8\u8ba4\u503c\u4e3a<code>World</code>\u3002\u8fd9\u79cd\u5199\u6cd5\u7684\u7f3a\u70b9\u5728\u4e8e\uff0c\u5982\u679c\u53c2\u6570<code>y</code>\u8d4b\u503c\u4e86\uff0c\u4f46\u662f\u5bf9\u5e94\u7684\u5e03\u5c14\u503c\u4e3a<code>false</code>\uff0c\u5219\u8be5\u8d4b\u503c\u4e0d\u8d77\u4f5c\u7528\u3002\u5c31\u50cf\u4e0a\u9762\u4ee3\u7801\u7684\u6700\u540e\u4e00\u884c\uff0c\u53c2\u6570<code>y</code>\u7b49\u4e8e\u7a7a\u5b57\u7b26\uff0c\u7ed3\u679c\u88ab\u6539\u4e3a\u9ed8\u8ba4\u503c\u3002</p>\n<p>\u4e3a\u4e86\u907f\u514d\u8fd9\u4e2a\u95ee\u9898\uff0c\u901a\u5e38\u9700\u8981\u5148\u5224\u65ad\u4e00\u4e0b\u53c2\u6570<code>y</code>\u662f\u5426\u88ab\u8d4b\u503c\uff0c\u5982\u679c\u6ca1\u6709\uff0c\u518d\u7b49\u4e8e\u9ed8\u8ba4\u503c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keywords">if</span> (<span class="hljs-built_in">typeof</span> <span class="hljs-symbol">y</span> === <span class="hljs-string">\'undefined\'</span>) {\n  <span class="hljs-symbol">y</span> = <span class="hljs-string">\'World\'</span>;\n}</code></pre>\n<p>ES6 \u5141\u8bb8\u4e3a\u51fd\u6570\u7684\u53c2\u6570\u8bbe\u7f6e\u9ed8\u8ba4\u503c\uff0c\u5373\u76f4\u63a5\u5199\u5728\u53c2\u6570\u5b9a\u4e49\u7684\u540e\u9762\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">log</span><span class="hljs-params">(x, y = \'World\')</span> {</span>\n  console.<span class="hljs-built_in">log</span>(x, y);\n}\n\n<span class="hljs-built_in">log</span>(<span class="hljs-string">\'Hello\'</span>) <span class="hljs-comment">// Hello World</span>\n<span class="hljs-built_in">log</span>(<span class="hljs-string">\'Hello\'</span>, <span class="hljs-string">\'China\'</span>) <span class="hljs-comment">// Hello China</span>\n<span class="hljs-built_in">log</span>(<span class="hljs-string">\'Hello\'</span>, <span class="hljs-string">\'\'</span>) <span class="hljs-comment">// Hello</span></code></pre>\n<p>\u53ef\u4ee5\u770b\u5230\uff0cES6 \u7684\u5199\u6cd5\u6bd4 ES5 \u7b80\u6d01\u8bb8\u591a\uff0c\u800c\u4e14\u975e\u5e38\u81ea\u7136\u3002\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">Point</span><span class="hljs-params">(x = 0, y = 0)</span> </span>{\n  <span class="hljs-keyword">this</span>.x = x;\n  <span class="hljs-keyword">this</span>.y = y;\n}\n\n<span class="hljs-keyword">const</span> p = <span class="hljs-keyword">new</span> Point();\np <span class="hljs-comment">// { x: 0, y: 0 }</span></code></pre>\n<p>\u9664\u4e86\u7b80\u6d01\uff0cES6 \u7684\u5199\u6cd5\u8fd8\u6709\u4e24\u4e2a\u597d\u5904\uff1a\u9996\u5148\uff0c\u9605\u8bfb\u4ee3\u7801\u7684\u4eba\uff0c\u53ef\u4ee5\u7acb\u523b\u610f\u8bc6\u5230\u54ea\u4e9b\u53c2\u6570\u662f\u53ef\u4ee5\u7701\u7565\u7684\uff0c\u4e0d\u7528\u67e5\u770b\u51fd\u6570\u4f53\u6216\u6587\u6863\uff1b\u5176\u6b21\uff0c\u6709\u5229\u4e8e\u5c06\u6765\u7684\u4ee3\u7801\u4f18\u5316\uff0c\u5373\u4f7f\u672a\u6765\u7684\u7248\u672c\u5728\u5bf9\u5916\u63a5\u53e3\u4e2d\uff0c\u5f7b\u5e95\u62ff\u6389\u8fd9\u4e2a\u53c2\u6570\uff0c\u4e5f\u4e0d\u4f1a\u5bfc\u81f4\u4ee5\u524d\u7684\u4ee3\u7801\u65e0\u6cd5\u8fd0\u884c\u3002</p>\n<p>\u53c2\u6570\u53d8\u91cf\u662f\u9ed8\u8ba4\u58f0\u660e\u7684\uff0c\u6240\u4ee5\u4e0d\u80fd\u7528<code>let</code>\u6216<code>const</code>\u518d\u6b21\u58f0\u660e\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span>(<span class="hljs-params">x = <span class="hljs-number">5</span></span>) </span>{\n  <span class="hljs-keyword">let</span> x = <span class="hljs-number">1</span>; <span class="hljs-comment">// error</span>\n  <span class="hljs-keyword">const</span> x = <span class="hljs-number">2</span>; <span class="hljs-comment">// error</span>\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53c2\u6570\u53d8\u91cf<code>x</code>\u662f\u9ed8\u8ba4\u58f0\u660e\u7684\uff0c\u5728\u51fd\u6570\u4f53\u4e2d\uff0c\u4e0d\u80fd\u7528<code>let</code>\u6216<code>const</code>\u518d\u6b21\u58f0\u660e\uff0c\u5426\u5219\u4f1a\u62a5\u9519\u3002</p>\n<p>\u4f7f\u7528\u53c2\u6570\u9ed8\u8ba4\u503c\u65f6\uff0c\u51fd\u6570\u4e0d\u80fd\u6709\u540c\u540d\u53c2\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u4e0d\u62a5\u9519</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span><span class="hljs-params">(x, x, y)</span> </span>{\n  <span class="hljs-comment">// ...</span>\n}\n\n<span class="hljs-comment">// \u62a5\u9519</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span><span class="hljs-params">(x, x, y = 1)</span> </span>{\n  <span class="hljs-comment">// ...</span>\n}\n<span class="hljs-comment">// SyntaxError: Duplicate parameter name not allowed in this context</span></code></pre>\n<p>\u53e6\u5916\uff0c\u4e00\u4e2a\u5bb9\u6613\u5ffd\u7565\u7684\u5730\u65b9\u662f\uff0c\u53c2\u6570\u9ed8\u8ba4\u503c\u4e0d\u662f\u4f20\u503c\u7684\uff0c\u800c\u662f\u6bcf\u6b21\u90fd\u91cd\u65b0\u8ba1\u7b97\u9ed8\u8ba4\u503c\u8868\u8fbe\u5f0f\u7684\u503c\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u53c2\u6570\u9ed8\u8ba4\u503c\u662f\u60f0\u6027\u6c42\u503c\u7684\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">let</span> x = <span class="hljs-number">99</span>;\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span>(<span class="hljs-params">p = x + <span class="hljs-number">1</span></span>) </span>{\n  <span class="hljs-built_in">console</span>.log(p);\n}\n\nfoo() <span class="hljs-comment">// 100</span>\n\nx = <span class="hljs-number">100</span>;\nfoo() <span class="hljs-comment">// 101</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53c2\u6570<code>p</code>\u7684\u9ed8\u8ba4\u503c\u662f<code>x + 1</code>\u3002\u8fd9\u65f6\uff0c\u6bcf\u6b21\u8c03\u7528\u51fd\u6570<code>foo</code>\uff0c\u90fd\u4f1a\u91cd\u65b0\u8ba1\u7b97<code>x + 1</code>\uff0c\u800c\u4e0d\u662f\u9ed8\u8ba4<code>p</code>\u7b49\u4e8e 100\u3002</p>\n<h3 id="\u4e0e\u89e3\u6784\u8d4b\u503c\u9ed8\u8ba4\u503c\u7ed3\u5408\u4f7f\u7528">\u4e0e\u89e3\u6784\u8d4b\u503c\u9ed8\u8ba4\u503c\u7ed3\u5408\u4f7f\u7528</h3>\n<p>\u53c2\u6570\u9ed8\u8ba4\u503c\u53ef\u4ee5\u4e0e\u89e3\u6784\u8d4b\u503c\u7684\u9ed8\u8ba4\u503c\uff0c\u7ed3\u5408\u8d77\u6765\u4f7f\u7528\u3002</p>\n<pre><code class="language-javascript">function foo({<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span> = <span class="hljs-number">5</span>}) {\n  console.log(<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>);\n}\n\nfoo({}) <span class="hljs-comment">// undefined 5</span>\nfoo({<span class="hljs-symbol">x</span>: <span class="hljs-number">1</span>}) <span class="hljs-comment">// 1 5</span>\nfoo({<span class="hljs-symbol">x</span>: <span class="hljs-number">1</span>, <span class="hljs-symbol">y</span>: <span class="hljs-number">2</span>}) <span class="hljs-comment">// 1 2</span>\nfoo() <span class="hljs-comment">// TypeError: Cannot read property \'x\' of undefined</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u53ea\u4f7f\u7528\u4e86\u5bf9\u8c61\u7684\u89e3\u6784\u8d4b\u503c\u9ed8\u8ba4\u503c\uff0c\u6ca1\u6709\u4f7f\u7528\u51fd\u6570\u53c2\u6570\u7684\u9ed8\u8ba4\u503c\u3002\u53ea\u6709\u5f53\u51fd\u6570<code>foo</code>\u7684\u53c2\u6570\u662f\u4e00\u4e2a\u5bf9\u8c61\u65f6\uff0c\u53d8\u91cf<code>x</code>\u548c<code>y</code>\u624d\u4f1a\u901a\u8fc7\u89e3\u6784\u8d4b\u503c\u751f\u6210\u3002\u5982\u679c\u51fd\u6570<code>foo</code>\u8c03\u7528\u65f6\u6ca1\u63d0\u4f9b\u53c2\u6570\uff0c\u53d8\u91cf<code>x</code>\u548c<code>y</code>\u5c31\u4e0d\u4f1a\u751f\u6210\uff0c\u4ece\u800c\u62a5\u9519\u3002\u901a\u8fc7\u63d0\u4f9b\u51fd\u6570\u53c2\u6570\u7684\u9ed8\u8ba4\u503c\uff0c\u5c31\u53ef\u4ee5\u907f\u514d\u8fd9\u79cd\u60c5\u51b5\u3002</p>\n<pre><code class="language-javascript">function foo({<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span> = <span class="hljs-number">5</span>} = {}) {\n  console.log(<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>);\n}\n\nfoo() <span class="hljs-comment">// undefined 5</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u6307\u5b9a\uff0c\u5982\u679c\u6ca1\u6709\u63d0\u4f9b\u53c2\u6570\uff0c\u51fd\u6570<code>foo</code>\u7684\u53c2\u6570\u9ed8\u8ba4\u4e3a\u4e00\u4e2a\u7a7a\u5bf9\u8c61\u3002</p>\n<p>\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u89e3\u6784\u8d4b\u503c\u9ed8\u8ba4\u503c\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">function</span> <span class="hljs-keyword">fetch</span>(url, { body = <span class="hljs-string">\'\'</span>, <span class="hljs-keyword">method</span> = <span class="hljs-string">\'GET\'</span>, headers = {} }) {\n  console.log(<span class="hljs-keyword">method</span>);\n}\n\n<span class="hljs-keyword">fetch</span>(<span class="hljs-string">\'http://example.com\'</span>, {})\n// "GET"\n\n<span class="hljs-keyword">fetch</span>(<span class="hljs-string">\'http://example.com\'</span>)\n// \u62a5\u9519</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5982\u679c\u51fd\u6570<code>fetch</code>\u7684\u7b2c\u4e8c\u4e2a\u53c2\u6570\u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5c31\u53ef\u4ee5\u4e3a\u5b83\u7684\u4e09\u4e2a\u5c5e\u6027\u8bbe\u7f6e\u9ed8\u8ba4\u503c\u3002\u8fd9\u79cd\u5199\u6cd5\u4e0d\u80fd\u7701\u7565\u7b2c\u4e8c\u4e2a\u53c2\u6570\uff0c\u5982\u679c\u7ed3\u5408\u51fd\u6570\u53c2\u6570\u7684\u9ed8\u8ba4\u503c\uff0c\u5c31\u53ef\u4ee5\u7701\u7565\u7b2c\u4e8c\u4e2a\u53c2\u6570\u3002\u8fd9\u65f6\uff0c\u5c31\u51fa\u73b0\u4e86\u53cc\u91cd\u9ed8\u8ba4\u503c\u3002</p>\n<pre><code class="language-javascript">function fetch(url, { body = \'\', method = \'GET\', headers = {} } = {}) {\n  console.log(method);\n}\n\nfetch(\'http://example.com\')\n// <span class="hljs-string">"<span class="hljs-keyword">GET</span>"</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570<code>fetch</code>\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u53c2\u6570\u65f6\uff0c\u51fd\u6570\u53c2\u6570\u7684\u9ed8\u8ba4\u503c\u5c31\u4f1a\u751f\u6548\uff0c\u7136\u540e\u624d\u662f\u89e3\u6784\u8d4b\u503c\u7684\u9ed8\u8ba4\u503c\u751f\u6548\uff0c\u53d8\u91cf<code>method</code>\u624d\u4f1a\u53d6\u5230\u9ed8\u8ba4\u503c<code>GET</code>\u3002</p>\n<p>\u4f5c\u4e3a\u7ec3\u4e60\uff0c\u8bf7\u95ee\u4e0b\u9762\u4e24\u79cd\u5199\u6cd5\u6709\u4ec0\u4e48\u5dee\u522b\uff1f</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u5199\u6cd5\u4e00</span>\nfunction m1({<span class="hljs-symbol">x</span> = <span class="hljs-number">0</span>, <span class="hljs-symbol">y</span> = <span class="hljs-number">0</span>} = {}) {\n  <span class="hljs-keywords">return</span> [<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>];\n}\n\n<span class="hljs-comment">// \u5199\u6cd5\u4e8c</span>\nfunction m2({<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>} = { <span class="hljs-symbol">x</span>: <span class="hljs-number">0</span>, <span class="hljs-symbol">y</span>: <span class="hljs-number">0</span> }) {\n  <span class="hljs-keywords">return</span> [<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>];\n}</code></pre>\n<p>\u4e0a\u9762\u4e24\u79cd\u5199\u6cd5\u90fd\u5bf9\u51fd\u6570\u7684\u53c2\u6570\u8bbe\u5b9a\u4e86\u9ed8\u8ba4\u503c\uff0c\u533a\u522b\u662f\u5199\u6cd5\u4e00\u51fd\u6570\u53c2\u6570\u7684\u9ed8\u8ba4\u503c\u662f\u7a7a\u5bf9\u8c61\uff0c\u4f46\u662f\u8bbe\u7f6e\u4e86\u5bf9\u8c61\u89e3\u6784\u8d4b\u503c\u7684\u9ed8\u8ba4\u503c\uff1b\u5199\u6cd5\u4e8c\u51fd\u6570\u53c2\u6570\u7684\u9ed8\u8ba4\u503c\u662f\u4e00\u4e2a\u6709\u5177\u4f53\u5c5e\u6027\u7684\u5bf9\u8c61\uff0c\u4f46\u662f\u6ca1\u6709\u8bbe\u7f6e\u5bf9\u8c61\u89e3\u6784\u8d4b\u503c\u7684\u9ed8\u8ba4\u503c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u51fd\u6570\u6ca1\u6709\u53c2\u6570\u7684\u60c5\u51b5</span>\n<span class="hljs-function"><span class="hljs-title">m1</span><span class="hljs-params">()</span></span> <span class="hljs-comment">// [0, 0]</span>\n<span class="hljs-function"><span class="hljs-title">m2</span><span class="hljs-params">()</span></span> <span class="hljs-comment">// [0, 0]</span>\n\n<span class="hljs-comment">// x \u548c y \u90fd\u6709\u503c\u7684\u60c5\u51b5</span>\n<span class="hljs-function"><span class="hljs-title">m1</span><span class="hljs-params">({x: <span class="hljs-number">3</span>, y: <span class="hljs-number">8</span>})</span></span> <span class="hljs-comment">// [3, 8]</span>\n<span class="hljs-function"><span class="hljs-title">m2</span><span class="hljs-params">({x: <span class="hljs-number">3</span>, y: <span class="hljs-number">8</span>})</span></span> <span class="hljs-comment">// [3, 8]</span>\n\n<span class="hljs-comment">// x \u6709\u503c\uff0cy \u65e0\u503c\u7684\u60c5\u51b5</span>\n<span class="hljs-function"><span class="hljs-title">m1</span><span class="hljs-params">({x: <span class="hljs-number">3</span>})</span></span> <span class="hljs-comment">// [3, 0]</span>\n<span class="hljs-function"><span class="hljs-title">m2</span><span class="hljs-params">({x: <span class="hljs-number">3</span>})</span></span> <span class="hljs-comment">// [3, undefined]</span>\n\n<span class="hljs-comment">// x \u548c y \u90fd\u65e0\u503c\u7684\u60c5\u51b5</span>\n<span class="hljs-function"><span class="hljs-title">m1</span><span class="hljs-params">({})</span></span> <span class="hljs-comment">// [0, 0];</span>\n<span class="hljs-function"><span class="hljs-title">m2</span><span class="hljs-params">({})</span></span> <span class="hljs-comment">// [undefined, undefined]</span>\n\n<span class="hljs-function"><span class="hljs-title">m1</span><span class="hljs-params">({z: <span class="hljs-number">3</span>})</span></span> <span class="hljs-comment">// [0, 0]</span>\n<span class="hljs-function"><span class="hljs-title">m2</span><span class="hljs-params">({z: <span class="hljs-number">3</span>})</span></span> <span class="hljs-comment">// [undefined, undefined]</span></code></pre>\n<h3 id="\u53c2\u6570\u9ed8\u8ba4\u503c\u7684\u4f4d\u7f6e">\u53c2\u6570\u9ed8\u8ba4\u503c\u7684\u4f4d\u7f6e</h3>\n<p>\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u5b9a\u4e49\u4e86\u9ed8\u8ba4\u503c\u7684\u53c2\u6570\uff0c\u5e94\u8be5\u662f\u51fd\u6570\u7684\u5c3e\u53c2\u6570\u3002\u56e0\u4e3a\u8fd9\u6837\u6bd4\u8f83\u5bb9\u6613\u770b\u51fa\u6765\uff0c\u5230\u5e95\u7701\u7565\u4e86\u54ea\u4e9b\u53c2\u6570\u3002\u5982\u679c\u975e\u5c3e\u90e8\u7684\u53c2\u6570\u8bbe\u7f6e\u9ed8\u8ba4\u503c\uff0c\u5b9e\u9645\u4e0a\u8fd9\u4e2a\u53c2\u6570\u662f\u6ca1\u6cd5\u7701\u7565\u7684\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u4f8b\u4e00</span>\nfunction f(<span class="hljs-symbol">x</span> = <span class="hljs-number">1</span>, <span class="hljs-symbol">y</span>) {\n  <span class="hljs-keywords">return</span> [<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>];\n}\n\nf() <span class="hljs-comment">// [1, undefined]</span>\nf(<span class="hljs-number">2</span>) <span class="hljs-comment">// [2, undefined])</span>\nf(, <span class="hljs-number">1</span>) <span class="hljs-comment">// \u62a5\u9519</span>\nf(<span class="hljs-literal">undefined</span>, <span class="hljs-number">1</span>) <span class="hljs-comment">// [1, 1]</span>\n\n<span class="hljs-comment">// \u4f8b\u4e8c</span>\nfunction f(<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span> = <span class="hljs-number">5</span>, z) {\n  <span class="hljs-keywords">return</span> [<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>, z];\n}\n\nf() <span class="hljs-comment">// [undefined, 5, undefined]</span>\nf(<span class="hljs-number">1</span>) <span class="hljs-comment">// [1, 5, undefined]</span>\nf(<span class="hljs-number">1</span>, ,<span class="hljs-number">2</span>) <span class="hljs-comment">// \u62a5\u9519</span>\nf(<span class="hljs-number">1</span>, <span class="hljs-literal">undefined</span>, <span class="hljs-number">2</span>) <span class="hljs-comment">// [1, 5, 2]</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6709\u9ed8\u8ba4\u503c\u7684\u53c2\u6570\u90fd\u4e0d\u662f\u5c3e\u53c2\u6570\u3002\u8fd9\u65f6\uff0c\u65e0\u6cd5\u53ea\u7701\u7565\u8be5\u53c2\u6570\uff0c\u800c\u4e0d\u7701\u7565\u5b83\u540e\u9762\u7684\u53c2\u6570\uff0c\u9664\u975e\u663e\u5f0f\u8f93\u5165<code>undefined</code>\u3002</p>\n<p>\u5982\u679c\u4f20\u5165<code>undefined</code>\uff0c\u5c06\u89e6\u53d1\u8be5\u53c2\u6570\u7b49\u4e8e\u9ed8\u8ba4\u503c\uff0c<code>null</code>\u5219\u6ca1\u6709\u8fd9\u4e2a\u6548\u679c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span>(<span class="hljs-params">x = <span class="hljs-number">5</span>, y = <span class="hljs-number">6</span></span>) </span>{\n  <span class="hljs-built_in">console</span>.log(x, y);\n}\n\nfoo(<span class="hljs-literal">undefined</span>, <span class="hljs-literal">null</span>)\n<span class="hljs-comment">// 5 null</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>x</code>\u53c2\u6570\u5bf9\u5e94<code>undefined</code>\uff0c\u7ed3\u679c\u89e6\u53d1\u4e86\u9ed8\u8ba4\u503c\uff0c<code>y</code>\u53c2\u6570\u7b49\u4e8e<code>null</code>\uff0c\u5c31\u6ca1\u6709\u89e6\u53d1\u9ed8\u8ba4\u503c\u3002</p>\n<h3 id="\u51fd\u6570\u7684-length-\u5c5e\u6027">\u51fd\u6570\u7684 length \u5c5e\u6027</h3>\n<p>\u6307\u5b9a\u4e86\u9ed8\u8ba4\u503c\u4ee5\u540e\uff0c\u51fd\u6570\u7684<code>length</code>\u5c5e\u6027\uff0c\u5c06\u8fd4\u56de\u6ca1\u6709\u6307\u5b9a\u9ed8\u8ba4\u503c\u7684\u53c2\u6570\u4e2a\u6570\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u6307\u5b9a\u4e86\u9ed8\u8ba4\u503c\u540e\uff0c<code>length</code>\u5c5e\u6027\u5c06\u5931\u771f\u3002</p>\n<pre><code class="language-javascript">(function (a) {})<span class="hljs-selector-class">.length</span> <span class="hljs-comment">// 1</span>\n(function (<span class="hljs-selector-tag">a</span> = <span class="hljs-number">5</span>) {})<span class="hljs-selector-class">.length</span> <span class="hljs-comment">// 0</span>\n(function (<span class="hljs-selector-tag">a</span>, <span class="hljs-selector-tag">b</span>, c = <span class="hljs-number">5</span>) {})<span class="hljs-selector-class">.length</span> <span class="hljs-comment">// 2</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>length</code>\u5c5e\u6027\u7684\u8fd4\u56de\u503c\uff0c\u7b49\u4e8e\u51fd\u6570\u7684\u53c2\u6570\u4e2a\u6570\u51cf\u53bb\u6307\u5b9a\u4e86\u9ed8\u8ba4\u503c\u7684\u53c2\u6570\u4e2a\u6570\u3002\u6bd4\u5982\uff0c\u4e0a\u9762\u6700\u540e\u4e00\u4e2a\u51fd\u6570\uff0c\u5b9a\u4e49\u4e86 3 \u4e2a\u53c2\u6570\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u53c2\u6570<code>c</code>\u6307\u5b9a\u4e86\u9ed8\u8ba4\u503c\uff0c\u56e0\u6b64<code>length</code>\u5c5e\u6027\u7b49\u4e8e<code>3</code>\u51cf\u53bb<code>1</code>\uff0c\u6700\u540e\u5f97\u5230<code>2</code>\u3002</p>\n<p>\u8fd9\u662f\u56e0\u4e3a<code>length</code>\u5c5e\u6027\u7684\u542b\u4e49\u662f\uff0c\u8be5\u51fd\u6570\u9884\u671f\u4f20\u5165\u7684\u53c2\u6570\u4e2a\u6570\u3002\u67d0\u4e2a\u53c2\u6570\u6307\u5b9a\u9ed8\u8ba4\u503c\u4ee5\u540e\uff0c\u9884\u671f\u4f20\u5165\u7684\u53c2\u6570\u4e2a\u6570\u5c31\u4e0d\u5305\u62ec\u8fd9\u4e2a\u53c2\u6570\u4e86\u3002\u540c\u7406\uff0c\u540e\u6587\u7684 rest \u53c2\u6570\u4e5f\u4e0d\u4f1a\u8ba1\u5165<code>length</code>\u5c5e\u6027\u3002</p>\n<pre><code class="language-javascript">(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(<span class="hljs-rest_arg">...args</span>)</span> </span>{}).length <span class="hljs-comment">// 0</span></code></pre>\n<p>\u5982\u679c\u8bbe\u7f6e\u4e86\u9ed8\u8ba4\u503c\u7684\u53c2\u6570\u4e0d\u662f\u5c3e\u53c2\u6570\uff0c\u90a3\u4e48<code>length</code>\u5c5e\u6027\u4e5f\u4e0d\u518d\u8ba1\u5165\u540e\u9762\u7684\u53c2\u6570\u4e86\u3002</p>\n<pre><code class="language-javascript">(function (<span class="hljs-selector-tag">a</span> = <span class="hljs-number">0</span>, <span class="hljs-selector-tag">b</span>, c) {})<span class="hljs-selector-class">.length</span> <span class="hljs-comment">// 0</span>\n(function (<span class="hljs-selector-tag">a</span>, <span class="hljs-selector-tag">b</span> = <span class="hljs-number">1</span>, c) {})<span class="hljs-selector-class">.length</span> <span class="hljs-comment">// 1</span></code></pre>\n<h3 id="\u4f5c\u7528\u57df">\u4f5c\u7528\u57df</h3>\n<p>\u4e00\u65e6\u8bbe\u7f6e\u4e86\u53c2\u6570\u7684\u9ed8\u8ba4\u503c\uff0c\u51fd\u6570\u8fdb\u884c\u58f0\u660e\u521d\u59cb\u5316\u65f6\uff0c\u53c2\u6570\u4f1a\u5f62\u6210\u4e00\u4e2a\u5355\u72ec\u7684\u4f5c\u7528\u57df\uff08context\uff09\u3002\u7b49\u5230\u521d\u59cb\u5316\u7ed3\u675f\uff0c\u8fd9\u4e2a\u4f5c\u7528\u57df\u5c31\u4f1a\u6d88\u5931\u3002\u8fd9\u79cd\u8bed\u6cd5\u884c\u4e3a\uff0c\u5728\u4e0d\u8bbe\u7f6e\u53c2\u6570\u9ed8\u8ba4\u503c\u65f6\uff0c\u662f\u4e0d\u4f1a\u51fa\u73b0\u7684\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keywords">var</span> <span class="hljs-symbol">x</span> = <span class="hljs-number">1</span>;\n\nfunction f(<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span> = <span class="hljs-symbol">x</span>) {\n  console.log(<span class="hljs-symbol">y</span>);\n}\n\nf(<span class="hljs-number">2</span>) <span class="hljs-comment">// 2</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53c2\u6570<code>y</code>\u7684\u9ed8\u8ba4\u503c\u7b49\u4e8e\u53d8\u91cf<code>x</code>\u3002\u8c03\u7528\u51fd\u6570<code>f</code>\u65f6\uff0c\u53c2\u6570\u5f62\u6210\u4e00\u4e2a\u5355\u72ec\u7684\u4f5c\u7528\u57df\u3002\u5728\u8fd9\u4e2a\u4f5c\u7528\u57df\u91cc\u9762\uff0c\u9ed8\u8ba4\u503c\u53d8\u91cf<code>x</code>\u6307\u5411\u7b2c\u4e00\u4e2a\u53c2\u6570<code>x</code>\uff0c\u800c\u4e0d\u662f\u5168\u5c40\u53d8\u91cf<code>x</code>\uff0c\u6240\u4ee5\u8f93\u51fa\u662f<code>2</code>\u3002</p>\n<p>\u518d\u770b\u4e0b\u9762\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">let</span> x = <span class="hljs-number">1</span>;\n\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params">y = x</span>) </span>{\n  <span class="hljs-keyword">let</span> x = <span class="hljs-number">2</span>;\n  <span class="hljs-built_in">console</span>.log(y);\n}\n\nf() <span class="hljs-comment">// 1</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570<code>f</code>\u8c03\u7528\u65f6\uff0c\u53c2\u6570<code>y = x</code>\u5f62\u6210\u4e00\u4e2a\u5355\u72ec\u7684\u4f5c\u7528\u57df\u3002\u8fd9\u4e2a\u4f5c\u7528\u57df\u91cc\u9762\uff0c\u53d8\u91cf<code>x</code>\u672c\u8eab\u6ca1\u6709\u5b9a\u4e49\uff0c\u6240\u4ee5\u6307\u5411\u5916\u5c42\u7684\u5168\u5c40\u53d8\u91cf<code>x</code>\u3002\u51fd\u6570\u8c03\u7528\u65f6\uff0c\u51fd\u6570\u4f53\u5185\u90e8\u7684\u5c40\u90e8\u53d8\u91cf<code>x</code>\u5f71\u54cd\u4e0d\u5230\u9ed8\u8ba4\u503c\u53d8\u91cf<code>x</code>\u3002</p>\n<p>\u5982\u679c\u6b64\u65f6\uff0c\u5168\u5c40\u53d8\u91cf<code>x</code>\u4e0d\u5b58\u5728\uff0c\u5c31\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params">y = x</span>) </span>{\n  <span class="hljs-keyword">let</span> x = <span class="hljs-number">2</span>;\n  <span class="hljs-built_in">console</span>.log(y);\n}\n\nf() <span class="hljs-comment">// ReferenceError: x is not defined</span></code></pre>\n<p>\u4e0b\u9762\u8fd9\u6837\u5199\uff0c\u4e5f\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> x = <span class="hljs-number">1</span>;\n\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span><span class="hljs-params">(x = x)</span> <span class="hljs-comment">{\n  // ...\n}</span>\n\n<span class="hljs-title">foo</span><span class="hljs-params">()</span> // <span class="hljs-title">ReferenceError</span>:</span> x <span class="hljs-keyword">is</span> <span class="hljs-keyword">not</span> defined</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53c2\u6570<code>x = x</code>\u5f62\u6210\u4e00\u4e2a\u5355\u72ec\u4f5c\u7528\u57df\u3002\u5b9e\u9645\u6267\u884c\u7684\u662f<code>let x = x</code>\uff0c\u7531\u4e8e\u6682\u65f6\u6027\u6b7b\u533a\u7684\u539f\u56e0\uff0c\u8fd9\u884c\u4ee3\u7801\u4f1a\u62a5\u9519\u201dx \u672a\u5b9a\u4e49\u201c\u3002</p>\n<p>\u5982\u679c\u53c2\u6570\u7684\u9ed8\u8ba4\u503c\u662f\u4e00\u4e2a\u51fd\u6570\uff0c\u8be5\u51fd\u6570\u7684\u4f5c\u7528\u57df\u4e5f\u9075\u5b88\u8fd9\u4e2a\u89c4\u5219\u3002\u8bf7\u770b\u4e0b\u9762\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript">let foo = <span class="hljs-string">\'outer\'</span><span class="hljs-comment">;</span>\n\nfunction bar(<span class="hljs-function"><span class="hljs-keyword">func</span> = <span class="hljs-params">()</span> =&gt; <span class="hljs-title">foo</span>) {</span>\n  let foo = <span class="hljs-string">\'inner\'</span><span class="hljs-comment">;</span>\n  console.<span class="hljs-built_in">log</span>(<span class="hljs-function"><span class="hljs-keyword">func</span><span class="hljs-params">()</span>);</span>\n}\n\nbar()<span class="hljs-comment">; // outer</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570<code>bar</code>\u7684\u53c2\u6570<code>func</code>\u7684\u9ed8\u8ba4\u503c\u662f\u4e00\u4e2a\u533f\u540d\u51fd\u6570\uff0c\u8fd4\u56de\u503c\u4e3a\u53d8\u91cf<code>foo</code>\u3002\u51fd\u6570\u53c2\u6570\u5f62\u6210\u7684\u5355\u72ec\u4f5c\u7528\u57df\u91cc\u9762\uff0c\u5e76\u6ca1\u6709\u5b9a\u4e49\u53d8\u91cf<code>foo</code>\uff0c\u6240\u4ee5<code>foo</code>\u6307\u5411\u5916\u5c42\u7684\u5168\u5c40\u53d8\u91cf<code>foo</code>\uff0c\u56e0\u6b64\u8f93\u51fa<code>outer</code>\u3002</p>\n<p>\u5982\u679c\u5199\u6210\u4e0b\u9762\u8fd9\u6837\uff0c\u5c31\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript">function bar(<span class="hljs-function"><span class="hljs-keyword">func</span> = <span class="hljs-params">()</span> =&gt; <span class="hljs-title">foo</span>) {</span>\n  let foo = <span class="hljs-string">\'inner\'</span><span class="hljs-comment">;</span>\n  console.<span class="hljs-built_in">log</span>(<span class="hljs-function"><span class="hljs-keyword">func</span><span class="hljs-params">()</span>);</span>\n}\n\nbar() // ReferenceError: foo is <span class="hljs-literal">not</span> defined</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u533f\u540d\u51fd\u6570\u91cc\u9762\u7684<code>foo</code>\u6307\u5411\u51fd\u6570\u5916\u5c42\uff0c\u4f46\u662f\u51fd\u6570\u5916\u5c42\u5e76\u6ca1\u6709\u58f0\u660e\u53d8\u91cf<code>foo</code>\uff0c\u6240\u4ee5\u5c31\u62a5\u9519\u4e86\u3002</p>\n<p>\u4e0b\u9762\u662f\u4e00\u4e2a\u66f4\u590d\u6742\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keywords">var</span> <span class="hljs-symbol">x</span> = <span class="hljs-number">1</span>;\nfunction foo(<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span> = function() { <span class="hljs-symbol">x</span> = <span class="hljs-number">2</span>; }) {\n  <span class="hljs-keywords">var</span> <span class="hljs-symbol">x</span> = <span class="hljs-number">3</span>;\n  <span class="hljs-symbol">y</span>();\n  console.log(<span class="hljs-symbol">x</span>);\n}\n\nfoo() <span class="hljs-comment">// 3</span>\n<span class="hljs-symbol">x</span> <span class="hljs-comment">// 1</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570<code>foo</code>\u7684\u53c2\u6570\u5f62\u6210\u4e00\u4e2a\u5355\u72ec\u4f5c\u7528\u57df\u3002\u8fd9\u4e2a\u4f5c\u7528\u57df\u91cc\u9762\uff0c\u9996\u5148\u58f0\u660e\u4e86\u53d8\u91cf<code>x</code>\uff0c\u7136\u540e\u58f0\u660e\u4e86\u53d8\u91cf<code>y</code>\uff0c<code>y</code>\u7684\u9ed8\u8ba4\u503c\u662f\u4e00\u4e2a\u533f\u540d\u51fd\u6570\u3002\u8fd9\u4e2a\u533f\u540d\u51fd\u6570\u5185\u90e8\u7684\u53d8\u91cf<code>x</code>\uff0c\u6307\u5411\u540c\u4e00\u4e2a\u4f5c\u7528\u57df\u7684\u7b2c\u4e00\u4e2a\u53c2\u6570<code>x</code>\u3002\u51fd\u6570<code>foo</code>\u5185\u90e8\u53c8\u58f0\u660e\u4e86\u4e00\u4e2a\u5185\u90e8\u53d8\u91cf<code>x</code>\uff0c\u8be5\u53d8\u91cf\u4e0e\u7b2c\u4e00\u4e2a\u53c2\u6570<code>x</code>\u7531\u4e8e\u4e0d\u662f\u540c\u4e00\u4e2a\u4f5c\u7528\u57df\uff0c\u6240\u4ee5\u4e0d\u662f\u540c\u4e00\u4e2a\u53d8\u91cf\uff0c\u56e0\u6b64\u6267\u884c<code>y</code>\u540e\uff0c\u5185\u90e8\u53d8\u91cf<code>x</code>\u548c\u5916\u90e8\u5168\u5c40\u53d8\u91cf<code>x</code>\u7684\u503c\u90fd\u6ca1\u53d8\u3002</p>\n<p>\u5982\u679c\u5c06<code>var x = 3</code>\u7684<code>var</code>\u53bb\u9664\uff0c\u51fd\u6570<code>foo</code>\u7684\u5185\u90e8\u53d8\u91cf<code>x</code>\u5c31\u6307\u5411\u7b2c\u4e00\u4e2a\u53c2\u6570<code>x</code>\uff0c\u4e0e\u533f\u540d\u51fd\u6570\u5185\u90e8\u7684<code>x</code>\u662f\u4e00\u81f4\u7684\uff0c\u6240\u4ee5\u6700\u540e\u8f93\u51fa\u7684\u5c31\u662f<code>2</code>\uff0c\u800c\u5916\u5c42\u7684\u5168\u5c40\u53d8\u91cf<code>x</code>\u4f9d\u7136\u4e0d\u53d7\u5f71\u54cd\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keywords">var</span> <span class="hljs-symbol">x</span> = <span class="hljs-number">1</span>;\nfunction foo(<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span> = function() { <span class="hljs-symbol">x</span> = <span class="hljs-number">2</span>; }) {\n  <span class="hljs-symbol">x</span> = <span class="hljs-number">3</span>;\n  <span class="hljs-symbol">y</span>();\n  console.log(<span class="hljs-symbol">x</span>);\n}\n\nfoo() <span class="hljs-comment">// 2</span>\n<span class="hljs-symbol">x</span> <span class="hljs-comment">// 1</span></code></pre>\n<h3 id="\u5e94\u7528">\u5e94\u7528</h3>\n<p>\u5229\u7528\u53c2\u6570\u9ed8\u8ba4\u503c\uff0c\u53ef\u4ee5\u6307\u5b9a\u67d0\u4e00\u4e2a\u53c2\u6570\u4e0d\u5f97\u7701\u7565\uff0c\u5982\u679c\u7701\u7565\u5c31\u629b\u51fa\u4e00\u4e2a\u9519\u8bef\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">throwIfMissing</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">throw</span> <span class="hljs-keyword">new</span> <span class="hljs-built_in">Error</span>(<span class="hljs-string">\'Missing parameter\'</span>);\n}\n\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span>(<span class="hljs-params">mustBeProvided = throwIfMissing(</span>)) </span>{\n  <span class="hljs-keyword">return</span> mustBeProvided;\n}\n\nfoo()\n<span class="hljs-comment">// Error: Missing parameter</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684<code>foo</code>\u51fd\u6570\uff0c\u5982\u679c\u8c03\u7528\u7684\u65f6\u5019\u6ca1\u6709\u53c2\u6570\uff0c\u5c31\u4f1a\u8c03\u7528\u9ed8\u8ba4\u503c<code>throwIfMissing</code>\u51fd\u6570\uff0c\u4ece\u800c\u629b\u51fa\u4e00\u4e2a\u9519\u8bef\u3002</p>\n<p>\u4ece\u4e0a\u9762\u4ee3\u7801\u8fd8\u53ef\u4ee5\u770b\u5230\uff0c\u53c2\u6570<code>mustBeProvided</code>\u7684\u9ed8\u8ba4\u503c\u7b49\u4e8e<code>throwIfMissing</code>\u51fd\u6570\u7684\u8fd0\u884c\u7ed3\u679c\uff08\u6ce8\u610f\u51fd\u6570\u540d<code>throwIfMissing</code>\u4e4b\u540e\u6709\u4e00\u5bf9\u5706\u62ec\u53f7\uff09\uff0c\u8fd9\u8868\u660e\u53c2\u6570\u7684\u9ed8\u8ba4\u503c\u4e0d\u662f\u5728\u5b9a\u4e49\u65f6\u6267\u884c\uff0c\u800c\u662f\u5728\u8fd0\u884c\u65f6\u6267\u884c\u3002\u5982\u679c\u53c2\u6570\u5df2\u7ecf\u8d4b\u503c\uff0c\u9ed8\u8ba4\u503c\u4e2d\u7684\u51fd\u6570\u5c31\u4e0d\u4f1a\u8fd0\u884c\u3002</p>\n<p>\u53e6\u5916\uff0c\u53ef\u4ee5\u5c06\u53c2\u6570\u9ed8\u8ba4\u503c\u8bbe\u4e3a<code>undefined</code>\uff0c\u8868\u660e\u8fd9\u4e2a\u53c2\u6570\u662f\u53ef\u4ee5\u7701\u7565\u7684\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span><span class="hljs-params">(optional = <span class="hljs-literal">undef</span>ined)</span> {</span> \xb7\xb7\xb7 }</code></pre>\n<h2 id="rest-\u53c2\u6570">rest \u53c2\u6570</h2>\n<p>ES6 \u5f15\u5165 rest \u53c2\u6570\uff08\u5f62\u5f0f\u4e3a<code>...\u53d8\u91cf\u540d</code>\uff09\uff0c\u7528\u4e8e\u83b7\u53d6\u51fd\u6570\u7684\u591a\u4f59\u53c2\u6570\uff0c\u8fd9\u6837\u5c31\u4e0d\u9700\u8981\u4f7f\u7528<code>arguments</code>\u5bf9\u8c61\u4e86\u3002rest \u53c2\u6570\u642d\u914d\u7684\u53d8\u91cf\u662f\u4e00\u4e2a\u6570\u7ec4\uff0c\u8be5\u53d8\u91cf\u5c06\u591a\u4f59\u7684\u53c2\u6570\u653e\u5165\u6570\u7ec4\u4e2d\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">add</span><span class="hljs-params">(<span class="hljs-rest_arg">...values</span>)</span> </span>{\n  let sum = <span class="hljs-number">0</span>;\n\n  <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> val of values) {\n    sum += val;\n  }\n\n  <span class="hljs-keyword">return</span> sum;\n}\n\nadd(<span class="hljs-number">2</span>, <span class="hljs-number">5</span>, <span class="hljs-number">3</span>) <span class="hljs-comment">// 10</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684<code>add</code>\u51fd\u6570\u662f\u4e00\u4e2a\u6c42\u548c\u51fd\u6570\uff0c\u5229\u7528 rest \u53c2\u6570\uff0c\u53ef\u4ee5\u5411\u8be5\u51fd\u6570\u4f20\u5165\u4efb\u610f\u6570\u76ee\u7684\u53c2\u6570\u3002</p>\n<p>\u4e0b\u9762\u662f\u4e00\u4e2a rest \u53c2\u6570\u4ee3\u66ff<code>arguments</code>\u53d8\u91cf\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// arguments\u53d8\u91cf\u7684\u5199\u6cd5</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">sortNumbers</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">return</span> <span class="hljs-built_in">Array</span>.prototype.slice.call(<span class="hljs-built_in">arguments</span>).sort();\n}\n\n<span class="hljs-comment">// rest\u53c2\u6570\u7684\u5199\u6cd5</span>\n<span class="hljs-keyword">const</span> sortNumbers = <span class="hljs-function">(<span class="hljs-params">...numbers</span>) =&gt;</span> numbers.sort();</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684\u4e24\u79cd\u5199\u6cd5\uff0c\u6bd4\u8f83\u540e\u53ef\u4ee5\u53d1\u73b0\uff0crest \u53c2\u6570\u7684\u5199\u6cd5\u66f4\u81ea\u7136\u4e5f\u66f4\u7b80\u6d01\u3002</p>\n<p><code>arguments</code>\u5bf9\u8c61\u4e0d\u662f\u6570\u7ec4\uff0c\u800c\u662f\u4e00\u4e2a\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61\u3002\u6240\u4ee5\u4e3a\u4e86\u4f7f\u7528\u6570\u7ec4\u7684\u65b9\u6cd5\uff0c\u5fc5\u987b\u4f7f\u7528<code>Array.prototype.slice.call</code>\u5148\u5c06\u5176\u8f6c\u4e3a\u6570\u7ec4\u3002rest \u53c2\u6570\u5c31\u4e0d\u5b58\u5728\u8fd9\u4e2a\u95ee\u9898\uff0c\u5b83\u5c31\u662f\u4e00\u4e2a\u771f\u6b63\u7684\u6570\u7ec4\uff0c\u6570\u7ec4\u7279\u6709\u7684\u65b9\u6cd5\u90fd\u53ef\u4ee5\u4f7f\u7528\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u5229\u7528 rest \u53c2\u6570\u6539\u5199\u6570\u7ec4<code>push</code>\u65b9\u6cd5\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">push</span><span class="hljs-params">(array, <span class="hljs-rest_arg">...items</span>)</span> </span>{\n  items.forEach(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(item)</span> </span>{\n    array.push(item);\n    console.log(item);\n  });\n}\n\n<span class="hljs-keyword">var</span> a = [];\npush(a, <span class="hljs-number">1</span>, <span class="hljs-number">2</span>, <span class="hljs-number">3</span>)</code></pre>\n<p>\u6ce8\u610f\uff0crest \u53c2\u6570\u4e4b\u540e\u4e0d\u80fd\u518d\u6709\u5176\u4ed6\u53c2\u6570\uff08\u5373\u53ea\u80fd\u662f\u6700\u540e\u4e00\u4e2a\u53c2\u6570\uff09\uff0c\u5426\u5219\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u62a5\u9519</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span><span class="hljs-params">(a, <span class="hljs-rest_arg">...b</span>, c)</span> </span>{\n  <span class="hljs-comment">// ...</span>\n}</code></pre>\n<p>\u51fd\u6570\u7684<code>length</code>\u5c5e\u6027\uff0c\u4e0d\u5305\u62ec rest \u53c2\u6570\u3002</p>\n<pre><code class="language-javascript">(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(a)</span> </span>{}).length  <span class="hljs-comment">// 1</span>\n(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(<span class="hljs-rest_arg">...a</span>)</span> </span>{}).length  <span class="hljs-comment">// 0</span>\n(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(a, <span class="hljs-rest_arg">...b</span>)</span> </span>{}).length  <span class="hljs-comment">// 1</span></code></pre>\n<h2 id="\u4e25\u683c\u6a21\u5f0f">\u4e25\u683c\u6a21\u5f0f</h2>\n<p>\u4ece ES5 \u5f00\u59cb\uff0c\u51fd\u6570\u5185\u90e8\u53ef\u4ee5\u8bbe\u5b9a\u4e3a\u4e25\u683c\u6a21\u5f0f\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">doSomething</span>(<span class="hljs-params">a, b</span>) </span>{\n<span class="hljs-meta">  \'use strict\'</span>;\n  <span class="hljs-comment">// code</span>\n}</code></pre>\n<p>ES2016 \u505a\u4e86\u4e00\u70b9\u4fee\u6539\uff0c\u89c4\u5b9a\u53ea\u8981\u51fd\u6570\u53c2\u6570\u4f7f\u7528\u4e86\u9ed8\u8ba4\u503c\u3001\u89e3\u6784\u8d4b\u503c\u3001\u6216\u8005\u6269\u5c55\u8fd0\u7b97\u7b26\uff0c\u90a3\u4e48\u51fd\u6570\u5185\u90e8\u5c31\u4e0d\u80fd\u663e\u5f0f\u8bbe\u5b9a\u4e3a\u4e25\u683c\u6a21\u5f0f\uff0c\u5426\u5219\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u62a5\u9519</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">doSomething</span>(<span class="hljs-params">a, b = a</span>) </span>{\n<span class="hljs-meta">  \'use strict\'</span>;\n  <span class="hljs-comment">// code</span>\n}\n\n<span class="hljs-comment">// \u62a5\u9519</span>\n<span class="hljs-keyword">const</span> doSomething = <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params">{a, b}</span>) </span>{\n<span class="hljs-meta">  \'use strict\'</span>;\n  <span class="hljs-comment">// code</span>\n};\n\n<span class="hljs-comment">// \u62a5\u9519</span>\n<span class="hljs-keyword">const</span> doSomething = <span class="hljs-function">(<span class="hljs-params">...a</span>) =&gt;</span> {\n  <span class="hljs-string">\'use strict\'</span>;\n  <span class="hljs-comment">// code</span>\n};\n\n<span class="hljs-keyword">const</span> obj = {\n  <span class="hljs-comment">// \u62a5\u9519</span>\n  doSomething({a, b}) {\n    <span class="hljs-string">\'use strict\'</span>;\n    <span class="hljs-comment">// code</span>\n  }\n};</code></pre>\n<p>\u8fd9\u6837\u89c4\u5b9a\u7684\u539f\u56e0\u662f\uff0c\u51fd\u6570\u5185\u90e8\u7684\u4e25\u683c\u6a21\u5f0f\uff0c\u540c\u65f6\u9002\u7528\u4e8e\u51fd\u6570\u4f53\u548c\u51fd\u6570\u53c2\u6570\u3002\u4f46\u662f\uff0c\u51fd\u6570\u6267\u884c\u7684\u65f6\u5019\uff0c\u5148\u6267\u884c\u51fd\u6570\u53c2\u6570\uff0c\u7136\u540e\u518d\u6267\u884c\u51fd\u6570\u4f53\u3002\u8fd9\u6837\u5c31\u6709\u4e00\u4e2a\u4e0d\u5408\u7406\u7684\u5730\u65b9\uff0c\u53ea\u6709\u4ece\u51fd\u6570\u4f53\u4e4b\u4e2d\uff0c\u624d\u80fd\u77e5\u9053\u53c2\u6570\u662f\u5426\u5e94\u8be5\u4ee5\u4e25\u683c\u6a21\u5f0f\u6267\u884c\uff0c\u4f46\u662f\u53c2\u6570\u5374\u5e94\u8be5\u5148\u4e8e\u51fd\u6570\u4f53\u6267\u884c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u62a5\u9519</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">doSomething</span>(<span class="hljs-params">value = <span class="hljs-number">070</span></span>) </span>{\n<span class="hljs-meta">  \'use strict\'</span>;\n  <span class="hljs-keyword">return</span> value;\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53c2\u6570<code>value</code>\u7684\u9ed8\u8ba4\u503c\u662f\u516b\u8fdb\u5236\u6570<code>070</code>\uff0c\u4f46\u662f\u4e25\u683c\u6a21\u5f0f\u4e0b\u4e0d\u80fd\u7528\u524d\u7f00<code>0</code>\u8868\u793a\u516b\u8fdb\u5236\uff0c\u6240\u4ee5\u5e94\u8be5\u62a5\u9519\u3002\u4f46\u662f\u5b9e\u9645\u4e0a\uff0cJavaScript \u5f15\u64ce\u4f1a\u5148\u6210\u529f\u6267\u884c<code>value = 070</code>\uff0c\u7136\u540e\u8fdb\u5165\u51fd\u6570\u4f53\u5185\u90e8\uff0c\u53d1\u73b0\u9700\u8981\u7528\u4e25\u683c\u6a21\u5f0f\u6267\u884c\uff0c\u8fd9\u65f6\u624d\u4f1a\u62a5\u9519\u3002</p>\n<p>\u867d\u7136\u53ef\u4ee5\u5148\u89e3\u6790\u51fd\u6570\u4f53\u4ee3\u7801\uff0c\u518d\u6267\u884c\u53c2\u6570\u4ee3\u7801\uff0c\u4f46\u662f\u8fd9\u6837\u65e0\u7591\u5c31\u589e\u52a0\u4e86\u590d\u6742\u6027\u3002\u56e0\u6b64\uff0c\u6807\u51c6\u7d22\u6027\u7981\u6b62\u4e86\u8fd9\u79cd\u7528\u6cd5\uff0c\u53ea\u8981\u53c2\u6570\u4f7f\u7528\u4e86\u9ed8\u8ba4\u503c\u3001\u89e3\u6784\u8d4b\u503c\u3001\u6216\u8005\u6269\u5c55\u8fd0\u7b97\u7b26\uff0c\u5c31\u4e0d\u80fd\u663e\u5f0f\u6307\u5b9a\u4e25\u683c\u6a21\u5f0f\u3002</p>\n<p>\u4e24\u79cd\u65b9\u6cd5\u53ef\u4ee5\u89c4\u907f\u8fd9\u79cd\u9650\u5236\u3002\u7b2c\u4e00\u79cd\u662f\u8bbe\u5b9a\u5168\u5c40\u6027\u7684\u4e25\u683c\u6a21\u5f0f\uff0c\u8fd9\u662f\u5408\u6cd5\u7684\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-meta">\'use strict\'</span>;\n\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">doSomething</span>(<span class="hljs-params">a, b = a</span>) </span>{\n  <span class="hljs-comment">// code</span>\n}</code></pre>\n<p>\u7b2c\u4e8c\u79cd\u662f\u628a\u51fd\u6570\u5305\u5728\u4e00\u4e2a\u65e0\u53c2\u6570\u7684\u7acb\u5373\u6267\u884c\u51fd\u6570\u91cc\u9762\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> doSomething = (<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n<span class="hljs-meta">  \'use strict\'</span>;\n  <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params">value = <span class="hljs-number">42</span></span>) </span>{\n    <span class="hljs-keyword">return</span> value;\n  };\n}());</code></pre>\n<h2 id="name-\u5c5e\u6027">name \u5c5e\u6027</h2>\n<p>\u51fd\u6570\u7684<code>name</code>\u5c5e\u6027\uff0c\u8fd4\u56de\u8be5\u51fd\u6570\u7684\u51fd\u6570\u540d\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span><span class="hljs-params">()</span> </span>{}\nfoo.name <span class="hljs-comment">// "foo"</span></code></pre>\n<p>\u8fd9\u4e2a\u5c5e\u6027\u65e9\u5c31\u88ab\u6d4f\u89c8\u5668\u5e7f\u6cdb\u652f\u6301\uff0c\u4f46\u662f\u76f4\u5230 ES6\uff0c\u624d\u5c06\u5176\u5199\u5165\u4e86\u6807\u51c6\u3002</p>\n<p>\u9700\u8981\u6ce8\u610f\u7684\u662f\uff0cES6 \u5bf9\u8fd9\u4e2a\u5c5e\u6027\u7684\u884c\u4e3a\u505a\u51fa\u4e86\u4e00\u4e9b\u4fee\u6539\u3002\u5982\u679c\u5c06\u4e00\u4e2a\u533f\u540d\u51fd\u6570\u8d4b\u503c\u7ed9\u4e00\u4e2a\u53d8\u91cf\uff0cES5 \u7684<code>name</code>\u5c5e\u6027\uff0c\u4f1a\u8fd4\u56de\u7a7a\u5b57\u7b26\u4e32\uff0c\u800c ES6 \u7684<code>name</code>\u5c5e\u6027\u4f1a\u8fd4\u56de\u5b9e\u9645\u7684\u51fd\u6570\u540d\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> f = <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">()</span> <span class="hljs-comment">{}</span>;</span>\n\n<span class="hljs-comment">// ES5</span>\nf.<span class="hljs-keyword">name</span> <span class="hljs-comment">// ""</span>\n\n<span class="hljs-comment">// ES6</span>\nf.<span class="hljs-keyword">name</span> <span class="hljs-comment">// "f"</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>f</code>\u7b49\u4e8e\u4e00\u4e2a\u533f\u540d\u51fd\u6570\uff0cES5 \u548c ES6 \u7684<code>name</code>\u5c5e\u6027\u8fd4\u56de\u7684\u503c\u4e0d\u4e00\u6837\u3002</p>\n<p>\u5982\u679c\u5c06\u4e00\u4e2a\u5177\u540d\u51fd\u6570\u8d4b\u503c\u7ed9\u4e00\u4e2a\u53d8\u91cf\uff0c\u5219 ES5 \u548c ES6 \u7684<code>name</code>\u5c5e\u6027\u90fd\u8fd4\u56de\u8fd9\u4e2a\u5177\u540d\u51fd\u6570\u539f\u672c\u7684\u540d\u5b57\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> bar = <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">baz</span><span class="hljs-params">()</span> <span class="hljs-comment">{}</span>;</span>\n\n<span class="hljs-comment">// ES5</span>\nbar.<span class="hljs-keyword">name</span> <span class="hljs-comment">// "baz"</span>\n\n<span class="hljs-comment">// ES6</span>\nbar.<span class="hljs-keyword">name</span> <span class="hljs-comment">// "baz"</span></code></pre>\n<p><code>Function</code>\u6784\u9020\u51fd\u6570\u8fd4\u56de\u7684\u51fd\u6570\u5b9e\u4f8b\uff0c<code>name</code>\u5c5e\u6027\u7684\u503c\u4e3a<code>anonymous</code>\u3002</p>\n<pre><code class="language-javascript">(<span class="hljs-built_in">new</span> <span class="hljs-keyword">Function</span>).name // "anonymous"</code></pre>\n<p><code>bind</code>\u8fd4\u56de\u7684\u51fd\u6570\uff0c<code>name</code>\u5c5e\u6027\u503c\u4f1a\u52a0\u4e0a<code>bound</code>\u524d\u7f00\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span><span class="hljs-params">()</span> <span class="hljs-comment">{}</span>;</span>\nfoo.bind(<span class="hljs-comment">{}</span>).<span class="hljs-keyword">name</span> <span class="hljs-comment">// "bound foo"</span>\n\n(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">()</span><span class="hljs-comment">{}</span>).<span class="hljs-title">bind</span><span class="hljs-params">(<span class="hljs-comment">{}</span>)</span>.<span class="hljs-title">name</span> <span class="hljs-comment">// "bound "</span></span></code></pre>\n<h2 id="\u7bad\u5934\u51fd\u6570">\u7bad\u5934\u51fd\u6570</h2>\n<h3 id="\u57fa\u672c\u7528\u6cd5-1">\u57fa\u672c\u7528\u6cd5</h3>\n<p>ES6 \u5141\u8bb8\u4f7f\u7528\u201c\u7bad\u5934\u201d\uff08<code>=&gt;</code>\uff09\u5b9a\u4e49\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> f = <span class="hljs-function"><span class="hljs-params">v</span> =&gt;</span> v;</code></pre>\n<p>\u4e0a\u9762\u7684\u7bad\u5934\u51fd\u6570\u7b49\u540c\u4e8e\uff1a</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> f = <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(v)</span> </span>{\n  <span class="hljs-keyword">return</span> v;\n};</code></pre>\n<p>\u5982\u679c\u7bad\u5934\u51fd\u6570\u4e0d\u9700\u8981\u53c2\u6570\u6216\u9700\u8981\u591a\u4e2a\u53c2\u6570\uff0c\u5c31\u4f7f\u7528\u4e00\u4e2a\u5706\u62ec\u53f7\u4ee3\u8868\u53c2\u6570\u90e8\u5206\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> f = <span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> <span class="hljs-number">5</span>;\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-keyword">var</span> f = <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{ <span class="hljs-keyword">return</span> <span class="hljs-number">5</span> };\n\n<span class="hljs-keyword">var</span> sum = <span class="hljs-function">(<span class="hljs-params">num1, num2</span>) =&gt;</span> num1 + num2;\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-keyword">var</span> sum = <span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params">num1, num2</span>) </span>{\n  <span class="hljs-keyword">return</span> num1 + num2;\n};</code></pre>\n<p>\u5982\u679c\u7bad\u5934\u51fd\u6570\u7684\u4ee3\u7801\u5757\u90e8\u5206\u591a\u4e8e\u4e00\u6761\u8bed\u53e5\uff0c\u5c31\u8981\u4f7f\u7528\u5927\u62ec\u53f7\u5c06\u5b83\u4eec\u62ec\u8d77\u6765\uff0c\u5e76\u4e14\u4f7f\u7528<code>return</code>\u8bed\u53e5\u8fd4\u56de\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> sum = <span class="hljs-function">(<span class="hljs-params">num1, num2</span>) =&gt;</span> { <span class="hljs-keyword">return</span> num1 + num2; }</code></pre>\n<p>\u7531\u4e8e\u5927\u62ec\u53f7\u88ab\u89e3\u91ca\u4e3a\u4ee3\u7801\u5757\uff0c\u6240\u4ee5\u5982\u679c\u7bad\u5934\u51fd\u6570\u76f4\u63a5\u8fd4\u56de\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5fc5\u987b\u5728\u5bf9\u8c61\u5916\u9762\u52a0\u4e0a\u62ec\u53f7\uff0c\u5426\u5219\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript">// \u62a5\u9519\n<span class="hljs-keyword">let</span> getTempItem =<span class="hljs-built_in"> id</span> =&gt; {<span class="hljs-built_in"> id</span>:<span class="hljs-built_in"> id</span>,<span class="hljs-built_in"> name</span>: <span class="hljs-string">"Temp"</span> };\n\n// \u4e0d\u62a5\u9519\n<span class="hljs-keyword">let</span> getTempItem =<span class="hljs-built_in"> id</span> =&gt; ({<span class="hljs-built_in"> id</span>:<span class="hljs-built_in"> id</span>,<span class="hljs-built_in"> name</span>: <span class="hljs-string">"Temp"</span> });</code></pre>\n<p>\u5982\u679c\u7bad\u5934\u51fd\u6570\u53ea\u6709\u4e00\u884c\u8bed\u53e5\uff0c\u4e14\u4e0d\u9700\u8981\u8fd4\u56de\u503c\uff0c\u53ef\u4ee5\u91c7\u7528\u4e0b\u9762\u7684\u5199\u6cd5\uff0c\u5c31\u4e0d\u7528\u5199\u5927\u62ec\u53f7\u4e86\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">let</span> fn = <span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> <span class="hljs-keyword">void</span> doesNotReturn();</code></pre>\n<p>\u7bad\u5934\u51fd\u6570\u53ef\u4ee5\u4e0e\u53d8\u91cf\u89e3\u6784\u7ed3\u5408\u4f7f\u7528\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> full = <span class="hljs-function">(<span class="hljs-params">{ first, last }</span>) =&gt;</span> first + <span class="hljs-string">\' \'</span> + last;\n\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">full</span>(<span class="hljs-params">person</span>) </span>{\n  <span class="hljs-keyword">return</span> person.first + <span class="hljs-string">\' \'</span> + person.last;\n}</code></pre>\n<p>\u7bad\u5934\u51fd\u6570\u4f7f\u5f97\u8868\u8fbe\u66f4\u52a0\u7b80\u6d01\u3002</p>\n<pre><code class="language-javascript">const <span class="hljs-built_in">isEven</span> = <span class="hljs-built_in">n</span> =&gt; <span class="hljs-built_in">n</span> % <span class="hljs-number">2</span> == <span class="hljs-number">0</span>;\nconst square = <span class="hljs-built_in">n</span> =&gt; <span class="hljs-built_in">n</span> * <span class="hljs-built_in">n</span>;</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u53ea\u7528\u4e86\u4e24\u884c\uff0c\u5c31\u5b9a\u4e49\u4e86\u4e24\u4e2a\u7b80\u5355\u7684\u5de5\u5177\u51fd\u6570\u3002\u5982\u679c\u4e0d\u7528\u7bad\u5934\u51fd\u6570\uff0c\u53ef\u80fd\u5c31\u8981\u5360\u7528\u591a\u884c\uff0c\u800c\u4e14\u8fd8\u4e0d\u5982\u73b0\u5728\u8fd9\u6837\u5199\u9192\u76ee\u3002</p>\n<p>\u7bad\u5934\u51fd\u6570\u7684\u4e00\u4e2a\u7528\u5904\u662f\u7b80\u5316\u56de\u8c03\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u6b63\u5e38\u51fd\u6570\u5199\u6cd5</span>\n[<span class="hljs-number">1</span>,<span class="hljs-number">2</span>,<span class="hljs-number">3</span>].map(function (<span class="hljs-symbol">x</span>) {\n  <span class="hljs-keywords">return</span> <span class="hljs-symbol">x</span> * <span class="hljs-symbol">x</span>;\n});\n\n<span class="hljs-comment">// \u7bad\u5934\u51fd\u6570\u5199\u6cd5</span>\n[<span class="hljs-number">1</span>,<span class="hljs-number">2</span>,<span class="hljs-number">3</span>].map(<span class="hljs-symbol">x</span> =&gt; <span class="hljs-symbol">x</span> * <span class="hljs-symbol">x</span>);</code></pre>\n<p>\u53e6\u4e00\u4e2a\u4f8b\u5b50\u662f</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u6b63\u5e38\u51fd\u6570\u5199\u6cd5</span>\n<span class="hljs-keyword">var</span> result = values.sort(<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params">a, b</span>) </span>{\n  <span class="hljs-keyword">return</span> a - b;\n});\n\n<span class="hljs-comment">// \u7bad\u5934\u51fd\u6570\u5199\u6cd5</span>\n<span class="hljs-keyword">var</span> result = values.sort(<span class="hljs-function">(<span class="hljs-params">a, b</span>) =&gt;</span> a - b);</code></pre>\n<p>\u4e0b\u9762\u662f rest \u53c2\u6570\u4e0e\u7bad\u5934\u51fd\u6570\u7ed3\u5408\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> numbers = (...nums) =&gt; nums;\n\nnumbers(<span class="hljs-number">1</span>, <span class="hljs-number">2</span>, <span class="hljs-number">3</span>, <span class="hljs-number">4</span>, <span class="hljs-number">5</span>)\n<span class="hljs-comment">// [1,2,3,4,5]</span>\n\n<span class="hljs-keyword">const</span> headAndTail = (head, ...tail) =&gt; [head, tail];\n\nheadAndTail(<span class="hljs-number">1</span>, <span class="hljs-number">2</span>, <span class="hljs-number">3</span>, <span class="hljs-number">4</span>, <span class="hljs-number">5</span>)\n<span class="hljs-comment">// [1,[2,3,4,5]]</span></code></pre>\n<h3 id="\u4f7f\u7528\u6ce8\u610f\u70b9">\u4f7f\u7528\u6ce8\u610f\u70b9</h3>\n<p>\u7bad\u5934\u51fd\u6570\u6709\u51e0\u4e2a\u4f7f\u7528\u6ce8\u610f\u70b9\u3002</p>\n<p>\uff081\uff09\u51fd\u6570\u4f53\u5185\u7684<code>this</code>\u5bf9\u8c61\uff0c\u5c31\u662f\u5b9a\u4e49\u65f6\u6240\u5728\u7684\u5bf9\u8c61\uff0c\u800c\u4e0d\u662f\u4f7f\u7528\u65f6\u6240\u5728\u7684\u5bf9\u8c61\u3002</p>\n<p>\uff082\uff09\u4e0d\u53ef\u4ee5\u5f53\u4f5c\u6784\u9020\u51fd\u6570\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4e0d\u53ef\u4ee5\u4f7f\u7528<code>new</code>\u547d\u4ee4\uff0c\u5426\u5219\u4f1a\u629b\u51fa\u4e00\u4e2a\u9519\u8bef\u3002</p>\n<p>\uff083\uff09\u4e0d\u53ef\u4ee5\u4f7f\u7528<code>arguments</code>\u5bf9\u8c61\uff0c\u8be5\u5bf9\u8c61\u5728\u51fd\u6570\u4f53\u5185\u4e0d\u5b58\u5728\u3002\u5982\u679c\u8981\u7528\uff0c\u53ef\u4ee5\u7528 rest \u53c2\u6570\u4ee3\u66ff\u3002</p>\n<p>\uff084\uff09\u4e0d\u53ef\u4ee5\u4f7f\u7528<code>yield</code>\u547d\u4ee4\uff0c\u56e0\u6b64\u7bad\u5934\u51fd\u6570\u4e0d\u80fd\u7528\u4f5c Generator \u51fd\u6570\u3002</p>\n<p>\u4e0a\u9762\u56db\u70b9\u4e2d\uff0c\u7b2c\u4e00\u70b9\u5c24\u5176\u503c\u5f97\u6ce8\u610f\u3002<code>this</code>\u5bf9\u8c61\u7684\u6307\u5411\u662f\u53ef\u53d8\u7684\uff0c\u4f46\u662f\u5728\u7bad\u5934\u51fd\u6570\u4e2d\uff0c\u5b83\u662f\u56fa\u5b9a\u7684\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span>(<span class="hljs-params"></span>) </span>{\n  setTimeout(<span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> {\n    <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'id:\'</span>, <span class="hljs-keyword">this</span>.id);\n  }, <span class="hljs-number">100</span>);\n}\n\n<span class="hljs-keyword">var</span> id = <span class="hljs-number">21</span>;\n\nfoo.call({ <span class="hljs-attr">id</span>: <span class="hljs-number">42</span> });\n<span class="hljs-comment">// id: 42</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>setTimeout</code>\u7684\u53c2\u6570\u662f\u4e00\u4e2a\u7bad\u5934\u51fd\u6570\uff0c\u8fd9\u4e2a\u7bad\u5934\u51fd\u6570\u7684\u5b9a\u4e49\u751f\u6548\u662f\u5728<code>foo</code>\u51fd\u6570\u751f\u6210\u65f6\uff0c\u800c\u5b83\u7684\u771f\u6b63\u6267\u884c\u8981\u7b49\u5230 100 \u6beb\u79d2\u540e\u3002\u5982\u679c\u662f\u666e\u901a\u51fd\u6570\uff0c\u6267\u884c\u65f6<code>this</code>\u5e94\u8be5\u6307\u5411\u5168\u5c40\u5bf9\u8c61<code>window</code>\uff0c\u8fd9\u65f6\u5e94\u8be5\u8f93\u51fa<code>21</code>\u3002\u4f46\u662f\uff0c\u7bad\u5934\u51fd\u6570\u5bfc\u81f4<code>this</code>\u603b\u662f\u6307\u5411\u51fd\u6570\u5b9a\u4e49\u751f\u6548\u65f6\u6240\u5728\u7684\u5bf9\u8c61\uff08\u672c\u4f8b\u662f<code>{id: 42}</code>\uff09\uff0c\u6240\u4ee5\u8f93\u51fa\u7684\u662f<code>42</code>\u3002</p>\n<p>\u7bad\u5934\u51fd\u6570\u53ef\u4ee5\u8ba9<code>setTimeout</code>\u91cc\u9762\u7684<code>this</code>\uff0c\u7ed1\u5b9a\u5b9a\u4e49\u65f6\u6240\u5728\u7684\u4f5c\u7528\u57df\uff0c\u800c\u4e0d\u662f\u6307\u5411\u8fd0\u884c\u65f6\u6240\u5728\u7684\u4f5c\u7528\u57df\u3002\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">Timer</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">this</span>.s1 = <span class="hljs-number">0</span>;\n  <span class="hljs-keyword">this</span>.s2 = <span class="hljs-number">0</span>;\n  <span class="hljs-comment">// \u7bad\u5934\u51fd\u6570</span>\n  setInterval(<span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> <span class="hljs-keyword">this</span>.s1++, <span class="hljs-number">1000</span>);\n  <span class="hljs-comment">// \u666e\u901a\u51fd\u6570</span>\n  setInterval(<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n    <span class="hljs-keyword">this</span>.s2++;\n  }, <span class="hljs-number">1000</span>);\n}\n\n<span class="hljs-keyword">var</span> timer = <span class="hljs-keyword">new</span> Timer();\n\nsetTimeout(<span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'s1: \'</span>, timer.s1), <span class="hljs-number">3100</span>);\nsetTimeout(<span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'s2: \'</span>, timer.s2), <span class="hljs-number">3100</span>);\n<span class="hljs-comment">// s1: 3</span>\n<span class="hljs-comment">// s2: 0</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Timer</code>\u51fd\u6570\u5185\u90e8\u8bbe\u7f6e\u4e86\u4e24\u4e2a\u5b9a\u65f6\u5668\uff0c\u5206\u522b\u4f7f\u7528\u4e86\u7bad\u5934\u51fd\u6570\u548c\u666e\u901a\u51fd\u6570\u3002\u524d\u8005\u7684<code>this</code>\u7ed1\u5b9a\u5b9a\u4e49\u65f6\u6240\u5728\u7684\u4f5c\u7528\u57df\uff08\u5373<code>Timer</code>\u51fd\u6570\uff09\uff0c\u540e\u8005\u7684<code>this</code>\u6307\u5411\u8fd0\u884c\u65f6\u6240\u5728\u7684\u4f5c\u7528\u57df\uff08\u5373\u5168\u5c40\u5bf9\u8c61\uff09\u3002\u6240\u4ee5\uff0c3100 \u6beb\u79d2\u4e4b\u540e\uff0c<code>timer.s1</code>\u88ab\u66f4\u65b0\u4e86 3 \u6b21\uff0c\u800c<code>timer.s2</code>\u4e00\u6b21\u90fd\u6ca1\u66f4\u65b0\u3002</p>\n<p>\u7bad\u5934\u51fd\u6570\u53ef\u4ee5\u8ba9<code>this</code>\u6307\u5411\u56fa\u5b9a\u5316\uff0c\u8fd9\u79cd\u7279\u6027\u5f88\u6709\u5229\u4e8e\u5c01\u88c5\u56de\u8c03\u51fd\u6570\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u4f8b\u5b50\uff0cDOM \u4e8b\u4ef6\u7684\u56de\u8c03\u51fd\u6570\u5c01\u88c5\u5728\u4e00\u4e2a\u5bf9\u8c61\u91cc\u9762\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> handler = {\n  id: <span class="hljs-string">\'123456\'</span>,\n\n  init: <span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params"></span>) </span>{\n    <span class="hljs-built_in">document</span>.addEventListener(<span class="hljs-string">\'click\'</span>,\n      <span class="hljs-function"><span class="hljs-params">event</span> =&gt;</span> <span class="hljs-keyword">this</span>.doSomething(event.type), <span class="hljs-literal">false</span>);\n  },\n\n  doSomething: <span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params"><span class="hljs-keyword">type</span></span>) </span>{\n    <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'Handling \'</span> + <span class="hljs-keyword">type</span>  + <span class="hljs-string">\' for \'</span> + <span class="hljs-keyword">this</span>.id);\n  }\n};</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684<code>init</code>\u65b9\u6cd5\u4e2d\uff0c\u4f7f\u7528\u4e86\u7bad\u5934\u51fd\u6570\uff0c\u8fd9\u5bfc\u81f4\u8fd9\u4e2a\u7bad\u5934\u51fd\u6570\u91cc\u9762\u7684<code>this</code>\uff0c\u603b\u662f\u6307\u5411<code>handler</code>\u5bf9\u8c61\u3002\u5426\u5219\uff0c\u56de\u8c03\u51fd\u6570\u8fd0\u884c\u65f6\uff0c<code>this.doSomething</code>\u8fd9\u4e00\u884c\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a\u6b64\u65f6<code>this</code>\u6307\u5411<code>document</code>\u5bf9\u8c61\u3002</p>\n<p><code>this</code>\u6307\u5411\u7684\u56fa\u5b9a\u5316\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u7bad\u5934\u51fd\u6570\u5185\u90e8\u6709\u7ed1\u5b9a<code>this</code>\u7684\u673a\u5236\uff0c\u5b9e\u9645\u539f\u56e0\u662f\u7bad\u5934\u51fd\u6570\u6839\u672c\u6ca1\u6709\u81ea\u5df1\u7684<code>this</code>\uff0c\u5bfc\u81f4\u5185\u90e8\u7684<code>this</code>\u5c31\u662f\u5916\u5c42\u4ee3\u7801\u5757\u7684<code>this</code>\u3002\u6b63\u662f\u56e0\u4e3a\u5b83\u6ca1\u6709<code>this</code>\uff0c\u6240\u4ee5\u4e5f\u5c31\u4e0d\u80fd\u7528\u4f5c\u6784\u9020\u51fd\u6570\u3002</p>\n<p>\u6240\u4ee5\uff0c\u7bad\u5934\u51fd\u6570\u8f6c\u6210 ES5 \u7684\u4ee3\u7801\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// ES6</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span>(<span class="hljs-params"></span>) </span>{\n  setTimeout(<span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> {\n    <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'id:\'</span>, <span class="hljs-keyword">this</span>.id);\n  }, <span class="hljs-number">100</span>);\n}\n\n<span class="hljs-comment">// ES5</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">var</span> _this = <span class="hljs-keyword">this</span>;\n\n  setTimeout(<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n    <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'id:\'</span>, _this.id);\n  }, <span class="hljs-number">100</span>);\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u8f6c\u6362\u540e\u7684 ES5 \u7248\u672c\u6e05\u695a\u5730\u8bf4\u660e\u4e86\uff0c\u7bad\u5934\u51fd\u6570\u91cc\u9762\u6839\u672c\u6ca1\u6709\u81ea\u5df1\u7684<code>this</code>\uff0c\u800c\u662f\u5f15\u7528\u5916\u5c42\u7684<code>this</code>\u3002</p>\n<p>\u8bf7\u95ee\u4e0b\u9762\u7684\u4ee3\u7801\u4e4b\u4e2d\u6709\u51e0\u4e2a<code>this</code>\uff1f</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> {\n    <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> {\n      <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> {\n        <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'id:\'</span>, <span class="hljs-keyword">this</span>.id);\n      };\n    };\n  };\n}\n\n<span class="hljs-keyword">var</span> f = foo.call({<span class="hljs-attr">id</span>: <span class="hljs-number">1</span>});\n\n<span class="hljs-keyword">var</span> t1 = f.call({<span class="hljs-attr">id</span>: <span class="hljs-number">2</span>})()(); <span class="hljs-comment">// id: 1</span>\n<span class="hljs-keyword">var</span> t2 = f().call({<span class="hljs-attr">id</span>: <span class="hljs-number">3</span>})(); <span class="hljs-comment">// id: 1</span>\n<span class="hljs-keyword">var</span> t3 = f()().call({<span class="hljs-attr">id</span>: <span class="hljs-number">4</span>}); <span class="hljs-comment">// id: 1</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e4b\u4e2d\uff0c\u53ea\u6709\u4e00\u4e2a<code>this</code>\uff0c\u5c31\u662f\u51fd\u6570<code>foo</code>\u7684<code>this</code>\uff0c\u6240\u4ee5<code>t1</code>\u3001<code>t2</code>\u3001<code>t3</code>\u90fd\u8f93\u51fa\u540c\u6837\u7684\u7ed3\u679c\u3002\u56e0\u4e3a\u6240\u6709\u7684\u5185\u5c42\u51fd\u6570\u90fd\u662f\u7bad\u5934\u51fd\u6570\uff0c\u90fd\u6ca1\u6709\u81ea\u5df1\u7684<code>this</code>\uff0c\u5b83\u4eec\u7684<code>this</code>\u5176\u5b9e\u90fd\u662f\u6700\u5916\u5c42<code>foo</code>\u51fd\u6570\u7684<code>this</code>\u3002</p>\n<p>\u9664\u4e86<code>this</code>\uff0c\u4ee5\u4e0b\u4e09\u4e2a\u53d8\u91cf\u5728\u7bad\u5934\u51fd\u6570\u4e4b\u4e2d\u4e5f\u662f\u4e0d\u5b58\u5728\u7684\uff0c\u6307\u5411\u5916\u5c42\u51fd\u6570\u7684\u5bf9\u5e94\u53d8\u91cf\uff1a<code>arguments</code>\u3001<code>super</code>\u3001<code>new.target</code>\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">foo</span>(<span class="hljs-params"></span>) </span>{\n  setTimeout(<span class="hljs-function"><span class="hljs-params">()</span> =&gt;</span> {\n    <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'args:\'</span>, <span class="hljs-built_in">arguments</span>);\n  }, <span class="hljs-number">100</span>);\n}\n\nfoo(<span class="hljs-number">2</span>, <span class="hljs-number">4</span>, <span class="hljs-number">6</span>, <span class="hljs-number">8</span>)\n<span class="hljs-comment">// args: [2, 4, 6, 8]</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u7bad\u5934\u51fd\u6570\u5185\u90e8\u7684\u53d8\u91cf<code>arguments</code>\uff0c\u5176\u5b9e\u662f\u51fd\u6570<code>foo</code>\u7684<code>arguments</code>\u53d8\u91cf\u3002</p>\n<p>\u53e6\u5916\uff0c\u7531\u4e8e\u7bad\u5934\u51fd\u6570\u6ca1\u6709\u81ea\u5df1\u7684<code>this</code>\uff0c\u6240\u4ee5\u5f53\u7136\u4e5f\u5c31\u4e0d\u80fd\u7528<code>call()</code>\u3001<code>apply()</code>\u3001<code>bind()</code>\u8fd9\u4e9b\u65b9\u6cd5\u53bb\u6539\u53d8<code>this</code>\u7684\u6307\u5411\u3002</p>\n<pre><code class="language-javascript">(<span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">return</span> [\n    <span class="hljs-function">(<span class="hljs-params">(<span class="hljs-params"></span>) =&gt; <span class="hljs-keyword">this</span>.x</span>).<span class="hljs-params">bind</span>(<span class="hljs-params">{ x: \'inner\' }</span>)<span class="hljs-params">()</span>\n  ];\n}).<span class="hljs-params">call</span>(<span class="hljs-params">{ x: \'outer\' }</span>);\n// [\'<span class="hljs-params">outer</span>\']</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u7bad\u5934\u51fd\u6570\u6ca1\u6709\u81ea\u5df1\u7684<code>this</code>\uff0c\u6240\u4ee5<code>bind</code>\u65b9\u6cd5\u65e0\u6548\uff0c\u5185\u90e8\u7684<code>this</code>\u6307\u5411\u5916\u90e8\u7684<code>this</code>\u3002</p>\n<p>\u957f\u671f\u4ee5\u6765\uff0cJavaScript \u8bed\u8a00\u7684<code>this</code>\u5bf9\u8c61\u4e00\u76f4\u662f\u4e00\u4e2a\u4ee4\u4eba\u5934\u75db\u7684\u95ee\u9898\uff0c\u5728\u5bf9\u8c61\u65b9\u6cd5\u4e2d\u4f7f\u7528<code>this</code>\uff0c\u5fc5\u987b\u975e\u5e38\u5c0f\u5fc3\u3002\u7bad\u5934\u51fd\u6570\u201d\u7ed1\u5b9a\u201d<code>this</code>\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u89e3\u51b3\u4e86\u8fd9\u4e2a\u56f0\u6270\u3002</p>\n<h3 id="\u5d4c\u5957\u7684\u7bad\u5934\u51fd\u6570">\u5d4c\u5957\u7684\u7bad\u5934\u51fd\u6570</h3>\n<p>\u7bad\u5934\u51fd\u6570\u5185\u90e8\uff0c\u8fd8\u53ef\u4ee5\u518d\u4f7f\u7528\u7bad\u5934\u51fd\u6570\u3002\u4e0b\u9762\u662f\u4e00\u4e2a ES5 \u8bed\u6cd5\u7684\u591a\u91cd\u5d4c\u5957\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">insert</span><span class="hljs-params">(value)</span> </span>{\n  <span class="hljs-keyword">return</span> {into: <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(array)</span> </span>{\n    <span class="hljs-keyword">return</span> {after: <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(afterValue)</span> </span>{\n      <span class="hljs-keyword">array</span>.splice(<span class="hljs-keyword">array</span>.indexOf(afterValue) + <span class="hljs-number">1</span>, <span class="hljs-number">0</span>, value);\n      <span class="hljs-keyword">return</span> <span class="hljs-keyword">array</span>;\n    }};\n  }};\n}\n\ninsert(<span class="hljs-number">2</span>).into([<span class="hljs-number">1</span>, <span class="hljs-number">3</span>]).after(<span class="hljs-number">1</span>); <span class="hljs-comment">//[1, 2, 3]</span></code></pre>\n<p>\u4e0a\u9762\u8fd9\u4e2a\u51fd\u6570\uff0c\u53ef\u4ee5\u4f7f\u7528\u7bad\u5934\u51fd\u6570\u6539\u5199\u3002</p>\n<pre><code class="language-javascript">let insert = (value) =&gt; ({<span class="hljs-built_in">int</span>o: (<span class="hljs-built_in">array</span>) =&gt; ({after: (afterValue) =&gt; {\n  <span class="hljs-built_in">array</span>.splice(<span class="hljs-built_in">array</span>.indexOf(afterValue) + <span class="hljs-number">1</span>, <span class="hljs-number">0</span>, value);\n  <span class="hljs-keyword">return</span> <span class="hljs-built_in">array</span>;\n}})});\n\ninsert(<span class="hljs-number">2</span>).<span class="hljs-built_in">int</span>o([<span class="hljs-number">1</span>, <span class="hljs-number">3</span>]).after(<span class="hljs-number">1</span>); <span class="hljs-comment">//[1, 2, 3]</span></code></pre>\n<p>\u4e0b\u9762\u662f\u4e00\u4e2a\u90e8\u7f72\u7ba1\u9053\u673a\u5236\uff08pipeline\uff09\u7684\u4f8b\u5b50\uff0c\u5373\u524d\u4e00\u4e2a\u51fd\u6570\u7684\u8f93\u51fa\u662f\u540e\u4e00\u4e2a\u51fd\u6570\u7684\u8f93\u5165\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> pipeline = <span class="hljs-function">(<span class="hljs-params">...funcs</span>) =&gt;</span>\n  val =&gt; funcs.reduce(<span class="hljs-function">(<span class="hljs-params">a, b</span>) =&gt;</span> b(a), val);\n\n<span class="hljs-keyword">const</span> plus1 = <span class="hljs-function"><span class="hljs-params">a</span> =&gt;</span> a + <span class="hljs-number">1</span>;\n<span class="hljs-keyword">const</span> mult2 = <span class="hljs-function"><span class="hljs-params">a</span> =&gt;</span> a * <span class="hljs-number">2</span>;\n<span class="hljs-keyword">const</span> addThenMult = pipeline(plus1, mult2);\n\naddThenMult(<span class="hljs-number">5</span>)\n<span class="hljs-comment">// 12</span></code></pre>\n<p>\u5982\u679c\u89c9\u5f97\u4e0a\u9762\u7684\u5199\u6cd5\u53ef\u8bfb\u6027\u6bd4\u8f83\u5dee\uff0c\u4e5f\u53ef\u4ee5\u91c7\u7528\u4e0b\u9762\u7684\u5199\u6cd5\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> plus1 = <span class="hljs-function"><span class="hljs-params">a</span> =&gt;</span> a + <span class="hljs-number">1</span>;\n<span class="hljs-keyword">const</span> mult2 = <span class="hljs-function"><span class="hljs-params">a</span> =&gt;</span> a * <span class="hljs-number">2</span>;\n\nmult2(plus1(<span class="hljs-number">5</span>))\n<span class="hljs-comment">// 12</span></code></pre>\n<p>\u7bad\u5934\u51fd\u6570\u8fd8\u6709\u4e00\u4e2a\u529f\u80fd\uff0c\u5c31\u662f\u53ef\u4ee5\u5f88\u65b9\u4fbf\u5730\u6539\u5199 \u03bb \u6f14\u7b97\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u03bb\u6f14\u7b97\u7684\u5199\u6cd5</span>\nfix = \u03bbf.(\u03bb<span class="hljs-symbol">x</span>.f(\u03bbv.<span class="hljs-symbol">x</span>(<span class="hljs-symbol">x</span>)(v)))(\u03bb<span class="hljs-symbol">x</span>.f(\u03bbv.<span class="hljs-symbol">x</span>(<span class="hljs-symbol">x</span>)(v)))\n\n<span class="hljs-comment">// ES6\u7684\u5199\u6cd5</span>\n<span class="hljs-keywords">var</span> fix = f =&gt; (<span class="hljs-symbol">x</span> =&gt; f(v =&gt; <span class="hljs-symbol">x</span>(<span class="hljs-symbol">x</span>)(v)))\n               (<span class="hljs-symbol">x</span> =&gt; f(v =&gt; <span class="hljs-symbol">x</span>(<span class="hljs-symbol">x</span>)(v)));</code></pre>\n<p>\u4e0a\u9762\u4e24\u79cd\u5199\u6cd5\uff0c\u51e0\u4e4e\u662f\u4e00\u4e00\u5bf9\u5e94\u7684\u3002\u7531\u4e8e \u03bb \u6f14\u7b97\u5bf9\u4e8e\u8ba1\u7b97\u673a\u79d1\u5b66\u975e\u5e38\u91cd\u8981\uff0c\u8fd9\u4f7f\u5f97\u6211\u4eec\u53ef\u4ee5\u7528 ES6 \u4f5c\u4e3a\u66ff\u4ee3\u5de5\u5177\uff0c\u63a2\u7d22\u8ba1\u7b97\u673a\u79d1\u5b66\u3002</p>\n<h2 id="\u53cc\u5192\u53f7\u8fd0\u7b97\u7b26">\u53cc\u5192\u53f7\u8fd0\u7b97\u7b26</h2>\n<p>\u7bad\u5934\u51fd\u6570\u53ef\u4ee5\u7ed1\u5b9a<code>this</code>\u5bf9\u8c61\uff0c\u5927\u5927\u51cf\u5c11\u4e86\u663e\u5f0f\u7ed1\u5b9a<code>this</code>\u5bf9\u8c61\u7684\u5199\u6cd5\uff08<code>call</code>\u3001<code>apply</code>\u3001<code>bind</code>\uff09\u3002\u4f46\u662f\uff0c\u7bad\u5934\u51fd\u6570\u5e76\u4e0d\u9002\u7528\u4e8e\u6240\u6709\u573a\u5408\uff0c\u6240\u4ee5\u73b0\u5728\u6709\u4e00\u4e2a<a href="https://github.com/zenparsing/es-function-bind">\u63d0\u6848</a>\uff0c\u63d0\u51fa\u4e86\u201c\u51fd\u6570\u7ed1\u5b9a\u201d\uff08function bind\uff09\u8fd0\u7b97\u7b26\uff0c\u7528\u6765\u53d6\u4ee3<code>call</code>\u3001<code>apply</code>\u3001<code>bind</code>\u8c03\u7528\u3002</p>\n<p>\u51fd\u6570\u7ed1\u5b9a\u8fd0\u7b97\u7b26\u662f\u5e76\u6392\u7684\u4e24\u4e2a\u5192\u53f7\uff08<code>::</code>\uff09\uff0c\u53cc\u5192\u53f7\u5de6\u8fb9\u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c\u53f3\u8fb9\u662f\u4e00\u4e2a\u51fd\u6570\u3002\u8be5\u8fd0\u7b97\u7b26\u4f1a\u81ea\u52a8\u5c06\u5de6\u8fb9\u7684\u5bf9\u8c61\uff0c\u4f5c\u4e3a\u4e0a\u4e0b\u6587\u73af\u5883\uff08\u5373<code>this</code>\u5bf9\u8c61\uff09\uff0c\u7ed1\u5b9a\u5230\u53f3\u8fb9\u7684\u51fd\u6570\u4e0a\u9762\u3002</p>\n<pre><code class="language-javascript">foo::bar;\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\nbar.bind(foo);\n\nfoo::bar(...arguments);\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\nbar.apply(foo, arguments);\n\n<span class="hljs-keyword">const</span> hasOwnProperty = Object.prototype.hasOwnProperty;\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">hasOwn</span><span class="hljs-params">(obj, key)</span> </span>{\n  <span class="hljs-keyword">return</span> obj::hasOwnProperty(key);\n}</code></pre>\n<p>\u5982\u679c\u53cc\u5192\u53f7\u5de6\u8fb9\u4e3a\u7a7a\uff0c\u53f3\u8fb9\u662f\u4e00\u4e2a\u5bf9\u8c61\u7684\u65b9\u6cd5\uff0c\u5219\u7b49\u4e8e\u5c06\u8be5\u65b9\u6cd5\u7ed1\u5b9a\u5728\u8be5\u5bf9\u8c61\u4e0a\u9762\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> <span class="hljs-function"><span class="hljs-keyword">method</span> = <span class="hljs-title">obj</span>:</span>:obj.foo;\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-keyword">var</span> <span class="hljs-function"><span class="hljs-keyword">method</span> = :</span>:obj.foo;\n\nlet log = ::console.log;\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-keyword">var</span> log = console.log.bind(console);</code></pre>\n<p>\u53cc\u5192\u53f7\u8fd0\u7b97\u7b26\u7684\u8fd0\u7b97\u7ed3\u679c\uff0c\u8fd8\u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c\u56e0\u6b64\u53ef\u4ee5\u91c7\u7528\u94fe\u5f0f\u5199\u6cd5\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u4f8b\u4e00</span>\nimport { map, takeWhile, forEach } from <span class="hljs-string">"iterlib"</span>;\n\nget<span class="hljs-constructor">Players()</span>\n::map(x =&gt; x.character<span class="hljs-literal">()</span>)\n::take<span class="hljs-constructor">While(<span class="hljs-params">x</span> =&gt; <span class="hljs-params">x</span>.<span class="hljs-params">strength</span> &gt; 100)</span>\n::for<span class="hljs-constructor">Each(<span class="hljs-params">x</span> =&gt; <span class="hljs-params">console</span>.<span class="hljs-params">log</span>(<span class="hljs-params">x</span>)</span>);\n\n<span class="hljs-comment">// \u4f8b\u4e8c</span>\n<span class="hljs-keyword">let</span> { find, html } = jake;\n\ndocument.query<span class="hljs-constructor">SelectorAll(<span class="hljs-string">"div.myClass"</span>)</span>\n::find(<span class="hljs-string">"p"</span>)\n::html(<span class="hljs-string">"hahaha"</span>);</code></pre>\n<h2 id="\u5c3e\u8c03\u7528\u4f18\u5316">\u5c3e\u8c03\u7528\u4f18\u5316</h2>\n<h3 id="\u4ec0\u4e48\u662f\u5c3e\u8c03\u7528\uff1f">\u4ec0\u4e48\u662f\u5c3e\u8c03\u7528\uff1f</h3>\n<p>\u5c3e\u8c03\u7528\uff08Tail Call\uff09\u662f\u51fd\u6570\u5f0f\u7f16\u7a0b\u7684\u4e00\u4e2a\u91cd\u8981\u6982\u5ff5\uff0c\u672c\u8eab\u975e\u5e38\u7b80\u5355\uff0c\u4e00\u53e5\u8bdd\u5c31\u80fd\u8bf4\u6e05\u695a\uff0c\u5c31\u662f\u6307\u67d0\u4e2a\u51fd\u6570\u7684\u6700\u540e\u4e00\u6b65\u662f\u8c03\u7528\u53e6\u4e00\u4e2a\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(x){\n  <span class="hljs-keyword">return</span> <span class="hljs-type">g(x)</span>;\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570<code>f</code>\u7684\u6700\u540e\u4e00\u6b65\u662f\u8c03\u7528\u51fd\u6570<code>g</code>\uff0c\u8fd9\u5c31\u53eb\u5c3e\u8c03\u7528\u3002</p>\n<p>\u4ee5\u4e0b\u4e09\u79cd\u60c5\u51b5\uff0c\u90fd\u4e0d\u5c5e\u4e8e\u5c3e\u8c03\u7528\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u60c5\u51b5\u4e00</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params">x</span>)</span>{\n  <span class="hljs-keyword">let</span> y = g(x);\n  <span class="hljs-keyword">return</span> y;\n}\n\n<span class="hljs-comment">// \u60c5\u51b5\u4e8c</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params">x</span>)</span>{\n  <span class="hljs-keyword">return</span> g(x) + <span class="hljs-number">1</span>;\n}\n\n<span class="hljs-comment">// \u60c5\u51b5\u4e09</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params">x</span>)</span>{\n  g(x);\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u60c5\u51b5\u4e00\u662f\u8c03\u7528\u51fd\u6570<code>g</code>\u4e4b\u540e\uff0c\u8fd8\u6709\u8d4b\u503c\u64cd\u4f5c\uff0c\u6240\u4ee5\u4e0d\u5c5e\u4e8e\u5c3e\u8c03\u7528\uff0c\u5373\u4f7f\u8bed\u4e49\u5b8c\u5168\u4e00\u6837\u3002\u60c5\u51b5\u4e8c\u4e5f\u5c5e\u4e8e\u8c03\u7528\u540e\u8fd8\u6709\u64cd\u4f5c\uff0c\u5373\u4f7f\u5199\u5728\u4e00\u884c\u5185\u3002\u60c5\u51b5\u4e09\u7b49\u540c\u4e8e\u4e0b\u9762\u7684\u4ee3\u7801\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span><span class="hljs-params">(x)</span></span>{\n  g(x);\n  <span class="hljs-keyword">return</span> <span class="hljs-literal">undefined</span>;\n}</code></pre>\n<p>\u5c3e\u8c03\u7528\u4e0d\u4e00\u5b9a\u51fa\u73b0\u5728\u51fd\u6570\u5c3e\u90e8\uff0c\u53ea\u8981\u662f\u6700\u540e\u4e00\u6b65\u64cd\u4f5c\u5373\u53ef\u3002</p>\n<pre><code class="language-javascript">function f(<span class="hljs-meta">x</span>) {\n  <span class="hljs-meta">if</span> (<span class="hljs-meta">x</span> &gt; 0) {\n    <span class="hljs-meta">return</span> m(<span class="hljs-meta">x</span>)\n  }\n  <span class="hljs-meta">return</span><span class="hljs-meta"> n(</span><span class="hljs-meta">x</span>);\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570<code>m</code>\u548c<code>n</code>\u90fd\u5c5e\u4e8e\u5c3e\u8c03\u7528\uff0c\u56e0\u4e3a\u5b83\u4eec\u90fd\u662f\u51fd\u6570<code>f</code>\u7684\u6700\u540e\u4e00\u6b65\u64cd\u4f5c\u3002</p>\n<h3 id="\u5c3e\u8c03\u7528\u4f18\u5316-1">\u5c3e\u8c03\u7528\u4f18\u5316</h3>\n<p>\u5c3e\u8c03\u7528\u4e4b\u6240\u4ee5\u4e0e\u5176\u4ed6\u8c03\u7528\u4e0d\u540c\uff0c\u5c31\u5728\u4e8e\u5b83\u7684\u7279\u6b8a\u7684\u8c03\u7528\u4f4d\u7f6e\u3002</p>\n<p>\u6211\u4eec\u77e5\u9053\uff0c\u51fd\u6570\u8c03\u7528\u4f1a\u5728\u5185\u5b58\u5f62\u6210\u4e00\u4e2a\u201c\u8c03\u7528\u8bb0\u5f55\u201d\uff0c\u53c8\u79f0\u201c\u8c03\u7528\u5e27\u201d\uff08call frame\uff09\uff0c\u4fdd\u5b58\u8c03\u7528\u4f4d\u7f6e\u548c\u5185\u90e8\u53d8\u91cf\u7b49\u4fe1\u606f\u3002\u5982\u679c\u5728\u51fd\u6570<code>A</code>\u7684\u5185\u90e8\u8c03\u7528\u51fd\u6570<code>B</code>\uff0c\u90a3\u4e48\u5728<code>A</code>\u7684\u8c03\u7528\u5e27\u4e0a\u65b9\uff0c\u8fd8\u4f1a\u5f62\u6210\u4e00\u4e2a<code>B</code>\u7684\u8c03\u7528\u5e27\u3002\u7b49\u5230<code>B</code>\u8fd0\u884c\u7ed3\u675f\uff0c\u5c06\u7ed3\u679c\u8fd4\u56de\u5230<code>A</code>\uff0c<code>B</code>\u7684\u8c03\u7528\u5e27\u624d\u4f1a\u6d88\u5931\u3002\u5982\u679c\u51fd\u6570<code>B</code>\u5185\u90e8\u8fd8\u8c03\u7528\u51fd\u6570<code>C</code>\uff0c\u90a3\u5c31\u8fd8\u6709\u4e00\u4e2a<code>C</code>\u7684\u8c03\u7528\u5e27\uff0c\u4ee5\u6b64\u7c7b\u63a8\u3002\u6240\u6709\u7684\u8c03\u7528\u5e27\uff0c\u5c31\u5f62\u6210\u4e00\u4e2a\u201c\u8c03\u7528\u6808\u201d\uff08call stack\uff09\u3002</p>\n<p>\u5c3e\u8c03\u7528\u7531\u4e8e\u662f\u51fd\u6570\u7684\u6700\u540e\u4e00\u6b65\u64cd\u4f5c\uff0c\u6240\u4ee5\u4e0d\u9700\u8981\u4fdd\u7559\u5916\u5c42\u51fd\u6570\u7684\u8c03\u7528\u5e27\uff0c\u56e0\u4e3a\u8c03\u7528\u4f4d\u7f6e\u3001\u5185\u90e8\u53d8\u91cf\u7b49\u4fe1\u606f\u90fd\u4e0d\u4f1a\u518d\u7528\u5230\u4e86\uff0c\u53ea\u8981\u76f4\u63a5\u7528\u5185\u5c42\u51fd\u6570\u7684\u8c03\u7528\u5e27\uff0c\u53d6\u4ee3\u5916\u5c42\u51fd\u6570\u7684\u8c03\u7528\u5e27\u5c31\u53ef\u4ee5\u4e86\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">let</span> m = <span class="hljs-number">1</span>;\n  <span class="hljs-keyword">let</span> n = <span class="hljs-number">2</span>;\n  <span class="hljs-keyword">return</span> g(m + n);\n}\nf();\n\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">return</span> g(<span class="hljs-number">3</span>);\n}\nf();\n\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\ng(<span class="hljs-number">3</span>);</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5982\u679c\u51fd\u6570<code>g</code>\u4e0d\u662f\u5c3e\u8c03\u7528\uff0c\u51fd\u6570<code>f</code>\u5c31\u9700\u8981\u4fdd\u5b58\u5185\u90e8\u53d8\u91cf<code>m</code>\u548c<code>n</code>\u7684\u503c\u3001<code>g</code>\u7684\u8c03\u7528\u4f4d\u7f6e\u7b49\u4fe1\u606f\u3002\u4f46\u7531\u4e8e\u8c03\u7528<code>g</code>\u4e4b\u540e\uff0c\u51fd\u6570<code>f</code>\u5c31\u7ed3\u675f\u4e86\uff0c\u6240\u4ee5\u6267\u884c\u5230\u6700\u540e\u4e00\u6b65\uff0c\u5b8c\u5168\u53ef\u4ee5\u5220\u9664<code>f(x)</code>\u7684\u8c03\u7528\u5e27\uff0c\u53ea\u4fdd\u7559<code>g(3)</code>\u7684\u8c03\u7528\u5e27\u3002</p>\n<p>\u8fd9\u5c31\u53eb\u505a\u201c\u5c3e\u8c03\u7528\u4f18\u5316\u201d\uff08Tail call optimization\uff09\uff0c\u5373\u53ea\u4fdd\u7559\u5185\u5c42\u51fd\u6570\u7684\u8c03\u7528\u5e27\u3002\u5982\u679c\u6240\u6709\u51fd\u6570\u90fd\u662f\u5c3e\u8c03\u7528\uff0c\u90a3\u4e48\u5b8c\u5168\u53ef\u4ee5\u505a\u5230\u6bcf\u6b21\u6267\u884c\u65f6\uff0c\u8c03\u7528\u5e27\u53ea\u6709\u4e00\u9879\uff0c\u8fd9\u5c06\u5927\u5927\u8282\u7701\u5185\u5b58\u3002\u8fd9\u5c31\u662f\u201c\u5c3e\u8c03\u7528\u4f18\u5316\u201d\u7684\u610f\u4e49\u3002</p>\n<p>\u6ce8\u610f\uff0c\u53ea\u6709\u4e0d\u518d\u7528\u5230\u5916\u5c42\u51fd\u6570\u7684\u5185\u90e8\u53d8\u91cf\uff0c\u5185\u5c42\u51fd\u6570\u7684\u8c03\u7528\u5e27\u624d\u4f1a\u53d6\u4ee3\u5916\u5c42\u51fd\u6570\u7684\u8c03\u7528\u5e27\uff0c\u5426\u5219\u5c31\u65e0\u6cd5\u8fdb\u884c\u201c\u5c3e\u8c03\u7528\u4f18\u5316\u201d\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">addOne</span><span class="hljs-params">(a)</span></span>{\n  <span class="hljs-keyword">var</span> one = <span class="hljs-number">1</span>;\n  <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">inner</span><span class="hljs-params">(b)</span></span>{\n    <span class="hljs-keyword">return</span> b + one;\n  }\n  <span class="hljs-keyword">return</span> inner(a);\n}</code></pre>\n<p>\u4e0a\u9762\u7684\u51fd\u6570\u4e0d\u4f1a\u8fdb\u884c\u5c3e\u8c03\u7528\u4f18\u5316\uff0c\u56e0\u4e3a\u5185\u5c42\u51fd\u6570<code>inner</code>\u7528\u5230\u4e86\u5916\u5c42\u51fd\u6570<code>addOne</code>\u7684\u5185\u90e8\u53d8\u91cf<code>one</code>\u3002</p>\n<h3 id="\u5c3e\u9012\u5f52">\u5c3e\u9012\u5f52</h3>\n<p>\u51fd\u6570\u8c03\u7528\u81ea\u8eab\uff0c\u79f0\u4e3a\u9012\u5f52\u3002\u5982\u679c\u5c3e\u8c03\u7528\u81ea\u8eab\uff0c\u5c31\u79f0\u4e3a\u5c3e\u9012\u5f52\u3002</p>\n<p>\u9012\u5f52\u975e\u5e38\u8017\u8d39\u5185\u5b58\uff0c\u56e0\u4e3a\u9700\u8981\u540c\u65f6\u4fdd\u5b58\u6210\u5343\u4e0a\u767e\u4e2a\u8c03\u7528\u5e27\uff0c\u5f88\u5bb9\u6613\u53d1\u751f\u201c\u6808\u6ea2\u51fa\u201d\u9519\u8bef\uff08stack overflow\uff09\u3002\u4f46\u5bf9\u4e8e\u5c3e\u9012\u5f52\u6765\u8bf4\uff0c\u7531\u4e8e\u53ea\u5b58\u5728\u4e00\u4e2a\u8c03\u7528\u5e27\uff0c\u6240\u4ee5\u6c38\u8fdc\u4e0d\u4f1a\u53d1\u751f\u201c\u6808\u6ea2\u51fa\u201d\u9519\u8bef\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">factorial</span><span class="hljs-params">(n)</span> </span>{\n  <span class="hljs-keyword">if</span> (n === <span class="hljs-number">1</span>) <span class="hljs-keyword">return</span> <span class="hljs-number">1</span>;\n  <span class="hljs-keyword">return</span> n * factorial(n - <span class="hljs-number">1</span>);\n}\n\nfactorial(<span class="hljs-number">5</span>) <span class="hljs-comment">// 120</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u662f\u4e00\u4e2a\u9636\u4e58\u51fd\u6570\uff0c\u8ba1\u7b97<code>n</code>\u7684\u9636\u4e58\uff0c\u6700\u591a\u9700\u8981\u4fdd\u5b58<code>n</code>\u4e2a\u8c03\u7528\u8bb0\u5f55\uff0c\u590d\u6742\u5ea6 O(n) \u3002</p>\n<p>\u5982\u679c\u6539\u5199\u6210\u5c3e\u9012\u5f52\uff0c\u53ea\u4fdd\u7559\u4e00\u4e2a\u8c03\u7528\u8bb0\u5f55\uff0c\u590d\u6742\u5ea6 O(1) \u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">factorial</span><span class="hljs-params">(n, total)</span> </span>{\n  <span class="hljs-keyword">if</span> (n === <span class="hljs-number">1</span>) <span class="hljs-keyword">return</span> total;\n  <span class="hljs-keyword">return</span> factorial(n - <span class="hljs-number">1</span>, n * total);\n}\n\nfactorial(<span class="hljs-number">5</span>, <span class="hljs-number">1</span>) <span class="hljs-comment">// 120</span></code></pre>\n<p>\u8fd8\u6709\u4e00\u4e2a\u6bd4\u8f83\u8457\u540d\u7684\u4f8b\u5b50\uff0c\u5c31\u662f\u8ba1\u7b97 Fibonacci \u6570\u5217\uff0c\u4e5f\u80fd\u5145\u5206\u8bf4\u660e\u5c3e\u9012\u5f52\u4f18\u5316\u7684\u91cd\u8981\u6027\u3002</p>\n<p>\u975e\u5c3e\u9012\u5f52\u7684 Fibonacci \u6570\u5217\u5b9e\u73b0\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">function</span> Fibonacci (n) {\n  <span class="hljs-keyword">if</span> ( n &lt;= <span class="hljs-number">1</span> ) {return <span class="hljs-number">1</span>};\n\n  return <span class="hljs-constructor">Fibonacci(<span class="hljs-params">n</span> - 1)</span> + <span class="hljs-constructor">Fibonacci(<span class="hljs-params">n</span> - 2)</span>;\n}\n\n<span class="hljs-constructor">Fibonacci(10)</span> <span class="hljs-comment">// 89</span>\n<span class="hljs-constructor">Fibonacci(100)</span> <span class="hljs-comment">// \u5806\u6808\u6ea2\u51fa</span>\n<span class="hljs-constructor">Fibonacci(500)</span> <span class="hljs-comment">// \u5806\u6808\u6ea2\u51fa</span></code></pre>\n<p>\u5c3e\u9012\u5f52\u4f18\u5316\u8fc7\u7684 Fibonacci \u6570\u5217\u5b9e\u73b0\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript">function Fibonacci2 (n , ac1 = <span class="hljs-number">1</span> , ac2 = <span class="hljs-number">1</span>) {\n  if( n &lt;= <span class="hljs-number">1</span> ) {return ac2};\n\n  return Fibonacci2 (n - <span class="hljs-number">1</span>, ac2, ac1 + ac2);\n}\n\nFibonacci2(<span class="hljs-number">100</span>) <span class="hljs-comment">// 573147844013817200000</span>\nFibonacci2(<span class="hljs-number">1000</span>) <span class="hljs-comment">// 7.0330367711422765e+208</span>\nFibonacci2(<span class="hljs-number">10000</span>) <span class="hljs-comment">// Infinity</span></code></pre>\n<p>\u7531\u6b64\u53ef\u89c1\uff0c\u201c\u5c3e\u8c03\u7528\u4f18\u5316\u201d\u5bf9\u9012\u5f52\u64cd\u4f5c\u610f\u4e49\u91cd\u5927\uff0c\u6240\u4ee5\u4e00\u4e9b\u51fd\u6570\u5f0f\u7f16\u7a0b\u8bed\u8a00\u5c06\u5176\u5199\u5165\u4e86\u8bed\u8a00\u89c4\u683c\u3002ES6 \u662f\u5982\u6b64\uff0c\u7b2c\u4e00\u6b21\u660e\u786e\u89c4\u5b9a\uff0c\u6240\u6709 ECMAScript \u7684\u5b9e\u73b0\uff0c\u90fd\u5fc5\u987b\u90e8\u7f72\u201c\u5c3e\u8c03\u7528\u4f18\u5316\u201d\u3002\u8fd9\u5c31\u662f\u8bf4\uff0cES6 \u4e2d\u53ea\u8981\u4f7f\u7528\u5c3e\u9012\u5f52\uff0c\u5c31\u4e0d\u4f1a\u53d1\u751f\u6808\u6ea2\u51fa\uff0c\u76f8\u5bf9\u8282\u7701\u5185\u5b58\u3002</p>\n<h3 id="\u9012\u5f52\u51fd\u6570\u7684\u6539\u5199">\u9012\u5f52\u51fd\u6570\u7684\u6539\u5199</h3>\n<p>\u5c3e\u9012\u5f52\u7684\u5b9e\u73b0\uff0c\u5f80\u5f80\u9700\u8981\u6539\u5199\u9012\u5f52\u51fd\u6570\uff0c\u786e\u4fdd\u6700\u540e\u4e00\u6b65\u53ea\u8c03\u7528\u81ea\u8eab\u3002\u505a\u5230\u8fd9\u4e00\u70b9\u7684\u65b9\u6cd5\uff0c\u5c31\u662f\u628a\u6240\u6709\u7528\u5230\u7684\u5185\u90e8\u53d8\u91cf\u6539\u5199\u6210\u51fd\u6570\u7684\u53c2\u6570\u3002\u6bd4\u5982\u4e0a\u9762\u7684\u4f8b\u5b50\uff0c\u9636\u4e58\u51fd\u6570 factorial \u9700\u8981\u7528\u5230\u4e00\u4e2a\u4e2d\u95f4\u53d8\u91cf<code>total</code>\uff0c\u90a3\u5c31\u628a\u8fd9\u4e2a\u4e2d\u95f4\u53d8\u91cf\u6539\u5199\u6210\u51fd\u6570\u7684\u53c2\u6570\u3002\u8fd9\u6837\u505a\u7684\u7f3a\u70b9\u5c31\u662f\u4e0d\u592a\u76f4\u89c2\uff0c\u7b2c\u4e00\u773c\u5f88\u96be\u770b\u51fa\u6765\uff0c\u4e3a\u4ec0\u4e48\u8ba1\u7b97<code>5</code>\u7684\u9636\u4e58\uff0c\u9700\u8981\u4f20\u5165\u4e24\u4e2a\u53c2\u6570<code>5</code>\u548c<code>1</code>\uff1f</p>\n<p>\u4e24\u4e2a\u65b9\u6cd5\u53ef\u4ee5\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\u3002\u65b9\u6cd5\u4e00\u662f\u5728\u5c3e\u9012\u5f52\u51fd\u6570\u4e4b\u5916\uff0c\u518d\u63d0\u4f9b\u4e00\u4e2a\u6b63\u5e38\u5f62\u5f0f\u7684\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">function</span> tail<span class="hljs-constructor">Factorial(<span class="hljs-params">n</span>, <span class="hljs-params">total</span>)</span> {\n  <span class="hljs-keyword">if</span> (n<span class="hljs-operator"> === </span><span class="hljs-number">1</span>) return total;\n  return tail<span class="hljs-constructor">Factorial(<span class="hljs-params">n</span> - 1, <span class="hljs-params">n</span> <span class="hljs-operator">*</span> <span class="hljs-params">total</span>)</span>;\n}\n\n<span class="hljs-keyword">function</span> factorial(n) {\n  return tail<span class="hljs-constructor">Factorial(<span class="hljs-params">n</span>, 1)</span>;\n}\n\nfactorial(<span class="hljs-number">5</span>) <span class="hljs-comment">// 120</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u901a\u8fc7\u4e00\u4e2a\u6b63\u5e38\u5f62\u5f0f\u7684\u9636\u4e58\u51fd\u6570<code>factorial</code>\uff0c\u8c03\u7528\u5c3e\u9012\u5f52\u51fd\u6570<code>tailFactorial</code>\uff0c\u770b\u8d77\u6765\u5c31\u6b63\u5e38\u591a\u4e86\u3002</p>\n<p>\u51fd\u6570\u5f0f\u7f16\u7a0b\u6709\u4e00\u4e2a\u6982\u5ff5\uff0c\u53eb\u505a\u67ef\u91cc\u5316\uff08currying\uff09\uff0c\u610f\u601d\u662f\u5c06\u591a\u53c2\u6570\u7684\u51fd\u6570\u8f6c\u6362\u6210\u5355\u53c2\u6570\u7684\u5f62\u5f0f\u3002\u8fd9\u91cc\u4e5f\u53ef\u4ee5\u4f7f\u7528\u67ef\u91cc\u5316\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">currying</span><span class="hljs-params">(fn, n)</span> </span>{\n  <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(m)</span> </span>{\n    <span class="hljs-keyword">return</span> fn.call(<span class="hljs-keyword">this</span>, m, n);\n  };\n}\n\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">tailFactorial</span><span class="hljs-params">(n, total)</span> </span>{\n  <span class="hljs-keyword">if</span> (n === <span class="hljs-number">1</span>) <span class="hljs-keyword">return</span> total;\n  <span class="hljs-keyword">return</span> tailFactorial(n - <span class="hljs-number">1</span>, n * total);\n}\n\n<span class="hljs-keyword">const</span> factorial = currying(tailFactorial, <span class="hljs-number">1</span>);\n\nfactorial(<span class="hljs-number">5</span>) <span class="hljs-comment">// 120</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u901a\u8fc7\u67ef\u91cc\u5316\uff0c\u5c06\u5c3e\u9012\u5f52\u51fd\u6570<code>tailFactorial</code>\u53d8\u4e3a\u53ea\u63a5\u53d7\u4e00\u4e2a\u53c2\u6570\u7684<code>factorial</code>\u3002</p>\n<p>\u7b2c\u4e8c\u79cd\u65b9\u6cd5\u5c31\u7b80\u5355\u591a\u4e86\uff0c\u5c31\u662f\u91c7\u7528 ES6 \u7684\u51fd\u6570\u9ed8\u8ba4\u503c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">factorial</span><span class="hljs-params">(n, total = 1)</span> </span>{\n  <span class="hljs-keyword">if</span> (n === <span class="hljs-number">1</span>) <span class="hljs-keyword">return</span> total;\n  <span class="hljs-keyword">return</span> factorial(n - <span class="hljs-number">1</span>, n * total);\n}\n\nfactorial(<span class="hljs-number">5</span>) <span class="hljs-comment">// 120</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53c2\u6570<code>total</code>\u6709\u9ed8\u8ba4\u503c<code>1</code>\uff0c\u6240\u4ee5\u8c03\u7528\u65f6\u4e0d\u7528\u63d0\u4f9b\u8fd9\u4e2a\u503c\u3002</p>\n<p>\u603b\u7ed3\u4e00\u4e0b\uff0c\u9012\u5f52\u672c\u8d28\u4e0a\u662f\u4e00\u79cd\u5faa\u73af\u64cd\u4f5c\u3002\u7eaf\u7cb9\u7684\u51fd\u6570\u5f0f\u7f16\u7a0b\u8bed\u8a00\u6ca1\u6709\u5faa\u73af\u64cd\u4f5c\u547d\u4ee4\uff0c\u6240\u6709\u7684\u5faa\u73af\u90fd\u7528\u9012\u5f52\u5b9e\u73b0\uff0c\u8fd9\u5c31\u662f\u4e3a\u4ec0\u4e48\u5c3e\u9012\u5f52\u5bf9\u8fd9\u4e9b\u8bed\u8a00\u6781\u5176\u91cd\u8981\u3002\u5bf9\u4e8e\u5176\u4ed6\u652f\u6301\u201c\u5c3e\u8c03\u7528\u4f18\u5316\u201d\u7684\u8bed\u8a00\uff08\u6bd4\u5982 Lua\uff0cES6\uff09\uff0c\u53ea\u9700\u8981\u77e5\u9053\u5faa\u73af\u53ef\u4ee5\u7528\u9012\u5f52\u4ee3\u66ff\uff0c\u800c\u4e00\u65e6\u4f7f\u7528\u9012\u5f52\uff0c\u5c31\u6700\u597d\u4f7f\u7528\u5c3e\u9012\u5f52\u3002</p>\n<h3 id="\u4e25\u683c\u6a21\u5f0f-1">\u4e25\u683c\u6a21\u5f0f</h3>\n<p>ES6 \u7684\u5c3e\u8c03\u7528\u4f18\u5316\u53ea\u5728\u4e25\u683c\u6a21\u5f0f\u4e0b\u5f00\u542f\uff0c\u6b63\u5e38\u6a21\u5f0f\u662f\u65e0\u6548\u7684\u3002</p>\n<p>\u8fd9\u662f\u56e0\u4e3a\u5728\u6b63\u5e38\u6a21\u5f0f\u4e0b\uff0c\u51fd\u6570\u5185\u90e8\u6709\u4e24\u4e2a\u53d8\u91cf\uff0c\u53ef\u4ee5\u8ddf\u8e2a\u51fd\u6570\u7684\u8c03\u7528\u6808\u3002</p>\n<ul>\n<li><code>func.arguments</code>\uff1a\u8fd4\u56de\u8c03\u7528\u65f6\u51fd\u6570\u7684\u53c2\u6570\u3002</li>\n<li><code>func.caller</code>\uff1a\u8fd4\u56de\u8c03\u7528\u5f53\u524d\u51fd\u6570\u7684\u90a3\u4e2a\u51fd\u6570\u3002</li>\n</ul>\n<p>\u5c3e\u8c03\u7528\u4f18\u5316\u53d1\u751f\u65f6\uff0c\u51fd\u6570\u7684\u8c03\u7528\u6808\u4f1a\u6539\u5199\uff0c\u56e0\u6b64\u4e0a\u9762\u4e24\u4e2a\u53d8\u91cf\u5c31\u4f1a\u5931\u771f\u3002\u4e25\u683c\u6a21\u5f0f\u7981\u7528\u8fd9\u4e24\u4e2a\u53d8\u91cf\uff0c\u6240\u4ee5\u5c3e\u8c03\u7528\u6a21\u5f0f\u4ec5\u5728\u4e25\u683c\u6a21\u5f0f\u4e0b\u751f\u6548\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">restricted</span>(<span class="hljs-params"></span>) </span>{\n<span class="hljs-meta">  \'use strict\'</span>;\n  restricted.caller;    <span class="hljs-comment">// \u62a5\u9519</span>\n  restricted.arguments; <span class="hljs-comment">// \u62a5\u9519</span>\n}\nrestricted();</code></pre>\n<h3 id="\u5c3e\u9012\u5f52\u4f18\u5316\u7684\u5b9e\u73b0">\u5c3e\u9012\u5f52\u4f18\u5316\u7684\u5b9e\u73b0</h3>\n<p>\u5c3e\u9012\u5f52\u4f18\u5316\u53ea\u5728\u4e25\u683c\u6a21\u5f0f\u4e0b\u751f\u6548\uff0c\u90a3\u4e48\u6b63\u5e38\u6a21\u5f0f\u4e0b\uff0c\u6216\u8005\u90a3\u4e9b\u4e0d\u652f\u6301\u8be5\u529f\u80fd\u7684\u73af\u5883\u4e2d\uff0c\u6709\u6ca1\u6709\u529e\u6cd5\u4e5f\u4f7f\u7528\u5c3e\u9012\u5f52\u4f18\u5316\u5462\uff1f\u56de\u7b54\u662f\u53ef\u4ee5\u7684\uff0c\u5c31\u662f\u81ea\u5df1\u5b9e\u73b0\u5c3e\u9012\u5f52\u4f18\u5316\u3002</p>\n<p>\u5b83\u7684\u539f\u7406\u975e\u5e38\u7b80\u5355\u3002\u5c3e\u9012\u5f52\u4e4b\u6240\u4ee5\u9700\u8981\u4f18\u5316\uff0c\u539f\u56e0\u662f\u8c03\u7528\u6808\u592a\u591a\uff0c\u9020\u6210\u6ea2\u51fa\uff0c\u90a3\u4e48\u53ea\u8981\u51cf\u5c11\u8c03\u7528\u6808\uff0c\u5c31\u4e0d\u4f1a\u6ea2\u51fa\u3002\u600e\u4e48\u505a\u53ef\u4ee5\u51cf\u5c11\u8c03\u7528\u6808\u5462\uff1f\u5c31\u662f\u91c7\u7528\u201c\u5faa\u73af\u201d\u6362\u6389\u201c\u9012\u5f52\u201d\u3002</p>\n<p>\u4e0b\u9762\u662f\u4e00\u4e2a\u6b63\u5e38\u7684\u9012\u5f52\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span></span> <span class="hljs-built_in">sum</span>(x, y) {\n  <span class="hljs-keyword">if</span> (y &gt; <span class="hljs-number">0</span>) {\n    <span class="hljs-keyword">return</span> <span class="hljs-built_in">sum</span>(x + <span class="hljs-number">1</span>, y - <span class="hljs-number">1</span>);\n  } <span class="hljs-keyword">else</span> {\n    <span class="hljs-keyword">return</span> x;\n  }\n}\n\n<span class="hljs-built_in">sum</span>(<span class="hljs-number">1</span>, <span class="hljs-number">100000</span>)\n// Uncaught RangeError: Maximum <span class="hljs-keyword">call</span> stack <span class="hljs-built_in">size</span> exceeded(\u2026)</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>sum</code>\u662f\u4e00\u4e2a\u9012\u5f52\u51fd\u6570\uff0c\u53c2\u6570<code>x</code>\u662f\u9700\u8981\u7d2f\u52a0\u7684\u503c\uff0c\u53c2\u6570<code>y</code>\u63a7\u5236\u9012\u5f52\u6b21\u6570\u3002\u4e00\u65e6\u6307\u5b9a<code>sum</code>\u9012\u5f52 100000 \u6b21\uff0c\u5c31\u4f1a\u62a5\u9519\uff0c\u63d0\u793a\u8d85\u51fa\u8c03\u7528\u6808\u7684\u6700\u5927\u6b21\u6570\u3002</p>\n<p>\u8e66\u5e8a\u51fd\u6570\uff08trampoline\uff09\u53ef\u4ee5\u5c06\u9012\u5f52\u6267\u884c\u8f6c\u4e3a\u5faa\u73af\u6267\u884c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">trampoline</span><span class="hljs-params">(f)</span> </span>{\n  <span class="hljs-keyword">while</span> (f &amp;&amp; f <span class="hljs-keyword">instanceof</span> <span class="hljs-function"><span class="hljs-keyword">Function</span>) </span>{\n    f = f();\n  }\n  <span class="hljs-keyword">return</span> f;\n}</code></pre>\n<p>\u4e0a\u9762\u5c31\u662f\u8e66\u5e8a\u51fd\u6570\u7684\u4e00\u4e2a\u5b9e\u73b0\uff0c\u5b83\u63a5\u53d7\u4e00\u4e2a\u51fd\u6570<code>f</code>\u4f5c\u4e3a\u53c2\u6570\u3002\u53ea\u8981<code>f</code>\u6267\u884c\u540e\u8fd4\u56de\u4e00\u4e2a\u51fd\u6570\uff0c\u5c31\u7ee7\u7eed\u6267\u884c\u3002\u6ce8\u610f\uff0c\u8fd9\u91cc\u662f\u8fd4\u56de\u4e00\u4e2a\u51fd\u6570\uff0c\u7136\u540e\u6267\u884c\u8be5\u51fd\u6570\uff0c\u800c\u4e0d\u662f\u51fd\u6570\u91cc\u9762\u8c03\u7528\u51fd\u6570\uff0c\u8fd9\u6837\u5c31\u907f\u514d\u4e86\u9012\u5f52\u6267\u884c\uff0c\u4ece\u800c\u5c31\u6d88\u9664\u4e86\u8c03\u7528\u6808\u8fc7\u5927\u7684\u95ee\u9898\u3002</p>\n<p>\u7136\u540e\uff0c\u8981\u505a\u7684\u5c31\u662f\u5c06\u539f\u6765\u7684\u9012\u5f52\u51fd\u6570\uff0c\u6539\u5199\u4e3a\u6bcf\u4e00\u6b65\u8fd4\u56de\u53e6\u4e00\u4e2a\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript">function sum(<span class="hljs-symbol">x</span>, <span class="hljs-symbol">y</span>) {\n  <span class="hljs-keywords">if</span> (<span class="hljs-symbol">y</span> &gt; <span class="hljs-number">0</span>) {\n    <span class="hljs-keywords">return</span> sum.bind(null, <span class="hljs-symbol">x</span> + <span class="hljs-number">1</span>, <span class="hljs-symbol">y</span> - <span class="hljs-number">1</span>);\n  } <span class="hljs-keywords">else</span> {\n    <span class="hljs-keywords">return</span> <span class="hljs-symbol">x</span>;\n  }\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>sum</code>\u51fd\u6570\u7684\u6bcf\u6b21\u6267\u884c\uff0c\u90fd\u4f1a\u8fd4\u56de\u81ea\u8eab\u7684\u53e6\u4e00\u4e2a\u7248\u672c\u3002</p>\n<p>\u73b0\u5728\uff0c\u4f7f\u7528\u8e66\u5e8a\u51fd\u6570\u6267\u884c<code>sum</code>\uff0c\u5c31\u4e0d\u4f1a\u53d1\u751f\u8c03\u7528\u6808\u6ea2\u51fa\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-title">trampoline</span><span class="hljs-params">(sum(<span class="hljs-number">1</span>, <span class="hljs-number">100000</span>)</span></span>)\n<span class="hljs-comment">// 100001</span></code></pre>\n<p>\u8e66\u5e8a\u51fd\u6570\u5e76\u4e0d\u662f\u771f\u6b63\u7684\u5c3e\u9012\u5f52\u4f18\u5316\uff0c\u4e0b\u9762\u7684\u5b9e\u73b0\u624d\u662f\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">tco</span>(<span class="hljs-params">f</span>) </span>{\n  <span class="hljs-keyword">var</span> value;\n  <span class="hljs-keyword">var</span> active = <span class="hljs-literal">false</span>;\n  <span class="hljs-keyword">var</span> accumulated = [];\n\n  <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">accumulator</span>(<span class="hljs-params"></span>) </span>{\n    accumulated.push(<span class="hljs-built_in">arguments</span>);\n    <span class="hljs-keyword">if</span> (!active) {\n      active = <span class="hljs-literal">true</span>;\n      <span class="hljs-keyword">while</span> (accumulated.length) {\n        value = f.apply(<span class="hljs-keyword">this</span>, accumulated.shift());\n      }\n      active = <span class="hljs-literal">false</span>;\n      <span class="hljs-keyword">return</span> value;\n    }\n  };\n}\n\n<span class="hljs-keyword">var</span> sum = tco(<span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params">x, y</span>) </span>{\n  <span class="hljs-keyword">if</span> (y &gt; <span class="hljs-number">0</span>) {\n    <span class="hljs-keyword">return</span> sum(x + <span class="hljs-number">1</span>, y - <span class="hljs-number">1</span>)\n  }\n  <span class="hljs-keyword">else</span> {\n    <span class="hljs-keyword">return</span> x\n  }\n});\n\nsum(<span class="hljs-number">1</span>, <span class="hljs-number">100000</span>)\n<span class="hljs-comment">// 100001</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>tco</code>\u51fd\u6570\u662f\u5c3e\u9012\u5f52\u4f18\u5316\u7684\u5b9e\u73b0\uff0c\u5b83\u7684\u5965\u5999\u5c31\u5728\u4e8e\u72b6\u6001\u53d8\u91cf<code>active</code>\u3002\u9ed8\u8ba4\u60c5\u51b5\u4e0b\uff0c\u8fd9\u4e2a\u53d8\u91cf\u662f\u4e0d\u6fc0\u6d3b\u7684\u3002\u4e00\u65e6\u8fdb\u5165\u5c3e\u9012\u5f52\u4f18\u5316\u7684\u8fc7\u7a0b\uff0c\u8fd9\u4e2a\u53d8\u91cf\u5c31\u6fc0\u6d3b\u4e86\u3002\u7136\u540e\uff0c\u6bcf\u4e00\u8f6e\u9012\u5f52<code>sum</code>\u8fd4\u56de\u7684\u90fd\u662f<code>undefined</code>\uff0c\u6240\u4ee5\u5c31\u907f\u514d\u4e86\u9012\u5f52\u6267\u884c\uff1b\u800c<code>accumulated</code>\u6570\u7ec4\u5b58\u653e\u6bcf\u4e00\u8f6e<code>sum</code>\u6267\u884c\u7684\u53c2\u6570\uff0c\u603b\u662f\u6709\u503c\u7684\uff0c\u8fd9\u5c31\u4fdd\u8bc1\u4e86<code>accumulator</code>\u51fd\u6570\u5185\u90e8\u7684<code>while</code>\u5faa\u73af\u603b\u662f\u4f1a\u6267\u884c\u3002\u8fd9\u6837\u5c31\u5f88\u5de7\u5999\u5730\u5c06\u201c\u9012\u5f52\u201d\u6539\u6210\u4e86\u201c\u5faa\u73af\u201d\uff0c\u800c\u540e\u4e00\u8f6e\u7684\u53c2\u6570\u4f1a\u53d6\u4ee3\u524d\u4e00\u8f6e\u7684\u53c2\u6570\uff0c\u4fdd\u8bc1\u4e86\u8c03\u7528\u6808\u53ea\u6709\u4e00\u5c42\u3002</p>\n<h2 id="\u51fd\u6570\u53c2\u6570\u7684\u5c3e\u9017\u53f7">\u51fd\u6570\u53c2\u6570\u7684\u5c3e\u9017\u53f7</h2>\n<p>ES2017 <a href="https://github.com/jeffmo/es-trailing-function-commas">\u5141\u8bb8</a>\u51fd\u6570\u7684\u6700\u540e\u4e00\u4e2a\u53c2\u6570\u6709\u5c3e\u9017\u53f7\uff08trailing comma\uff09\u3002</p>\n<p>\u6b64\u524d\uff0c\u51fd\u6570\u5b9a\u4e49\u548c\u8c03\u7528\u65f6\uff0c\u90fd\u4e0d\u5141\u8bb8\u6700\u540e\u4e00\u4e2a\u53c2\u6570\u540e\u9762\u51fa\u73b0\u9017\u53f7\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">clownsEverywhere</span><span class="hljs-params">(\n  param1,\n  param2\n)</span> </span>{ <span class="hljs-comment">/* ... */</span> }\n\nclownsEverywhere(\n  <span class="hljs-string">\'foo\'</span>,\n  <span class="hljs-string">\'bar\'</span>\n);</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5982\u679c\u5728<code>param2</code>\u6216<code>bar</code>\u540e\u9762\u52a0\u4e00\u4e2a\u9017\u53f7\uff0c\u5c31\u4f1a\u62a5\u9519\u3002</p>\n<p>\u5982\u679c\u50cf\u4e0a\u9762\u8fd9\u6837\uff0c\u5c06\u53c2\u6570\u5199\u6210\u591a\u884c\uff08\u5373\u6bcf\u4e2a\u53c2\u6570\u5360\u636e\u4e00\u884c\uff09\uff0c\u4ee5\u540e\u4fee\u6539\u4ee3\u7801\u7684\u65f6\u5019\uff0c\u60f3\u4e3a\u51fd\u6570<code>clownsEverywhere</code>\u6dfb\u52a0\u7b2c\u4e09\u4e2a\u53c2\u6570\uff0c\u6216\u8005\u8c03\u6574\u53c2\u6570\u7684\u6b21\u5e8f\uff0c\u5c31\u52bf\u5fc5\u8981\u5728\u539f\u6765\u6700\u540e\u4e00\u4e2a\u53c2\u6570\u540e\u9762\u6dfb\u52a0\u4e00\u4e2a\u9017\u53f7\u3002\u8fd9\u5bf9\u4e8e\u7248\u672c\u7ba1\u7406\u7cfb\u7edf\u6765\u8bf4\uff0c\u5c31\u4f1a\u663e\u793a\u6dfb\u52a0\u9017\u53f7\u7684\u90a3\u4e00\u884c\u4e5f\u53d1\u751f\u4e86\u53d8\u52a8\u3002\u8fd9\u770b\u4e0a\u53bb\u6709\u70b9\u5197\u4f59\uff0c\u56e0\u6b64\u65b0\u7684\u8bed\u6cd5\u5141\u8bb8\u5b9a\u4e49\u548c\u8c03\u7528\u65f6\uff0c\u5c3e\u90e8\u76f4\u63a5\u6709\u4e00\u4e2a\u9017\u53f7\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">clownsEverywhere</span><span class="hljs-params">(\n  param1,\n  param2,\n)</span> </span>{ <span class="hljs-comment">/* ... */</span> }\n\nclownsEverywhere(\n  <span class="hljs-string">\'foo\'</span>,\n  <span class="hljs-string">\'bar\'</span>,\n);</code></pre>\n<p>\u8fd9\u6837\u7684\u89c4\u5b9a\u4e5f\u4f7f\u5f97\uff0c\u51fd\u6570\u53c2\u6570\u4e0e\u6570\u7ec4\u548c\u5bf9\u8c61\u7684\u5c3e\u9017\u53f7\u89c4\u5219\uff0c\u4fdd\u6301\u4e00\u81f4\u4e86\u3002</p>\n<h2 id="catch-\u8bed\u53e5\u7684\u53c2\u6570">catch \u8bed\u53e5\u7684\u53c2\u6570</h2>\n<p>\u76ee\u524d\uff0c\u6709\u4e00\u4e2a<a href="https://github.com/tc39/proposal-optional-catch-binding">\u63d0\u6848</a>\uff0c\u5141\u8bb8<code>try...catch</code>\u7ed3\u6784\u4e2d\u7684<code>catch</code>\u8bed\u53e5\u8c03\u7528\u65f6\u4e0d\u5e26\u6709\u53c2\u6570\u3002\u8fd9\u4e2a\u63d0\u6848\u8ddf\u53c2\u6570\u6709\u5173\uff0c\u4e5f\u653e\u5728\u8fd9\u4e00\u7ae0\u4ecb\u7ecd\u3002</p>\n<p>\u4f20\u7edf\u7684\u5199\u6cd5\u662f<code>catch</code>\u8bed\u53e5\u5fc5\u987b\u5e26\u6709\u53c2\u6570\uff0c\u7528\u6765\u63a5\u6536<code>try</code>\u4ee3\u7801\u5757\u629b\u51fa\u7684\u9519\u8bef\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">try</span> {\n  <span class="hljs-comment">//  \xb7\xb7\xb7</span>\n} <span class="hljs-keyword">catch</span> (<span class="hljs-keyword">error</span>) {\n  <span class="hljs-comment">//  \xb7\xb7\xb7</span>\n}</code></pre>\n<p>\u65b0\u7684\u5199\u6cd5\u5141\u8bb8\u7701\u7565<code>catch</code>\u540e\u9762\u7684\u53c2\u6570\uff0c\u800c\u4e0d\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">try</span> {\n  <span class="hljs-comment">//  \xb7\xb7\xb7</span>\n} <span class="hljs-keyword">catch</span> {\n  <span class="hljs-comment">//  \xb7\xb7\xb7</span>\n}</code></pre>\n<p>\u65b0\u5199\u6cd5\u53ea\u5728\u4e0d\u9700\u8981\u9519\u8bef\u5b9e\u4f8b\u7684\u60c5\u51b5\u4e0b\u6709\u7528\uff0c\u56e0\u6b64\u4e0d\u53ca\u4f20\u7edf\u5199\u6cd5\u7684\u7528\u9014\u5e7f\u3002</p>\n<pre><code class="language-javascript">let <span class="hljs-keyword">jsonData;\n</span>try {\n  <span class="hljs-keyword">jsonData </span>= <span class="hljs-keyword">JSON.parse(str);\n</span>} catch {\n  <span class="hljs-keyword">jsonData </span>= DEFAULT_DATA<span class="hljs-comment">;</span>\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>JSON.parse</code>\u62a5\u9519\u53ea\u6709\u4e00\u79cd\u53ef\u80fd\uff1a\u89e3\u6790\u5931\u8d25\u3002\u56e0\u6b64\uff0c\u53ef\u4ee5\u4e0d\u9700\u8981\u629b\u51fa\u7684\u9519\u8bef\u5b9e\u4f8b\u3002</p>\n'}}]);