(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([[46],{iypx:function(s,n){s.exports='<h1 id="generator-\u51fd\u6570\u7684\u5f02\u6b65\u5e94\u7528">Generator \u51fd\u6570\u7684\u5f02\u6b65\u5e94\u7528</h1>\n<p>\u5f02\u6b65\u7f16\u7a0b\u5bf9 JavaScript \u8bed\u8a00\u592a\u91cd\u8981\u3002Javascript \u8bed\u8a00\u7684\u6267\u884c\u73af\u5883\u662f\u201c\u5355\u7ebf\u7a0b\u201d\u7684\uff0c\u5982\u679c\u6ca1\u6709\u5f02\u6b65\u7f16\u7a0b\uff0c\u6839\u672c\u6ca1\u6cd5\u7528\uff0c\u975e\u5361\u6b7b\u4e0d\u53ef\u3002\u672c\u7ae0\u4e3b\u8981\u4ecb\u7ecd Generator \u51fd\u6570\u5982\u4f55\u5b8c\u6210\u5f02\u6b65\u64cd\u4f5c\u3002</p>\n<h2 id="\u4f20\u7edf\u65b9\u6cd5">\u4f20\u7edf\u65b9\u6cd5</h2>\n<p>ES6 \u8bde\u751f\u4ee5\u524d\uff0c\u5f02\u6b65\u7f16\u7a0b\u7684\u65b9\u6cd5\uff0c\u5927\u6982\u6709\u4e0b\u9762\u56db\u79cd\u3002</p>\n<ul>\n<li>\u56de\u8c03\u51fd\u6570</li>\n<li>\u4e8b\u4ef6\u76d1\u542c</li>\n<li>\u53d1\u5e03/\u8ba2\u9605</li>\n<li>Promise \u5bf9\u8c61</li>\n</ul>\n<p>Generator \u51fd\u6570\u5c06 JavaScript \u5f02\u6b65\u7f16\u7a0b\u5e26\u5165\u4e86\u4e00\u4e2a\u5168\u65b0\u7684\u9636\u6bb5\u3002</p>\n<h2 id="\u57fa\u672c\u6982\u5ff5">\u57fa\u672c\u6982\u5ff5</h2>\n<h3 id="\u5f02\u6b65">\u5f02\u6b65</h3>\n<p>\u6240\u8c13&quot;\u5f02\u6b65&quot;\uff0c\u7b80\u5355\u8bf4\u5c31\u662f\u4e00\u4e2a\u4efb\u52a1\u4e0d\u662f\u8fde\u7eed\u5b8c\u6210\u7684\uff0c\u53ef\u4ee5\u7406\u89e3\u6210\u8be5\u4efb\u52a1\u88ab\u4eba\u4e3a\u5206\u6210\u4e24\u6bb5\uff0c\u5148\u6267\u884c\u7b2c\u4e00\u6bb5\uff0c\u7136\u540e\u8f6c\u800c\u6267\u884c\u5176\u4ed6\u4efb\u52a1\uff0c\u7b49\u505a\u597d\u4e86\u51c6\u5907\uff0c\u518d\u56de\u8fc7\u5934\u6267\u884c\u7b2c\u4e8c\u6bb5\u3002</p>\n<p>\u6bd4\u5982\uff0c\u6709\u4e00\u4e2a\u4efb\u52a1\u662f\u8bfb\u53d6\u6587\u4ef6\u8fdb\u884c\u5904\u7406\uff0c\u4efb\u52a1\u7684\u7b2c\u4e00\u6bb5\u662f\u5411\u64cd\u4f5c\u7cfb\u7edf\u53d1\u51fa\u8bf7\u6c42\uff0c\u8981\u6c42\u8bfb\u53d6\u6587\u4ef6\u3002\u7136\u540e\uff0c\u7a0b\u5e8f\u6267\u884c\u5176\u4ed6\u4efb\u52a1\uff0c\u7b49\u5230\u64cd\u4f5c\u7cfb\u7edf\u8fd4\u56de\u6587\u4ef6\uff0c\u518d\u63a5\u7740\u6267\u884c\u4efb\u52a1\u7684\u7b2c\u4e8c\u6bb5\uff08\u5904\u7406\u6587\u4ef6\uff09\u3002\u8fd9\u79cd\u4e0d\u8fde\u7eed\u7684\u6267\u884c\uff0c\u5c31\u53eb\u505a\u5f02\u6b65\u3002</p>\n<p>\u76f8\u5e94\u5730\uff0c\u8fde\u7eed\u7684\u6267\u884c\u5c31\u53eb\u505a\u540c\u6b65\u3002\u7531\u4e8e\u662f\u8fde\u7eed\u6267\u884c\uff0c\u4e0d\u80fd\u63d2\u5165\u5176\u4ed6\u4efb\u52a1\uff0c\u6240\u4ee5\u64cd\u4f5c\u7cfb\u7edf\u4ece\u786c\u76d8\u8bfb\u53d6\u6587\u4ef6\u7684\u8fd9\u6bb5\u65f6\u95f4\uff0c\u7a0b\u5e8f\u53ea\u80fd\u5e72\u7b49\u7740\u3002</p>\n<h3 id="\u56de\u8c03\u51fd\u6570">\u56de\u8c03\u51fd\u6570</h3>\n<p>JavaScript \u8bed\u8a00\u5bf9\u5f02\u6b65\u7f16\u7a0b\u7684\u5b9e\u73b0\uff0c\u5c31\u662f\u56de\u8c03\u51fd\u6570\u3002\u6240\u8c13\u56de\u8c03\u51fd\u6570\uff0c\u5c31\u662f\u628a\u4efb\u52a1\u7684\u7b2c\u4e8c\u6bb5\u5355\u72ec\u5199\u5728\u4e00\u4e2a\u51fd\u6570\u91cc\u9762\uff0c\u7b49\u5230\u91cd\u65b0\u6267\u884c\u8fd9\u4e2a\u4efb\u52a1\u7684\u65f6\u5019\uff0c\u5c31\u76f4\u63a5\u8c03\u7528\u8fd9\u4e2a\u51fd\u6570\u3002\u56de\u8c03\u51fd\u6570\u7684\u82f1\u8bed\u540d\u5b57<code>callback</code>\uff0c\u76f4\u8bd1\u8fc7\u6765\u5c31\u662f&quot;\u91cd\u65b0\u8c03\u7528&quot;\u3002</p>\n<p>\u8bfb\u53d6\u6587\u4ef6\u8fdb\u884c\u5904\u7406\uff0c\u662f\u8fd9\u6837\u5199\u7684\u3002</p>\n<pre><code class="language-javascript">fs.readFile(<span class="hljs-string">\'/etc/passwd\'</span>, <span class="hljs-string">\'utf-8\'</span>, <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params">err, data</span>) </span>{\n  <span class="hljs-keyword">if</span> (err) <span class="hljs-keyword">throw</span> err;\n  <span class="hljs-built_in">console</span>.log(data);\n});</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>readFile</code>\u51fd\u6570\u7684\u7b2c\u4e09\u4e2a\u53c2\u6570\uff0c\u5c31\u662f\u56de\u8c03\u51fd\u6570\uff0c\u4e5f\u5c31\u662f\u4efb\u52a1\u7684\u7b2c\u4e8c\u6bb5\u3002\u7b49\u5230\u64cd\u4f5c\u7cfb\u7edf\u8fd4\u56de\u4e86<code>/etc/passwd</code>\u8fd9\u4e2a\u6587\u4ef6\u4ee5\u540e\uff0c\u56de\u8c03\u51fd\u6570\u624d\u4f1a\u6267\u884c\u3002</p>\n<p>\u4e00\u4e2a\u6709\u8da3\u7684\u95ee\u9898\u662f\uff0c\u4e3a\u4ec0\u4e48 Node \u7ea6\u5b9a\uff0c\u56de\u8c03\u51fd\u6570\u7684\u7b2c\u4e00\u4e2a\u53c2\u6570\uff0c\u5fc5\u987b\u662f\u9519\u8bef\u5bf9\u8c61<code>err</code>\uff08\u5982\u679c\u6ca1\u6709\u9519\u8bef\uff0c\u8be5\u53c2\u6570\u5c31\u662f<code>null</code>\uff09\uff1f</p>\n<p>\u539f\u56e0\u662f\u6267\u884c\u5206\u6210\u4e24\u6bb5\uff0c\u7b2c\u4e00\u6bb5\u6267\u884c\u5b8c\u4ee5\u540e\uff0c\u4efb\u52a1\u6240\u5728\u7684\u4e0a\u4e0b\u6587\u73af\u5883\u5c31\u5df2\u7ecf\u7ed3\u675f\u4e86\u3002\u5728\u8fd9\u4ee5\u540e\u629b\u51fa\u7684\u9519\u8bef\uff0c\u539f\u6765\u7684\u4e0a\u4e0b\u6587\u73af\u5883\u5df2\u7ecf\u65e0\u6cd5\u6355\u6349\uff0c\u53ea\u80fd\u5f53\u4f5c\u53c2\u6570\uff0c\u4f20\u5165\u7b2c\u4e8c\u6bb5\u3002</p>\n<h3 id="promise">Promise</h3>\n<p>\u56de\u8c03\u51fd\u6570\u672c\u8eab\u5e76\u6ca1\u6709\u95ee\u9898\uff0c\u5b83\u7684\u95ee\u9898\u51fa\u73b0\u5728\u591a\u4e2a\u56de\u8c03\u51fd\u6570\u5d4c\u5957\u3002\u5047\u5b9a\u8bfb\u53d6<code>A</code>\u6587\u4ef6\u4e4b\u540e\uff0c\u518d\u8bfb\u53d6<code>B</code>\u6587\u4ef6\uff0c\u4ee3\u7801\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript">fs.read<span class="hljs-constructor">File(<span class="hljs-params">fileA</span>, \'<span class="hljs-params">utf</span>-8\', <span class="hljs-params">function</span> (<span class="hljs-params">err</span>, <span class="hljs-params">data</span>)</span> {\n  fs.read<span class="hljs-constructor">File(<span class="hljs-params">fileB</span>, \'<span class="hljs-params">utf</span>-8\', <span class="hljs-params">function</span> (<span class="hljs-params">err</span>, <span class="hljs-params">data</span>)</span> {\n    <span class="hljs-comment">// ...</span>\n  });\n});</code></pre>\n<p>\u4e0d\u96be\u60f3\u8c61\uff0c\u5982\u679c\u4f9d\u6b21\u8bfb\u53d6\u4e24\u4e2a\u4ee5\u4e0a\u7684\u6587\u4ef6\uff0c\u5c31\u4f1a\u51fa\u73b0\u591a\u91cd\u5d4c\u5957\u3002\u4ee3\u7801\u4e0d\u662f\u7eb5\u5411\u53d1\u5c55\uff0c\u800c\u662f\u6a2a\u5411\u53d1\u5c55\uff0c\u5f88\u5feb\u5c31\u4f1a\u4e71\u6210\u4e00\u56e2\uff0c\u65e0\u6cd5\u7ba1\u7406\u3002\u56e0\u4e3a\u591a\u4e2a\u5f02\u6b65\u64cd\u4f5c\u5f62\u6210\u4e86\u5f3a\u8026\u5408\uff0c\u53ea\u8981\u6709\u4e00\u4e2a\u64cd\u4f5c\u9700\u8981\u4fee\u6539\uff0c\u5b83\u7684\u4e0a\u5c42\u56de\u8c03\u51fd\u6570\u548c\u4e0b\u5c42\u56de\u8c03\u51fd\u6570\uff0c\u53ef\u80fd\u90fd\u8981\u8ddf\u7740\u4fee\u6539\u3002\u8fd9\u79cd\u60c5\u51b5\u5c31\u79f0\u4e3a&quot;\u56de\u8c03\u51fd\u6570\u5730\u72f1&quot;\uff08callback hell\uff09\u3002</p>\n<p>Promise \u5bf9\u8c61\u5c31\u662f\u4e3a\u4e86\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\u800c\u63d0\u51fa\u7684\u3002\u5b83\u4e0d\u662f\u65b0\u7684\u8bed\u6cd5\u529f\u80fd\uff0c\u800c\u662f\u4e00\u79cd\u65b0\u7684\u5199\u6cd5\uff0c\u5141\u8bb8\u5c06\u56de\u8c03\u51fd\u6570\u7684\u5d4c\u5957\uff0c\u6539\u6210\u94fe\u5f0f\u8c03\u7528\u3002\u91c7\u7528 Promise\uff0c\u8fde\u7eed\u8bfb\u53d6\u591a\u4e2a\u6587\u4ef6\uff0c\u5199\u6cd5\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript">var readFile = <span class="hljs-built_in">require</span>(<span class="hljs-string">\'fs-readfile-promise\'</span>);\n\nreadFile(fileA)\n.<span class="hljs-keyword">then</span>(<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(data)</span></span> {\n  console.<span class="hljs-built_in">log</span>(data.toString());\n})\n.<span class="hljs-keyword">then</span>(<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">()</span></span> {\n  <span class="hljs-keyword">return</span> readFile(fileB);\n})\n.<span class="hljs-keyword">then</span>(<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(data)</span></span> {\n  console.<span class="hljs-built_in">log</span>(data.toString());\n})\n.catch(<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(err)</span></span> {\n  console.<span class="hljs-built_in">log</span>(err);\n});</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6211\u4f7f\u7528\u4e86<code>fs-readfile-promise</code>\u6a21\u5757\uff0c\u5b83\u7684\u4f5c\u7528\u5c31\u662f\u8fd4\u56de\u4e00\u4e2a Promise \u7248\u672c\u7684<code>readFile</code>\u51fd\u6570\u3002Promise \u63d0\u4f9b<code>then</code>\u65b9\u6cd5\u52a0\u8f7d\u56de\u8c03\u51fd\u6570\uff0c<code>catch</code>\u65b9\u6cd5\u6355\u6349\u6267\u884c\u8fc7\u7a0b\u4e2d\u629b\u51fa\u7684\u9519\u8bef\u3002</p>\n<p>\u53ef\u4ee5\u770b\u5230\uff0cPromise \u7684\u5199\u6cd5\u53ea\u662f\u56de\u8c03\u51fd\u6570\u7684\u6539\u8fdb\uff0c\u4f7f\u7528<code>then</code>\u65b9\u6cd5\u4ee5\u540e\uff0c\u5f02\u6b65\u4efb\u52a1\u7684\u4e24\u6bb5\u6267\u884c\u770b\u5f97\u66f4\u6e05\u695a\u4e86\uff0c\u9664\u6b64\u4ee5\u5916\uff0c\u5e76\u65e0\u65b0\u610f\u3002</p>\n<p>Promise \u7684\u6700\u5927\u95ee\u9898\u662f\u4ee3\u7801\u5197\u4f59\uff0c\u539f\u6765\u7684\u4efb\u52a1\u88ab Promise \u5305\u88c5\u4e86\u4e00\u4e0b\uff0c\u4e0d\u7ba1\u4ec0\u4e48\u64cd\u4f5c\uff0c\u4e00\u773c\u770b\u53bb\u90fd\u662f\u4e00\u5806<code>then</code>\uff0c\u539f\u6765\u7684\u8bed\u4e49\u53d8\u5f97\u5f88\u4e0d\u6e05\u695a\u3002</p>\n<p>\u90a3\u4e48\uff0c\u6709\u6ca1\u6709\u66f4\u597d\u7684\u5199\u6cd5\u5462\uff1f</p>\n<h2 id="generator-\u51fd\u6570">Generator \u51fd\u6570</h2>\n<h3 id="\u534f\u7a0b">\u534f\u7a0b</h3>\n<p>\u4f20\u7edf\u7684\u7f16\u7a0b\u8bed\u8a00\uff0c\u65e9\u6709\u5f02\u6b65\u7f16\u7a0b\u7684\u89e3\u51b3\u65b9\u6848\uff08\u5176\u5b9e\u662f\u591a\u4efb\u52a1\u7684\u89e3\u51b3\u65b9\u6848\uff09\u3002\u5176\u4e2d\u6709\u4e00\u79cd\u53eb\u505a&quot;\u534f\u7a0b&quot;\uff08coroutine\uff09\uff0c\u610f\u601d\u662f\u591a\u4e2a\u7ebf\u7a0b\u4e92\u76f8\u534f\u4f5c\uff0c\u5b8c\u6210\u5f02\u6b65\u4efb\u52a1\u3002</p>\n<p>\u534f\u7a0b\u6709\u70b9\u50cf\u51fd\u6570\uff0c\u53c8\u6709\u70b9\u50cf\u7ebf\u7a0b\u3002\u5b83\u7684\u8fd0\u884c\u6d41\u7a0b\u5927\u81f4\u5982\u4e0b\u3002</p>\n<ul>\n<li>\u7b2c\u4e00\u6b65\uff0c\u534f\u7a0b<code>A</code>\u5f00\u59cb\u6267\u884c\u3002</li>\n<li>\u7b2c\u4e8c\u6b65\uff0c\u534f\u7a0b<code>A</code>\u6267\u884c\u5230\u4e00\u534a\uff0c\u8fdb\u5165\u6682\u505c\uff0c\u6267\u884c\u6743\u8f6c\u79fb\u5230\u534f\u7a0b<code>B</code>\u3002</li>\n<li>\u7b2c\u4e09\u6b65\uff0c\uff08\u4e00\u6bb5\u65f6\u95f4\u540e\uff09\u534f\u7a0b<code>B</code>\u4ea4\u8fd8\u6267\u884c\u6743\u3002</li>\n<li>\u7b2c\u56db\u6b65\uff0c\u534f\u7a0b<code>A</code>\u6062\u590d\u6267\u884c\u3002</li>\n</ul>\n<p>\u4e0a\u9762\u6d41\u7a0b\u7684\u534f\u7a0b<code>A</code>\uff0c\u5c31\u662f\u5f02\u6b65\u4efb\u52a1\uff0c\u56e0\u4e3a\u5b83\u5206\u6210\u4e24\u6bb5\uff08\u6216\u591a\u6bb5\uff09\u6267\u884c\u3002</p>\n<p>\u4e3e\u4f8b\u6765\u8bf4\uff0c\u8bfb\u53d6\u6587\u4ef6\u7684\u534f\u7a0b\u5199\u6cd5\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span>* <span class="hljs-title">asyncJob</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-comment">// ...\u5176\u4ed6\u4ee3\u7801</span>\n  <span class="hljs-keyword">var</span> f = <span class="hljs-keyword">yield</span> readFile(fileA);\n  <span class="hljs-comment">// ...\u5176\u4ed6\u4ee3\u7801</span>\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684\u51fd\u6570<code>asyncJob</code>\u662f\u4e00\u4e2a\u534f\u7a0b\uff0c\u5b83\u7684\u5965\u5999\u5c31\u5728\u5176\u4e2d\u7684<code>yield</code>\u547d\u4ee4\u3002\u5b83\u8868\u793a\u6267\u884c\u5230\u6b64\u5904\uff0c\u6267\u884c\u6743\u5c06\u4ea4\u7ed9\u5176\u4ed6\u534f\u7a0b\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c<code>yield</code>\u547d\u4ee4\u662f\u5f02\u6b65\u4e24\u4e2a\u9636\u6bb5\u7684\u5206\u754c\u7ebf\u3002</p>\n<p>\u534f\u7a0b\u9047\u5230<code>yield</code>\u547d\u4ee4\u5c31\u6682\u505c\uff0c\u7b49\u5230\u6267\u884c\u6743\u8fd4\u56de\uff0c\u518d\u4ece\u6682\u505c\u7684\u5730\u65b9\u7ee7\u7eed\u5f80\u540e\u6267\u884c\u3002\u5b83\u7684\u6700\u5927\u4f18\u70b9\uff0c\u5c31\u662f\u4ee3\u7801\u7684\u5199\u6cd5\u975e\u5e38\u50cf\u540c\u6b65\u64cd\u4f5c\uff0c\u5982\u679c\u53bb\u9664<code>yield</code>\u547d\u4ee4\uff0c\u7b80\u76f4\u4e00\u6a21\u4e00\u6837\u3002</p>\n<h3 id="\u534f\u7a0b\u7684-generator-\u51fd\u6570\u5b9e\u73b0">\u534f\u7a0b\u7684 Generator \u51fd\u6570\u5b9e\u73b0</h3>\n<p>Generator \u51fd\u6570\u662f\u534f\u7a0b\u5728 ES6 \u7684\u5b9e\u73b0\uff0c\u6700\u5927\u7279\u70b9\u5c31\u662f\u53ef\u4ee5\u4ea4\u51fa\u51fd\u6570\u7684\u6267\u884c\u6743\uff08\u5373\u6682\u505c\u6267\u884c\uff09\u3002</p>\n<p>\u6574\u4e2a Generator \u51fd\u6570\u5c31\u662f\u4e00\u4e2a\u5c01\u88c5\u7684\u5f02\u6b65\u4efb\u52a1\uff0c\u6216\u8005\u8bf4\u662f\u5f02\u6b65\u4efb\u52a1\u7684\u5bb9\u5668\u3002\u5f02\u6b65\u64cd\u4f5c\u9700\u8981\u6682\u505c\u7684\u5730\u65b9\uff0c\u90fd\u7528<code>yield</code>\u8bed\u53e5\u6ce8\u660e\u3002Generator \u51fd\u6570\u7684\u6267\u884c\u65b9\u6cd5\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript">function* gen(<span class="hljs-symbol">x</span>) {\n  <span class="hljs-keywords">var</span> <span class="hljs-symbol">y</span> = yield <span class="hljs-symbol">x</span> + <span class="hljs-number">2</span>;\n  <span class="hljs-keywords">return</span> <span class="hljs-symbol">y</span>;\n}\n\n<span class="hljs-keywords">var</span> g = gen(<span class="hljs-number">1</span>);\ng.next() <span class="hljs-comment">// { value: 3, done: false }</span>\ng.next() <span class="hljs-comment">// { value: undefined, done: true }</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u8c03\u7528 Generator \u51fd\u6570\uff0c\u4f1a\u8fd4\u56de\u4e00\u4e2a\u5185\u90e8\u6307\u9488\uff08\u5373\u904d\u5386\u5668\uff09<code>g</code>\u3002\u8fd9\u662f Generator \u51fd\u6570\u4e0d\u540c\u4e8e\u666e\u901a\u51fd\u6570\u7684\u53e6\u4e00\u4e2a\u5730\u65b9\uff0c\u5373\u6267\u884c\u5b83\u4e0d\u4f1a\u8fd4\u56de\u7ed3\u679c\uff0c\u8fd4\u56de\u7684\u662f\u6307\u9488\u5bf9\u8c61\u3002\u8c03\u7528\u6307\u9488<code>g</code>\u7684<code>next</code>\u65b9\u6cd5\uff0c\u4f1a\u79fb\u52a8\u5185\u90e8\u6307\u9488\uff08\u5373\u6267\u884c\u5f02\u6b65\u4efb\u52a1\u7684\u7b2c\u4e00\u6bb5\uff09\uff0c\u6307\u5411\u7b2c\u4e00\u4e2a\u9047\u5230\u7684<code>yield</code>\u8bed\u53e5\uff0c\u4e0a\u4f8b\u662f\u6267\u884c\u5230<code>x + 2</code>\u4e3a\u6b62\u3002</p>\n<p>\u6362\u8a00\u4e4b\uff0c<code>next</code>\u65b9\u6cd5\u7684\u4f5c\u7528\u662f\u5206\u9636\u6bb5\u6267\u884c<code>Generator</code>\u51fd\u6570\u3002\u6bcf\u6b21\u8c03\u7528<code>next</code>\u65b9\u6cd5\uff0c\u4f1a\u8fd4\u56de\u4e00\u4e2a\u5bf9\u8c61\uff0c\u8868\u793a\u5f53\u524d\u9636\u6bb5\u7684\u4fe1\u606f\uff08<code>value</code>\u5c5e\u6027\u548c<code>done</code>\u5c5e\u6027\uff09\u3002<code>value</code>\u5c5e\u6027\u662f<code>yield</code>\u8bed\u53e5\u540e\u9762\u8868\u8fbe\u5f0f\u7684\u503c\uff0c\u8868\u793a\u5f53\u524d\u9636\u6bb5\u7684\u503c\uff1b<code>done</code>\u5c5e\u6027\u662f\u4e00\u4e2a\u5e03\u5c14\u503c\uff0c\u8868\u793a Generator \u51fd\u6570\u662f\u5426\u6267\u884c\u5b8c\u6bd5\uff0c\u5373\u662f\u5426\u8fd8\u6709\u4e0b\u4e00\u4e2a\u9636\u6bb5\u3002</p>\n<h3 id="generator-\u51fd\u6570\u7684\u6570\u636e\u4ea4\u6362\u548c\u9519\u8bef\u5904\u7406">Generator \u51fd\u6570\u7684\u6570\u636e\u4ea4\u6362\u548c\u9519\u8bef\u5904\u7406</h3>\n<p>Generator \u51fd\u6570\u53ef\u4ee5\u6682\u505c\u6267\u884c\u548c\u6062\u590d\u6267\u884c\uff0c\u8fd9\u662f\u5b83\u80fd\u5c01\u88c5\u5f02\u6b65\u4efb\u52a1\u7684\u6839\u672c\u539f\u56e0\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5b83\u8fd8\u6709\u4e24\u4e2a\u7279\u6027\uff0c\u4f7f\u5b83\u53ef\u4ee5\u4f5c\u4e3a\u5f02\u6b65\u7f16\u7a0b\u7684\u5b8c\u6574\u89e3\u51b3\u65b9\u6848\uff1a\u51fd\u6570\u4f53\u5185\u5916\u7684\u6570\u636e\u4ea4\u6362\u548c\u9519\u8bef\u5904\u7406\u673a\u5236\u3002</p>\n<p><code>next</code>\u8fd4\u56de\u503c\u7684 value \u5c5e\u6027\uff0c\u662f Generator \u51fd\u6570\u5411\u5916\u8f93\u51fa\u6570\u636e\uff1b<code>next</code>\u65b9\u6cd5\u8fd8\u53ef\u4ee5\u63a5\u53d7\u53c2\u6570\uff0c\u5411 Generator \u51fd\u6570\u4f53\u5185\u8f93\u5165\u6570\u636e\u3002</p>\n<pre><code class="language-javascript">function* gen(<span class="hljs-symbol">x</span>){\n  <span class="hljs-keywords">var</span> <span class="hljs-symbol">y</span> = yield <span class="hljs-symbol">x</span> + <span class="hljs-number">2</span>;\n  <span class="hljs-keywords">return</span> <span class="hljs-symbol">y</span>;\n}\n\n<span class="hljs-keywords">var</span> g = gen(<span class="hljs-number">1</span>);\ng.next() <span class="hljs-comment">// { value: 3, done: false }</span>\ng.next(<span class="hljs-number">2</span>) <span class="hljs-comment">// { value: 2, done: true }</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u7b2c\u4e00<code>next</code>\u65b9\u6cd5\u7684<code>value</code>\u5c5e\u6027\uff0c\u8fd4\u56de\u8868\u8fbe\u5f0f<code>x + 2</code>\u7684\u503c<code>3</code>\u3002\u7b2c\u4e8c\u4e2a<code>next</code>\u65b9\u6cd5\u5e26\u6709\u53c2\u6570<code>2</code>\uff0c\u8fd9\u4e2a\u53c2\u6570\u53ef\u4ee5\u4f20\u5165 Generator \u51fd\u6570\uff0c\u4f5c\u4e3a\u4e0a\u4e2a\u9636\u6bb5\u5f02\u6b65\u4efb\u52a1\u7684\u8fd4\u56de\u7ed3\u679c\uff0c\u88ab\u51fd\u6570\u4f53\u5185\u7684\u53d8\u91cf<code>y</code>\u63a5\u6536\u3002\u56e0\u6b64\uff0c\u8fd9\u4e00\u6b65\u7684<code>value</code>\u5c5e\u6027\uff0c\u8fd4\u56de\u7684\u5c31\u662f<code>2</code>\uff08\u53d8\u91cf<code>y</code>\u7684\u503c\uff09\u3002</p>\n<p>Generator \u51fd\u6570\u5185\u90e8\u8fd8\u53ef\u4ee5\u90e8\u7f72\u9519\u8bef\u5904\u7406\u4ee3\u7801\uff0c\u6355\u83b7\u51fd\u6570\u4f53\u5916\u629b\u51fa\u7684\u9519\u8bef\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span>* <span class="hljs-title">gen</span>(<span class="hljs-params">x</span>)</span>{\n  <span class="hljs-keyword">try</span> {\n    <span class="hljs-keyword">var</span> y = <span class="hljs-keyword">yield</span> x + <span class="hljs-number">2</span>;\n  } <span class="hljs-keyword">catch</span> (e){\n    <span class="hljs-built_in">console</span>.log(e);\n  }\n  <span class="hljs-keyword">return</span> y;\n}\n\n<span class="hljs-keyword">var</span> g = gen(<span class="hljs-number">1</span>);\ng.next();\ng.throw(<span class="hljs-string">\'\u51fa\u9519\u4e86\'</span>);\n<span class="hljs-comment">// \u51fa\u9519\u4e86</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684\u6700\u540e\u4e00\u884c\uff0cGenerator \u51fd\u6570\u4f53\u5916\uff0c\u4f7f\u7528\u6307\u9488\u5bf9\u8c61\u7684<code>throw</code>\u65b9\u6cd5\u629b\u51fa\u7684\u9519\u8bef\uff0c\u53ef\u4ee5\u88ab\u51fd\u6570\u4f53\u5185\u7684<code>try...catch</code>\u4ee3\u7801\u5757\u6355\u83b7\u3002\u8fd9\u610f\u5473\u7740\uff0c\u51fa\u9519\u7684\u4ee3\u7801\u4e0e\u5904\u7406\u9519\u8bef\u7684\u4ee3\u7801\uff0c\u5b9e\u73b0\u4e86\u65f6\u95f4\u548c\u7a7a\u95f4\u4e0a\u7684\u5206\u79bb\uff0c\u8fd9\u5bf9\u4e8e\u5f02\u6b65\u7f16\u7a0b\u65e0\u7591\u662f\u5f88\u91cd\u8981\u7684\u3002</p>\n<h3 id="\u5f02\u6b65\u4efb\u52a1\u7684\u5c01\u88c5">\u5f02\u6b65\u4efb\u52a1\u7684\u5c01\u88c5</h3>\n<p>\u4e0b\u9762\u770b\u770b\u5982\u4f55\u4f7f\u7528 Generator \u51fd\u6570\uff0c\u6267\u884c\u4e00\u4e2a\u771f\u5b9e\u7684\u5f02\u6b65\u4efb\u52a1\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> fetch = <span class="hljs-built_in">require</span>(<span class="hljs-string">\'node-fetch\'</span>);\n\n<span class="hljs-function"><span class="hljs-keyword">function</span>* <span class="hljs-title">gen</span>(<span class="hljs-params"></span>)</span>{\n  <span class="hljs-keyword">var</span> <span class="hljs-built_in">url</span> = <span class="hljs-string">\'https://api.github.com/users/github\'</span>;\n  <span class="hljs-keyword">var</span> result = <span class="hljs-keyword">yield</span> fetch(<span class="hljs-built_in">url</span>);\n  <span class="hljs-built_in">console</span>.log(result.bio);\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0cGenerator \u51fd\u6570\u5c01\u88c5\u4e86\u4e00\u4e2a\u5f02\u6b65\u64cd\u4f5c\uff0c\u8be5\u64cd\u4f5c\u5148\u8bfb\u53d6\u4e00\u4e2a\u8fdc\u7a0b\u63a5\u53e3\uff0c\u7136\u540e\u4ece JSON \u683c\u5f0f\u7684\u6570\u636e\u89e3\u6790\u4fe1\u606f\u3002\u5c31\u50cf\u524d\u9762\u8bf4\u8fc7\u7684\uff0c\u8fd9\u6bb5\u4ee3\u7801\u975e\u5e38\u50cf\u540c\u6b65\u64cd\u4f5c\uff0c\u9664\u4e86\u52a0\u4e0a\u4e86<code>yield</code>\u547d\u4ee4\u3002</p>\n<p>\u6267\u884c\u8fd9\u6bb5\u4ee3\u7801\u7684\u65b9\u6cd5\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript">var g = gen();\nvar result = g.next();\n\nresult.<span class="hljs-keyword">value</span>.<span class="hljs-keyword">then</span>(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(data)</span></span>{\n  <span class="hljs-keyword">return</span> <span class="hljs-keyword">data</span>.json();\n}).<span class="hljs-keyword">then</span>(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(data)</span></span>{\n  g.next(<span class="hljs-keyword">data</span>);\n});</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u9996\u5148\u6267\u884c Generator \u51fd\u6570\uff0c\u83b7\u53d6\u904d\u5386\u5668\u5bf9\u8c61\uff0c\u7136\u540e\u4f7f\u7528<code>next</code>\u65b9\u6cd5\uff08\u7b2c\u4e8c\u884c\uff09\uff0c\u6267\u884c\u5f02\u6b65\u4efb\u52a1\u7684\u7b2c\u4e00\u9636\u6bb5\u3002\u7531\u4e8e<code>Fetch</code>\u6a21\u5757\u8fd4\u56de\u7684\u662f\u4e00\u4e2a Promise \u5bf9\u8c61\uff0c\u56e0\u6b64\u8981\u7528<code>then</code>\u65b9\u6cd5\u8c03\u7528\u4e0b\u4e00\u4e2a<code>next</code>\u65b9\u6cd5\u3002</p>\n<p>\u53ef\u4ee5\u770b\u5230\uff0c\u867d\u7136 Generator \u51fd\u6570\u5c06\u5f02\u6b65\u64cd\u4f5c\u8868\u793a\u5f97\u5f88\u7b80\u6d01\uff0c\u4f46\u662f\u6d41\u7a0b\u7ba1\u7406\u5374\u4e0d\u65b9\u4fbf\uff08\u5373\u4f55\u65f6\u6267\u884c\u7b2c\u4e00\u9636\u6bb5\u3001\u4f55\u65f6\u6267\u884c\u7b2c\u4e8c\u9636\u6bb5\uff09\u3002</p>\n<h2 id="thunk-\u51fd\u6570">Thunk \u51fd\u6570</h2>\n<p>Thunk \u51fd\u6570\u662f\u81ea\u52a8\u6267\u884c Generator \u51fd\u6570\u7684\u4e00\u79cd\u65b9\u6cd5\u3002</p>\n<h3 id="\u53c2\u6570\u7684\u6c42\u503c\u7b56\u7565">\u53c2\u6570\u7684\u6c42\u503c\u7b56\u7565</h3>\n<p>Thunk \u51fd\u6570\u65e9\u5728\u4e0a\u4e2a\u4e16\u7eaa 60 \u5e74\u4ee3\u5c31\u8bde\u751f\u4e86\u3002</p>\n<p>\u90a3\u65f6\uff0c\u7f16\u7a0b\u8bed\u8a00\u521a\u521a\u8d77\u6b65\uff0c\u8ba1\u7b97\u673a\u5b66\u5bb6\u8fd8\u5728\u7814\u7a76\uff0c\u7f16\u8bd1\u5668\u600e\u4e48\u5199\u6bd4\u8f83\u597d\u3002\u4e00\u4e2a\u4e89\u8bba\u7684\u7126\u70b9\u662f&quot;\u6c42\u503c\u7b56\u7565&quot;\uff0c\u5373\u51fd\u6570\u7684\u53c2\u6570\u5230\u5e95\u5e94\u8be5\u4f55\u65f6\u6c42\u503c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> x = <span class="hljs-number">1</span>;\n\n<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span><span class="hljs-params">(m)</span></span>{\n  <span class="hljs-keyword">return</span> m * <span class="hljs-number">2</span>;\n}\n\nf(x + <span class="hljs-number">5</span>)</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u5148\u5b9a\u4e49\u51fd\u6570<code>f</code>\uff0c\u7136\u540e\u5411\u5b83\u4f20\u5165\u8868\u8fbe\u5f0f<code>x + 5</code>\u3002\u8bf7\u95ee\uff0c\u8fd9\u4e2a\u8868\u8fbe\u5f0f\u5e94\u8be5\u4f55\u65f6\u6c42\u503c\uff1f</p>\n<p>\u4e00\u79cd\u610f\u89c1\u662f&quot;\u4f20\u503c\u8c03\u7528&quot;\uff08call by value\uff09\uff0c\u5373\u5728\u8fdb\u5165\u51fd\u6570\u4f53\u4e4b\u524d\uff0c\u5c31\u8ba1\u7b97<code>x + 5</code>\u7684\u503c\uff08\u7b49\u4e8e 6\uff09\uff0c\u518d\u5c06\u8fd9\u4e2a\u503c\u4f20\u5165\u51fd\u6570<code>f</code>\u3002C \u8bed\u8a00\u5c31\u91c7\u7528\u8fd9\u79cd\u7b56\u7565\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-title">f</span><span class="hljs-params">(x + <span class="hljs-number">5</span>)</span></span>\n<span class="hljs-comment">// \u4f20\u503c\u8c03\u7528\u65f6\uff0c\u7b49\u540c\u4e8e</span>\n<span class="hljs-function"><span class="hljs-title">f</span><span class="hljs-params">(<span class="hljs-number">6</span>)</span></span></code></pre>\n<p>\u53e6\u4e00\u79cd\u610f\u89c1\u662f\u201c\u4f20\u540d\u8c03\u7528\u201d\uff08call by name\uff09\uff0c\u5373\u76f4\u63a5\u5c06\u8868\u8fbe\u5f0f<code>x + 5</code>\u4f20\u5165\u51fd\u6570\u4f53\uff0c\u53ea\u5728\u7528\u5230\u5b83\u7684\u65f6\u5019\u6c42\u503c\u3002Haskell \u8bed\u8a00\u91c7\u7528\u8fd9\u79cd\u7b56\u7565\u3002</p>\n<pre><code class="language-javascript">f(x + <span class="hljs-number">5</span>)\n<span class="hljs-comment">// \u4f20\u540d\u8c03\u7528\u65f6\uff0c\u7b49\u540c\u4e8e</span>\n(x + <span class="hljs-number">5</span>) * <span class="hljs-number">2</span></code></pre>\n<p>\u4f20\u503c\u8c03\u7528\u548c\u4f20\u540d\u8c03\u7528\uff0c\u54ea\u4e00\u79cd\u6bd4\u8f83\u597d\uff1f</p>\n<p>\u56de\u7b54\u662f\u5404\u6709\u5229\u5f0a\u3002\u4f20\u503c\u8c03\u7528\u6bd4\u8f83\u7b80\u5355\uff0c\u4f46\u662f\u5bf9\u53c2\u6570\u6c42\u503c\u7684\u65f6\u5019\uff0c\u5b9e\u9645\u4e0a\u8fd8\u6ca1\u7528\u5230\u8fd9\u4e2a\u53c2\u6570\uff0c\u6709\u53ef\u80fd\u9020\u6210\u6027\u80fd\u635f\u5931\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(a, b){\n  <span class="hljs-keyword">return</span> <span class="hljs-type">b</span>;\n}\n\nf(<span class="hljs-number">3</span> * x * x - <span class="hljs-number">2</span> * x - <span class="hljs-number">1</span>, x);</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570<code>f</code>\u7684\u7b2c\u4e00\u4e2a\u53c2\u6570\u662f\u4e00\u4e2a\u590d\u6742\u7684\u8868\u8fbe\u5f0f\uff0c\u4f46\u662f\u51fd\u6570\u4f53\u5185\u6839\u672c\u6ca1\u7528\u5230\u3002\u5bf9\u8fd9\u4e2a\u53c2\u6570\u6c42\u503c\uff0c\u5b9e\u9645\u4e0a\u662f\u4e0d\u5fc5\u8981\u7684\u3002\u56e0\u6b64\uff0c\u6709\u4e00\u4e9b\u8ba1\u7b97\u673a\u5b66\u5bb6\u503e\u5411\u4e8e&quot;\u4f20\u540d\u8c03\u7528&quot;\uff0c\u5373\u53ea\u5728\u6267\u884c\u65f6\u6c42\u503c\u3002</p>\n<h3 id="thunk-\u51fd\u6570\u7684\u542b\u4e49">Thunk \u51fd\u6570\u7684\u542b\u4e49</h3>\n<p>\u7f16\u8bd1\u5668\u7684\u201c\u4f20\u540d\u8c03\u7528\u201d\u5b9e\u73b0\uff0c\u5f80\u5f80\u662f\u5c06\u53c2\u6570\u653e\u5230\u4e00\u4e2a\u4e34\u65f6\u51fd\u6570\u4e4b\u4e2d\uff0c\u518d\u5c06\u8fd9\u4e2a\u4e34\u65f6\u51fd\u6570\u4f20\u5165\u51fd\u6570\u4f53\u3002\u8fd9\u4e2a\u4e34\u65f6\u51fd\u6570\u5c31\u53eb\u505a Thunk \u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(m) {\n  <span class="hljs-keyword">return</span> <span class="hljs-type">m</span> * <span class="hljs-number">2</span>;\n}\n\nf(x + <span class="hljs-number">5</span>);\n\n// \u7b49\u540c\u4e8e\n\nvar thunk = <span class="hljs-keyword">function</span> <span class="hljs-title"></span>() {\n  <span class="hljs-keyword">return</span> <span class="hljs-type">x</span> + <span class="hljs-number">5</span>;\n};\n\n<span class="hljs-keyword">function</span> <span class="hljs-title">f</span>(thunk) {\n  <span class="hljs-keyword">return</span> <span class="hljs-type">thunk()</span> * <span class="hljs-number">2</span>;\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570 f \u7684\u53c2\u6570<code>x + 5</code>\u88ab\u4e00\u4e2a\u51fd\u6570\u66ff\u6362\u4e86\u3002\u51e1\u662f\u7528\u5230\u539f\u53c2\u6570\u7684\u5730\u65b9\uff0c\u5bf9<code>Thunk</code>\u51fd\u6570\u6c42\u503c\u5373\u53ef\u3002</p>\n<p>\u8fd9\u5c31\u662f Thunk \u51fd\u6570\u7684\u5b9a\u4e49\uff0c\u5b83\u662f\u201c\u4f20\u540d\u8c03\u7528\u201d\u7684\u4e00\u79cd\u5b9e\u73b0\u7b56\u7565\uff0c\u7528\u6765\u66ff\u6362\u67d0\u4e2a\u8868\u8fbe\u5f0f\u3002</p>\n<h3 id="javascript-\u8bed\u8a00\u7684-thunk-\u51fd\u6570">JavaScript \u8bed\u8a00\u7684 Thunk \u51fd\u6570</h3>\n<p>JavaScript \u8bed\u8a00\u662f\u4f20\u503c\u8c03\u7528\uff0c\u5b83\u7684 Thunk \u51fd\u6570\u542b\u4e49\u6709\u6240\u4e0d\u540c\u3002\u5728 JavaScript \u8bed\u8a00\u4e2d\uff0cThunk \u51fd\u6570\u66ff\u6362\u7684\u4e0d\u662f\u8868\u8fbe\u5f0f\uff0c\u800c\u662f\u591a\u53c2\u6570\u51fd\u6570\uff0c\u5c06\u5176\u66ff\u6362\u6210\u4e00\u4e2a\u53ea\u63a5\u53d7\u56de\u8c03\u51fd\u6570\u4f5c\u4e3a\u53c2\u6570\u7684\u5355\u53c2\u6570\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u6b63\u5e38\u7248\u672c\u7684readFile\uff08\u591a\u53c2\u6570\u7248\u672c\uff09</span>\nfs.read<span class="hljs-constructor">File(<span class="hljs-params">fileName</span>, <span class="hljs-params">callback</span>)</span>;\n\n<span class="hljs-comment">// Thunk\u7248\u672c\u7684readFile\uff08\u5355\u53c2\u6570\u7248\u672c\uff09</span>\nvar Thunk = <span class="hljs-keyword">function</span> (fileName) {\n  return <span class="hljs-keyword">function</span> (callback) {\n    return fs.read<span class="hljs-constructor">File(<span class="hljs-params">fileName</span>, <span class="hljs-params">callback</span>)</span>;\n  };\n};\n\nvar readFileThunk = <span class="hljs-constructor">Thunk(<span class="hljs-params">fileName</span>)</span>;\nread<span class="hljs-constructor">FileThunk(<span class="hljs-params">callback</span>)</span>;</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>fs</code>\u6a21\u5757\u7684<code>readFile</code>\u65b9\u6cd5\u662f\u4e00\u4e2a\u591a\u53c2\u6570\u51fd\u6570\uff0c\u4e24\u4e2a\u53c2\u6570\u5206\u522b\u4e3a\u6587\u4ef6\u540d\u548c\u56de\u8c03\u51fd\u6570\u3002\u7ecf\u8fc7\u8f6c\u6362\u5668\u5904\u7406\uff0c\u5b83\u53d8\u6210\u4e86\u4e00\u4e2a\u5355\u53c2\u6570\u51fd\u6570\uff0c\u53ea\u63a5\u53d7\u56de\u8c03\u51fd\u6570\u4f5c\u4e3a\u53c2\u6570\u3002\u8fd9\u4e2a\u5355\u53c2\u6570\u7248\u672c\uff0c\u5c31\u53eb\u505a Thunk \u51fd\u6570\u3002</p>\n<p>\u4efb\u4f55\u51fd\u6570\uff0c\u53ea\u8981\u53c2\u6570\u6709\u56de\u8c03\u51fd\u6570\uff0c\u5c31\u80fd\u5199\u6210 Thunk \u51fd\u6570\u7684\u5f62\u5f0f\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u7b80\u5355\u7684 Thunk \u51fd\u6570\u8f6c\u6362\u5668\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// ES5\u7248\u672c</span>\n<span class="hljs-keyword">var</span> Thunk = <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(fn)</span></span>{\n  <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">()</span></span>{\n    <span class="hljs-keyword">var</span> args = Array.prototype.slice.call(arguments);\n    <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(callback)</span></span>{\n      args.push(callback);\n      <span class="hljs-keyword">return</span> fn.apply(<span class="hljs-keyword">this</span>, args);\n    }\n  };\n};\n\n<span class="hljs-comment">// ES6\u7248\u672c</span>\n<span class="hljs-keyword">const</span> Thunk = <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(fn)</span> </span>{\n  <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(<span class="hljs-rest_arg">...args</span>)</span> </span>{\n    <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(callback)</span> </span>{\n      <span class="hljs-keyword">return</span> fn.call(<span class="hljs-keyword">this</span>, ...args, callback);\n    }\n  };\n};</code></pre>\n<p>\u4f7f\u7528\u4e0a\u9762\u7684\u8f6c\u6362\u5668\uff0c\u751f\u6210<code>fs.readFile</code>\u7684 Thunk \u51fd\u6570\u3002</p>\n<pre><code class="language-javascript">var readFileThunk = Thunk(<span class="hljs-name">fs</span>.readFile)<span class="hljs-comment">;</span>\nreadFileThunk(<span class="hljs-name">fileA</span>)(<span class="hljs-name">callback</span>)<span class="hljs-comment">;</span></code></pre>\n<p>\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u5b8c\u6574\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">f</span><span class="hljs-params">(a, cb)</span> <span class="hljs-comment">{\n  cb(a);\n}</span>\n<span class="hljs-title">const</span> <span class="hljs-title">ft</span> = <span class="hljs-title">Thunk</span><span class="hljs-params">(f)</span>;</span>\n\nft(<span class="hljs-number">1</span>)(console.log) <span class="hljs-comment">// 1</span></code></pre>\n<h3 id="thunkify-\u6a21\u5757">Thunkify \u6a21\u5757</h3>\n<p>\u751f\u4ea7\u73af\u5883\u7684\u8f6c\u6362\u5668\uff0c\u5efa\u8bae\u4f7f\u7528 Thunkify \u6a21\u5757\u3002</p>\n<p>\u9996\u5148\u662f\u5b89\u88c5\u3002</p>\n<pre><code class="language-bash">$ npm <span class="hljs-keyword">install</span> thunkify</code></pre>\n<p>\u4f7f\u7528\u65b9\u5f0f\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> thunkify = <span class="hljs-keyword">require</span>(<span class="hljs-string">\'thunkify\'</span>);\n<span class="hljs-keyword">var</span> fs = <span class="hljs-keyword">require</span>(<span class="hljs-string">\'fs\'</span>);\n\n<span class="hljs-keyword">var</span> <span class="hljs-keyword">read</span> = thunkify(fs.readFile);\n<span class="hljs-keyword">read</span>(<span class="hljs-string">\'package.json\'</span>)(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(err, str)</span><span class="hljs-comment">{\n  // ...\n}</span>);</span></code></pre>\n<p>Thunkify \u7684\u6e90\u7801\u4e0e\u4e0a\u4e00\u8282\u90a3\u4e2a\u7b80\u5355\u7684\u8f6c\u6362\u5668\u975e\u5e38\u50cf\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">thunkify</span>(<span class="hljs-params">fn</span>) </span>{\n  <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params"></span>) </span>{\n    <span class="hljs-keyword">var</span> args = <span class="hljs-keyword">new</span> <span class="hljs-built_in">Array</span>(<span class="hljs-built_in">arguments</span>.length);\n    <span class="hljs-keyword">var</span> ctx = <span class="hljs-keyword">this</span>;\n\n    <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> i = <span class="hljs-number">0</span>; i &lt; args.length; ++i) {\n      args[i] = <span class="hljs-built_in">arguments</span>[i];\n    }\n\n    <span class="hljs-keyword">return</span> <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params">done</span>) </span>{\n      <span class="hljs-keyword">var</span> called;\n\n      args.push(<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n        <span class="hljs-keyword">if</span> (called) <span class="hljs-keyword">return</span>;\n        called = <span class="hljs-literal">true</span>;\n        done.apply(<span class="hljs-literal">null</span>, <span class="hljs-built_in">arguments</span>);\n      });\n\n      <span class="hljs-keyword">try</span> {\n        fn.apply(ctx, args);\n      } <span class="hljs-keyword">catch</span> (err) {\n        done(err);\n      }\n    }\n  }\n};</code></pre>\n<p>\u5b83\u7684\u6e90\u7801\u4e3b\u8981\u591a\u4e86\u4e00\u4e2a\u68c0\u67e5\u673a\u5236\uff0c\u53d8\u91cf<code>called</code>\u786e\u4fdd\u56de\u8c03\u51fd\u6570\u53ea\u8fd0\u884c\u4e00\u6b21\u3002\u8fd9\u6837\u7684\u8bbe\u8ba1\u4e0e\u4e0b\u6587\u7684 Generator \u51fd\u6570\u76f8\u5173\u3002\u8bf7\u770b\u4e0b\u9762\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript">function <span class="hljs-built_in">f</span>(a, b, callback){\n  <span class="hljs-keyword">var</span> <span class="hljs-keyword">sum</span> = a + b;\n  callback(<span class="hljs-keyword">sum</span>);\n  callback(<span class="hljs-keyword">sum</span>);\n}\n\n<span class="hljs-keyword">var</span> ft = thunkify(f);\n<span class="hljs-keyword">var</span> <span class="hljs-keyword">print</span> = console.<span class="hljs-keyword">log</span>.bind(console);\nft(1, 2)(<span class="hljs-keyword">print</span>);\n<span class="hljs-comment">// 3</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u7531\u4e8e<code>thunkify</code>\u53ea\u5141\u8bb8\u56de\u8c03\u51fd\u6570\u6267\u884c\u4e00\u6b21\uff0c\u6240\u4ee5\u53ea\u8f93\u51fa\u4e00\u884c\u7ed3\u679c\u3002</p>\n<h3 id="generator-\u51fd\u6570\u7684\u6d41\u7a0b\u7ba1\u7406">Generator \u51fd\u6570\u7684\u6d41\u7a0b\u7ba1\u7406</h3>\n<p>\u4f60\u53ef\u80fd\u4f1a\u95ee\uff0c Thunk \u51fd\u6570\u6709\u4ec0\u4e48\u7528\uff1f\u56de\u7b54\u662f\u4ee5\u524d\u786e\u5b9e\u6ca1\u4ec0\u4e48\u7528\uff0c\u4f46\u662f ES6 \u6709\u4e86 Generator \u51fd\u6570\uff0cThunk \u51fd\u6570\u73b0\u5728\u53ef\u4ee5\u7528\u4e8e Generator \u51fd\u6570\u7684\u81ea\u52a8\u6d41\u7a0b\u7ba1\u7406\u3002</p>\n<p>Generator \u51fd\u6570\u53ef\u4ee5\u81ea\u52a8\u6267\u884c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span>* <span class="hljs-title">gen</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-comment">// ...</span>\n}\n\n<span class="hljs-keyword">var</span> g = gen();\n<span class="hljs-keyword">var</span> res = g.next();\n\n<span class="hljs-keyword">while</span>(!res.done){\n  <span class="hljs-built_in">console</span>.log(res.value);\n  res = g.next();\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0cGenerator \u51fd\u6570<code>gen</code>\u4f1a\u81ea\u52a8\u6267\u884c\u5b8c\u6240\u6709\u6b65\u9aa4\u3002</p>\n<p>\u4f46\u662f\uff0c\u8fd9\u4e0d\u9002\u5408\u5f02\u6b65\u64cd\u4f5c\u3002\u5982\u679c\u5fc5\u987b\u4fdd\u8bc1\u524d\u4e00\u6b65\u6267\u884c\u5b8c\uff0c\u624d\u80fd\u6267\u884c\u540e\u4e00\u6b65\uff0c\u4e0a\u9762\u7684\u81ea\u52a8\u6267\u884c\u5c31\u4e0d\u53ef\u884c\u3002\u8fd9\u65f6\uff0cThunk \u51fd\u6570\u5c31\u80fd\u6d3e\u4e0a\u7528\u5904\u3002\u4ee5\u8bfb\u53d6\u6587\u4ef6\u4e3a\u4f8b\u3002\u4e0b\u9762\u7684 Generator \u51fd\u6570\u5c01\u88c5\u4e86\u4e24\u4e2a\u5f02\u6b65\u64cd\u4f5c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> fs = <span class="hljs-built_in">require</span>(<span class="hljs-string">\'fs\'</span>);\n<span class="hljs-keyword">var</span> thunkify = <span class="hljs-built_in">require</span>(<span class="hljs-string">\'thunkify\'</span>);\n<span class="hljs-keyword">var</span> readFileThunk = thunkify(fs.readFile);\n\n<span class="hljs-keyword">var</span> gen = <span class="hljs-function"><span class="hljs-keyword">function</span>* (<span class="hljs-params"></span>)</span>{\n  <span class="hljs-keyword">var</span> r1 = <span class="hljs-keyword">yield</span> readFileThunk(<span class="hljs-string">\'/etc/fstab\'</span>);\n  <span class="hljs-built_in">console</span>.log(r1.toString());\n  <span class="hljs-keyword">var</span> r2 = <span class="hljs-keyword">yield</span> readFileThunk(<span class="hljs-string">\'/etc/shells\'</span>);\n  <span class="hljs-built_in">console</span>.log(r2.toString());\n};</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>yield</code>\u547d\u4ee4\u7528\u4e8e\u5c06\u7a0b\u5e8f\u7684\u6267\u884c\u6743\u79fb\u51fa Generator \u51fd\u6570\uff0c\u90a3\u4e48\u5c31\u9700\u8981\u4e00\u79cd\u65b9\u6cd5\uff0c\u5c06\u6267\u884c\u6743\u518d\u4ea4\u8fd8\u7ed9 Generator \u51fd\u6570\u3002</p>\n<p>\u8fd9\u79cd\u65b9\u6cd5\u5c31\u662f Thunk \u51fd\u6570\uff0c\u56e0\u4e3a\u5b83\u53ef\u4ee5\u5728\u56de\u8c03\u51fd\u6570\u91cc\uff0c\u5c06\u6267\u884c\u6743\u4ea4\u8fd8\u7ed9 Generator \u51fd\u6570\u3002\u4e3a\u4e86\u4fbf\u4e8e\u7406\u89e3\uff0c\u6211\u4eec\u5148\u770b\u5982\u4f55\u624b\u52a8\u6267\u884c\u4e0a\u9762\u8fd9\u4e2a Generator \u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> g = gen();\n\n<span class="hljs-keyword">var</span> r1 = g.next();\nr1.value(<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(err, data)</span> </span>{\n  <span class="hljs-keyword">if</span> (err) <span class="hljs-keyword">throw</span> err;\n  <span class="hljs-keyword">var</span> r2 = g.next(data);\n  r2.value(<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">(err, data)</span> </span>{\n    <span class="hljs-keyword">if</span> (err) <span class="hljs-keyword">throw</span> err;\n    g.next(data);\n  });\n});</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>g</code>\u662f Generator \u51fd\u6570\u7684\u5185\u90e8\u6307\u9488\uff0c\u8868\u793a\u76ee\u524d\u6267\u884c\u5230\u54ea\u4e00\u6b65\u3002<code>next</code>\u65b9\u6cd5\u8d1f\u8d23\u5c06\u6307\u9488\u79fb\u52a8\u5230\u4e0b\u4e00\u6b65\uff0c\u5e76\u8fd4\u56de\u8be5\u6b65\u7684\u4fe1\u606f\uff08<code>value</code>\u5c5e\u6027\u548c<code>done</code>\u5c5e\u6027\uff09\u3002</p>\n<p>\u4ed4\u7ec6\u67e5\u770b\u4e0a\u9762\u7684\u4ee3\u7801\uff0c\u53ef\u4ee5\u53d1\u73b0 Generator \u51fd\u6570\u7684\u6267\u884c\u8fc7\u7a0b\uff0c\u5176\u5b9e\u662f\u5c06\u540c\u4e00\u4e2a\u56de\u8c03\u51fd\u6570\uff0c\u53cd\u590d\u4f20\u5165<code>next</code>\u65b9\u6cd5\u7684<code>value</code>\u5c5e\u6027\u3002\u8fd9\u4f7f\u5f97\u6211\u4eec\u53ef\u4ee5\u7528\u9012\u5f52\u6765\u81ea\u52a8\u5b8c\u6210\u8fd9\u4e2a\u8fc7\u7a0b\u3002</p>\n<h3 id="thunk-\u51fd\u6570\u7684\u81ea\u52a8\u6d41\u7a0b\u7ba1\u7406">Thunk \u51fd\u6570\u7684\u81ea\u52a8\u6d41\u7a0b\u7ba1\u7406</h3>\n<p>Thunk \u51fd\u6570\u771f\u6b63\u7684\u5a01\u529b\uff0c\u5728\u4e8e\u53ef\u4ee5\u81ea\u52a8\u6267\u884c Generator \u51fd\u6570\u3002\u4e0b\u9762\u5c31\u662f\u4e00\u4e2a\u57fa\u4e8e Thunk \u51fd\u6570\u7684 Generator \u6267\u884c\u5668\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">run</span>(<span class="hljs-params">fn</span>) </span>{\n  <span class="hljs-keyword">var</span> gen = fn();\n\n  <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">next</span>(<span class="hljs-params">err, data</span>) </span>{\n    <span class="hljs-keyword">var</span> result = gen.next(data);\n    <span class="hljs-keyword">if</span> (result.done) <span class="hljs-keyword">return</span>;\n    result.value(next);\n  }\n\n  next();\n}\n\n<span class="hljs-function"><span class="hljs-keyword">function</span>* <span class="hljs-title">g</span>(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-comment">// ...</span>\n}\n\nrun(g);</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684<code>run</code>\u51fd\u6570\uff0c\u5c31\u662f\u4e00\u4e2a Generator \u51fd\u6570\u7684\u81ea\u52a8\u6267\u884c\u5668\u3002\u5185\u90e8\u7684<code>next</code>\u51fd\u6570\u5c31\u662f Thunk \u7684\u56de\u8c03\u51fd\u6570\u3002<code>next</code>\u51fd\u6570\u5148\u5c06\u6307\u9488\u79fb\u5230 Generator \u51fd\u6570\u7684\u4e0b\u4e00\u6b65\uff08<code>gen.next</code>\u65b9\u6cd5\uff09\uff0c\u7136\u540e\u5224\u65ad Generator \u51fd\u6570\u662f\u5426\u7ed3\u675f\uff08<code>result.done</code>\u5c5e\u6027\uff09\uff0c\u5982\u679c\u6ca1\u7ed3\u675f\uff0c\u5c31\u5c06<code>next</code>\u51fd\u6570\u518d\u4f20\u5165 Thunk \u51fd\u6570\uff08<code>result.value</code>\u5c5e\u6027\uff09\uff0c\u5426\u5219\u5c31\u76f4\u63a5\u9000\u51fa\u3002</p>\n<p>\u6709\u4e86\u8fd9\u4e2a\u6267\u884c\u5668\uff0c\u6267\u884c Generator \u51fd\u6570\u65b9\u4fbf\u591a\u4e86\u3002\u4e0d\u7ba1\u5185\u90e8\u6709\u591a\u5c11\u4e2a\u5f02\u6b65\u64cd\u4f5c\uff0c\u76f4\u63a5\u628a Generator \u51fd\u6570\u4f20\u5165<code>run</code>\u51fd\u6570\u5373\u53ef\u3002\u5f53\u7136\uff0c\u524d\u63d0\u662f\u6bcf\u4e00\u4e2a\u5f02\u6b65\u64cd\u4f5c\uff0c\u90fd\u8981\u662f Thunk \u51fd\u6570\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u8ddf\u5728<code>yield</code>\u547d\u4ee4\u540e\u9762\u7684\u5fc5\u987b\u662f Thunk \u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> g = function* (){\n  <span class="hljs-keyword">var</span> f1 = <span class="hljs-function"><span class="hljs-keyword">yield</span> <span class="hljs-title">readFileThunk</span>(<span class="hljs-params"><span class="hljs-string">\'fileA\'</span></span>)</span>;\n  <span class="hljs-keyword">var</span> f2 = <span class="hljs-function"><span class="hljs-keyword">yield</span> <span class="hljs-title">readFileThunk</span>(<span class="hljs-params"><span class="hljs-string">\'fileB\'</span></span>)</span>;\n  <span class="hljs-comment">// ...</span>\n  <span class="hljs-keyword">var</span> fn = <span class="hljs-function"><span class="hljs-keyword">yield</span> <span class="hljs-title">readFileThunk</span>(<span class="hljs-params"><span class="hljs-string">\'fileN\'</span></span>)</span>;\n};\n\nrun(g);</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570<code>g</code>\u5c01\u88c5\u4e86<code>n</code>\u4e2a\u5f02\u6b65\u7684\u8bfb\u53d6\u6587\u4ef6\u64cd\u4f5c\uff0c\u53ea\u8981\u6267\u884c<code>run</code>\u51fd\u6570\uff0c\u8fd9\u4e9b\u64cd\u4f5c\u5c31\u4f1a\u81ea\u52a8\u5b8c\u6210\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u5f02\u6b65\u64cd\u4f5c\u4e0d\u4ec5\u53ef\u4ee5\u5199\u5f97\u50cf\u540c\u6b65\u64cd\u4f5c\uff0c\u800c\u4e14\u4e00\u884c\u4ee3\u7801\u5c31\u53ef\u4ee5\u6267\u884c\u3002</p>\n<p>Thunk \u51fd\u6570\u5e76\u4e0d\u662f Generator \u51fd\u6570\u81ea\u52a8\u6267\u884c\u7684\u552f\u4e00\u65b9\u6848\u3002\u56e0\u4e3a\u81ea\u52a8\u6267\u884c\u7684\u5173\u952e\u662f\uff0c\u5fc5\u987b\u6709\u4e00\u79cd\u673a\u5236\uff0c\u81ea\u52a8\u63a7\u5236 Generator \u51fd\u6570\u7684\u6d41\u7a0b\uff0c\u63a5\u6536\u548c\u4ea4\u8fd8\u7a0b\u5e8f\u7684\u6267\u884c\u6743\u3002\u56de\u8c03\u51fd\u6570\u53ef\u4ee5\u505a\u5230\u8fd9\u4e00\u70b9\uff0cPromise \u5bf9\u8c61\u4e5f\u53ef\u4ee5\u505a\u5230\u8fd9\u4e00\u70b9\u3002</p>\n<h2 id="co-\u6a21\u5757">co \u6a21\u5757</h2>\n<h3 id="\u57fa\u672c\u7528\u6cd5">\u57fa\u672c\u7528\u6cd5</h3>\n<p><a href="https://github.com/tj/co">co \u6a21\u5757</a>\u662f\u8457\u540d\u7a0b\u5e8f\u5458 TJ Holowaychuk \u4e8e 2013 \u5e74 6 \u6708\u53d1\u5e03\u7684\u4e00\u4e2a\u5c0f\u5de5\u5177\uff0c\u7528\u4e8e Generator \u51fd\u6570\u7684\u81ea\u52a8\u6267\u884c\u3002</p>\n<p>\u4e0b\u9762\u662f\u4e00\u4e2a Generator \u51fd\u6570\uff0c\u7528\u4e8e\u4f9d\u6b21\u8bfb\u53d6\u4e24\u4e2a\u6587\u4ef6\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> gen = <span class="hljs-function"><span class="hljs-keyword">function</span>* (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">var</span> f1 = <span class="hljs-keyword">yield</span> readFile(<span class="hljs-string">\'/etc/fstab\'</span>);\n  <span class="hljs-keyword">var</span> f2 = <span class="hljs-keyword">yield</span> readFile(<span class="hljs-string">\'/etc/shells\'</span>);\n  <span class="hljs-built_in">console</span>.log(f1.toString());\n  <span class="hljs-built_in">console</span>.log(f2.toString());\n};</code></pre>\n<p>co \u6a21\u5757\u53ef\u4ee5\u8ba9\u4f60\u4e0d\u7528\u7f16\u5199 Generator \u51fd\u6570\u7684\u6267\u884c\u5668\u3002</p>\n<pre><code class="language-javascript">var co = require(\'co\')<span class="hljs-comment">;</span>\nco(<span class="hljs-name">gen</span>)<span class="hljs-comment">;</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0cGenerator \u51fd\u6570\u53ea\u8981\u4f20\u5165<code>co</code>\u51fd\u6570\uff0c\u5c31\u4f1a\u81ea\u52a8\u6267\u884c\u3002</p>\n<p><code>co</code>\u51fd\u6570\u8fd4\u56de\u4e00\u4e2a<code>Promise</code>\u5bf9\u8c61\uff0c\u56e0\u6b64\u53ef\u4ee5\u7528<code>then</code>\u65b9\u6cd5\u6dfb\u52a0\u56de\u8c03\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript">co(gen).<span class="hljs-keyword">then</span>(<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-params">()</span></span>{\n  console.<span class="hljs-built_in">log</span>(<span class="hljs-string">\'Generator \u51fd\u6570\u6267\u884c\u5b8c\u6210\'</span>);\n});</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u7b49\u5230 Generator \u51fd\u6570\u6267\u884c\u7ed3\u675f\uff0c\u5c31\u4f1a\u8f93\u51fa\u4e00\u884c\u63d0\u793a\u3002</p>\n<h3 id="co-\u6a21\u5757\u7684\u539f\u7406">co \u6a21\u5757\u7684\u539f\u7406</h3>\n<p>\u4e3a\u4ec0\u4e48 co \u53ef\u4ee5\u81ea\u52a8\u6267\u884c Generator \u51fd\u6570\uff1f</p>\n<p>\u524d\u9762\u8bf4\u8fc7\uff0cGenerator \u5c31\u662f\u4e00\u4e2a\u5f02\u6b65\u64cd\u4f5c\u7684\u5bb9\u5668\u3002\u5b83\u7684\u81ea\u52a8\u6267\u884c\u9700\u8981\u4e00\u79cd\u673a\u5236\uff0c\u5f53\u5f02\u6b65\u64cd\u4f5c\u6709\u4e86\u7ed3\u679c\uff0c\u80fd\u591f\u81ea\u52a8\u4ea4\u56de\u6267\u884c\u6743\u3002</p>\n<p>\u4e24\u79cd\u65b9\u6cd5\u53ef\u4ee5\u505a\u5230\u8fd9\u4e00\u70b9\u3002</p>\n<p>\uff081\uff09\u56de\u8c03\u51fd\u6570\u3002\u5c06\u5f02\u6b65\u64cd\u4f5c\u5305\u88c5\u6210 Thunk \u51fd\u6570\uff0c\u5728\u56de\u8c03\u51fd\u6570\u91cc\u9762\u4ea4\u56de\u6267\u884c\u6743\u3002</p>\n<p>\uff082\uff09Promise \u5bf9\u8c61\u3002\u5c06\u5f02\u6b65\u64cd\u4f5c\u5305\u88c5\u6210 Promise \u5bf9\u8c61\uff0c\u7528<code>then</code>\u65b9\u6cd5\u4ea4\u56de\u6267\u884c\u6743\u3002</p>\n<p>co \u6a21\u5757\u5176\u5b9e\u5c31\u662f\u5c06\u4e24\u79cd\u81ea\u52a8\u6267\u884c\u5668\uff08Thunk \u51fd\u6570\u548c Promise \u5bf9\u8c61\uff09\uff0c\u5305\u88c5\u6210\u4e00\u4e2a\u6a21\u5757\u3002\u4f7f\u7528 co \u7684\u524d\u63d0\u6761\u4ef6\u662f\uff0cGenerator \u51fd\u6570\u7684<code>yield</code>\u547d\u4ee4\u540e\u9762\uff0c\u53ea\u80fd\u662f Thunk \u51fd\u6570\u6216 Promise \u5bf9\u8c61\u3002\u5982\u679c\u6570\u7ec4\u6216\u5bf9\u8c61\u7684\u6210\u5458\uff0c\u5168\u90e8\u90fd\u662f Promise \u5bf9\u8c61\uff0c\u4e5f\u53ef\u4ee5\u4f7f\u7528 co\uff0c\u8be6\u89c1\u540e\u6587\u7684\u4f8b\u5b50\u3002</p>\n<p>\u4e0a\u4e00\u8282\u5df2\u7ecf\u4ecb\u7ecd\u4e86\u57fa\u4e8e Thunk \u51fd\u6570\u7684\u81ea\u52a8\u6267\u884c\u5668\u3002\u4e0b\u9762\u6765\u770b\uff0c\u57fa\u4e8e Promise \u5bf9\u8c61\u7684\u81ea\u52a8\u6267\u884c\u5668\u3002\u8fd9\u662f\u7406\u89e3 co \u6a21\u5757\u5fc5\u987b\u7684\u3002</p>\n<h3 id="\u57fa\u4e8e-promise-\u5bf9\u8c61\u7684\u81ea\u52a8\u6267\u884c">\u57fa\u4e8e Promise \u5bf9\u8c61\u7684\u81ea\u52a8\u6267\u884c</h3>\n<p>\u8fd8\u662f\u6cbf\u7528\u4e0a\u9762\u7684\u4f8b\u5b50\u3002\u9996\u5148\uff0c\u628a<code>fs</code>\u6a21\u5757\u7684<code>readFile</code>\u65b9\u6cd5\u5305\u88c5\u6210\u4e00\u4e2a Promise \u5bf9\u8c61\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> fs = <span class="hljs-built_in">require</span>(<span class="hljs-string">\'fs\'</span>);\n\n<span class="hljs-keyword">var</span> readFile = <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params">fileName</span>)</span>{\n  <span class="hljs-keyword">return</span> <span class="hljs-keyword">new</span> <span class="hljs-built_in">Promise</span>(<span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params">resolve, reject</span>)</span>{\n    fs.readFile(fileName, <span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params">error, data</span>)</span>{\n      <span class="hljs-keyword">if</span> (error) <span class="hljs-keyword">return</span> reject(error);\n      resolve(data);\n    });\n  });\n};\n\n<span class="hljs-keyword">var</span> gen = <span class="hljs-function"><span class="hljs-keyword">function</span>* (<span class="hljs-params"></span>)</span>{\n  <span class="hljs-keyword">var</span> f1 = <span class="hljs-keyword">yield</span> readFile(<span class="hljs-string">\'/etc/fstab\'</span>);\n  <span class="hljs-keyword">var</span> f2 = <span class="hljs-keyword">yield</span> readFile(<span class="hljs-string">\'/etc/shells\'</span>);\n  <span class="hljs-built_in">console</span>.log(f1.toString());\n  <span class="hljs-built_in">console</span>.log(f2.toString());\n};</code></pre>\n<p>\u7136\u540e\uff0c\u624b\u52a8\u6267\u884c\u4e0a\u9762\u7684 Generator \u51fd\u6570\u3002</p>\n<pre><code class="language-javascript">var g = gen();\n\ng.next().<span class="hljs-keyword">value</span>.<span class="hljs-keyword">then</span>(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(data)</span></span>{\n  g.next(<span class="hljs-keyword">data</span>).<span class="hljs-keyword">value</span>.<span class="hljs-keyword">then</span>(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(data)</span></span>{\n    g.next(<span class="hljs-keyword">data</span>);\n  });\n});</code></pre>\n<p>\u624b\u52a8\u6267\u884c\u5176\u5b9e\u5c31\u662f\u7528<code>then</code>\u65b9\u6cd5\uff0c\u5c42\u5c42\u6dfb\u52a0\u56de\u8c03\u51fd\u6570\u3002\u7406\u89e3\u4e86\u8fd9\u4e00\u70b9\uff0c\u5c31\u53ef\u4ee5\u5199\u51fa\u4e00\u4e2a\u81ea\u52a8\u6267\u884c\u5668\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span></span> run(gen){\n  var g = gen();\n\n  <span class="hljs-function"><span class="hljs-keyword">function</span></span> next(<span class="hljs-keyword">data</span>){\n    var result = g.next(<span class="hljs-keyword">data</span>);\n    <span class="hljs-keyword">if</span> (result.done) <span class="hljs-keyword">return</span> result.<span class="hljs-keyword">value</span>;\n    result.<span class="hljs-keyword">value</span>.<span class="hljs-keyword">then</span>(<span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(data)</span></span>{\n      next(<span class="hljs-keyword">data</span>);\n    });\n  }\n\n  next();\n}\n\nrun(gen);</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53ea\u8981 Generator \u51fd\u6570\u8fd8\u6ca1\u6267\u884c\u5230\u6700\u540e\u4e00\u6b65\uff0c<code>next</code>\u51fd\u6570\u5c31\u8c03\u7528\u81ea\u8eab\uff0c\u4ee5\u6b64\u5b9e\u73b0\u81ea\u52a8\u6267\u884c\u3002</p>\n<h3 id="co-\u6a21\u5757\u7684\u6e90\u7801">co \u6a21\u5757\u7684\u6e90\u7801</h3>\n<p>co \u5c31\u662f\u4e0a\u9762\u90a3\u4e2a\u81ea\u52a8\u6267\u884c\u5668\u7684\u6269\u5c55\uff0c\u5b83\u7684\u6e90\u7801\u53ea\u6709\u51e0\u5341\u884c\uff0c\u975e\u5e38\u7b80\u5355\u3002</p>\n<p>\u9996\u5148\uff0cco \u51fd\u6570\u63a5\u53d7 Generator \u51fd\u6570\u4f5c\u4e3a\u53c2\u6570\uff0c\u8fd4\u56de\u4e00\u4e2a Promise \u5bf9\u8c61\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">co</span>(<span class="hljs-params">gen</span>) </span>{\n  <span class="hljs-keyword">var</span> ctx = <span class="hljs-keyword">this</span>;\n\n  <span class="hljs-keyword">return</span> <span class="hljs-keyword">new</span> <span class="hljs-built_in">Promise</span>(<span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params">resolve, reject</span>) </span>{\n  });\n}</code></pre>\n<p>\u5728\u8fd4\u56de\u7684 Promise \u5bf9\u8c61\u91cc\u9762\uff0cco \u5148\u68c0\u67e5\u53c2\u6570<code>gen</code>\u662f\u5426\u4e3a Generator \u51fd\u6570\u3002\u5982\u679c\u662f\uff0c\u5c31\u6267\u884c\u8be5\u51fd\u6570\uff0c\u5f97\u5230\u4e00\u4e2a\u5185\u90e8\u6307\u9488\u5bf9\u8c61\uff1b\u5982\u679c\u4e0d\u662f\u5c31\u8fd4\u56de\uff0c\u5e76\u5c06 Promise \u5bf9\u8c61\u7684\u72b6\u6001\u6539\u4e3a<code>resolved</code>\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">co</span>(<span class="hljs-params">gen</span>) </span>{\n  <span class="hljs-keyword">var</span> ctx = <span class="hljs-keyword">this</span>;\n\n  <span class="hljs-keyword">return</span> <span class="hljs-keyword">new</span> <span class="hljs-built_in">Promise</span>(<span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params">resolve, reject</span>) </span>{\n    <span class="hljs-keyword">if</span> (<span class="hljs-keyword">typeof</span> gen === <span class="hljs-string">\'function\'</span>) gen = gen.call(ctx);\n    <span class="hljs-keyword">if</span> (!gen || <span class="hljs-keyword">typeof</span> gen.next !== <span class="hljs-string">\'function\'</span>) <span class="hljs-keyword">return</span> resolve(gen);\n  });\n}</code></pre>\n<p>\u63a5\u7740\uff0cco \u5c06 Generator \u51fd\u6570\u7684\u5185\u90e8\u6307\u9488\u5bf9\u8c61\u7684<code>next</code>\u65b9\u6cd5\uff0c\u5305\u88c5\u6210<code>onFulfilled</code>\u51fd\u6570\u3002\u8fd9\u4e3b\u8981\u662f\u4e3a\u4e86\u80fd\u591f\u6355\u6349\u629b\u51fa\u7684\u9519\u8bef\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">co</span>(<span class="hljs-params">gen</span>) </span>{\n  <span class="hljs-keyword">var</span> ctx = <span class="hljs-keyword">this</span>;\n\n  <span class="hljs-keyword">return</span> <span class="hljs-keyword">new</span> <span class="hljs-built_in">Promise</span>(<span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params">resolve, reject</span>) </span>{\n    <span class="hljs-keyword">if</span> (<span class="hljs-keyword">typeof</span> gen === <span class="hljs-string">\'function\'</span>) gen = gen.call(ctx);\n    <span class="hljs-keyword">if</span> (!gen || <span class="hljs-keyword">typeof</span> gen.next !== <span class="hljs-string">\'function\'</span>) <span class="hljs-keyword">return</span> resolve(gen);\n\n    onFulfilled();\n    <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">onFulfilled</span>(<span class="hljs-params">res</span>) </span>{\n      <span class="hljs-keyword">var</span> ret;\n      <span class="hljs-keyword">try</span> {\n        ret = gen.next(res);\n      } <span class="hljs-keyword">catch</span> (e) {\n        <span class="hljs-keyword">return</span> reject(e);\n      }\n      next(ret);\n    }\n  });\n}</code></pre>\n<p>\u6700\u540e\uff0c\u5c31\u662f\u5173\u952e\u7684<code>next</code>\u51fd\u6570\uff0c\u5b83\u4f1a\u53cd\u590d\u8c03\u7528\u81ea\u8eab\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">function</span> next(ret) {\n  <span class="hljs-keyword">if</span> (ret.done) <span class="hljs-keyword">return</span> resolve(ret.<span class="hljs-keyword">value</span>);\n  var <span class="hljs-keyword">value</span> = toPromise.<span class="hljs-keyword">call</span>(ctx, ret.<span class="hljs-keyword">value</span>);\n  <span class="hljs-keyword">if</span> (<span class="hljs-keyword">value</span> &amp;&amp; isPromise(<span class="hljs-keyword">value</span>)) <span class="hljs-keyword">return</span> <span class="hljs-keyword">value</span>.<span class="hljs-keyword">then</span>(onFulfilled, onRejected);\n  <span class="hljs-keyword">return</span> onRejected(\n    <span class="hljs-built_in">new</span> TypeError(\n      <span class="hljs-string">\'You may only yield a function, promise, generator, array, or object, \'</span>\n      + <span class="hljs-string">\'but the following object was passed: "\'</span>\n      + String(ret.<span class="hljs-keyword">value</span>)\n      + <span class="hljs-string">\'"\'</span>\n    )\n  );\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>next</code>\u51fd\u6570\u7684\u5185\u90e8\u4ee3\u7801\uff0c\u4e00\u5171\u53ea\u6709\u56db\u884c\u547d\u4ee4\u3002</p>\n<p>\u7b2c\u4e00\u884c\uff0c\u68c0\u67e5\u5f53\u524d\u662f\u5426\u4e3a Generator \u51fd\u6570\u7684\u6700\u540e\u4e00\u6b65\uff0c\u5982\u679c\u662f\u5c31\u8fd4\u56de\u3002</p>\n<p>\u7b2c\u4e8c\u884c\uff0c\u786e\u4fdd\u6bcf\u4e00\u6b65\u7684\u8fd4\u56de\u503c\uff0c\u662f Promise \u5bf9\u8c61\u3002</p>\n<p>\u7b2c\u4e09\u884c\uff0c\u4f7f\u7528<code>then</code>\u65b9\u6cd5\uff0c\u4e3a\u8fd4\u56de\u503c\u52a0\u4e0a\u56de\u8c03\u51fd\u6570\uff0c\u7136\u540e\u901a\u8fc7<code>onFulfilled</code>\u51fd\u6570\u518d\u6b21\u8c03\u7528<code>next</code>\u51fd\u6570\u3002</p>\n<p>\u7b2c\u56db\u884c\uff0c\u5728\u53c2\u6570\u4e0d\u7b26\u5408\u8981\u6c42\u7684\u60c5\u51b5\u4e0b\uff08\u53c2\u6570\u975e Thunk \u51fd\u6570\u548c Promise \u5bf9\u8c61\uff09\uff0c\u5c06 Promise \u5bf9\u8c61\u7684\u72b6\u6001\u6539\u4e3a<code>rejected</code>\uff0c\u4ece\u800c\u7ec8\u6b62\u6267\u884c\u3002</p>\n<h3 id="\u5904\u7406\u5e76\u53d1\u7684\u5f02\u6b65\u64cd\u4f5c">\u5904\u7406\u5e76\u53d1\u7684\u5f02\u6b65\u64cd\u4f5c</h3>\n<p>co \u652f\u6301\u5e76\u53d1\u7684\u5f02\u6b65\u64cd\u4f5c\uff0c\u5373\u5141\u8bb8\u67d0\u4e9b\u64cd\u4f5c\u540c\u65f6\u8fdb\u884c\uff0c\u7b49\u5230\u5b83\u4eec\u5168\u90e8\u5b8c\u6210\uff0c\u624d\u8fdb\u884c\u4e0b\u4e00\u6b65\u3002</p>\n<p>\u8fd9\u65f6\uff0c\u8981\u628a\u5e76\u53d1\u7684\u64cd\u4f5c\u90fd\u653e\u5728\u6570\u7ec4\u6216\u5bf9\u8c61\u91cc\u9762\uff0c\u8ddf\u5728<code>yield</code>\u8bed\u53e5\u540e\u9762\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u6570\u7ec4\u7684\u5199\u6cd5</span>\nco(<span class="hljs-function"><span class="hljs-keyword">function</span>* (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">var</span> res = <span class="hljs-keyword">yield</span> [\n    <span class="hljs-built_in">Promise</span>.resolve(<span class="hljs-number">1</span>),\n    <span class="hljs-built_in">Promise</span>.resolve(<span class="hljs-number">2</span>)\n  ];\n  <span class="hljs-built_in">console</span>.log(res);\n}).catch(onerror);\n\n<span class="hljs-comment">// \u5bf9\u8c61\u7684\u5199\u6cd5</span>\nco(<span class="hljs-function"><span class="hljs-keyword">function</span>* (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">var</span> res = <span class="hljs-keyword">yield</span> {\n    <span class="hljs-number">1</span>: <span class="hljs-built_in">Promise</span>.resolve(<span class="hljs-number">1</span>),\n    <span class="hljs-number">2</span>: <span class="hljs-built_in">Promise</span>.resolve(<span class="hljs-number">2</span>),\n  };\n  <span class="hljs-built_in">console</span>.log(res);\n}).catch(onerror);</code></pre>\n<p>\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript">co(<span class="hljs-function"><span class="hljs-keyword">function</span>* (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">var</span> values = [n1, n2, n3];\n  <span class="hljs-keyword">yield</span> values.map(somethingAsync);\n});\n\n<span class="hljs-function"><span class="hljs-keyword">function</span>* <span class="hljs-title">somethingAsync</span>(<span class="hljs-params">x</span>) </span>{\n  <span class="hljs-comment">// do something async</span>\n  <span class="hljs-keyword">return</span> y\n}</code></pre>\n<p>\u4e0a\u9762\u7684\u4ee3\u7801\u5141\u8bb8\u5e76\u53d1\u4e09\u4e2a<code>somethingAsync</code>\u5f02\u6b65\u64cd\u4f5c\uff0c\u7b49\u5230\u5b83\u4eec\u5168\u90e8\u5b8c\u6210\uff0c\u624d\u4f1a\u8fdb\u884c\u4e0b\u4e00\u6b65\u3002</p>\n<h3 id="\u5b9e\u4f8b\uff1a\u5904\u7406-stream">\u5b9e\u4f8b\uff1a\u5904\u7406 Stream</h3>\n<p>Node \u63d0\u4f9b Stream \u6a21\u5f0f\u8bfb\u5199\u6570\u636e\uff0c\u7279\u70b9\u662f\u4e00\u6b21\u53ea\u5904\u7406\u6570\u636e\u7684\u4e00\u90e8\u5206\uff0c\u6570\u636e\u5206\u6210\u4e00\u5757\u5757\u4f9d\u6b21\u5904\u7406\uff0c\u5c31\u597d\u50cf\u201c\u6570\u636e\u6d41\u201d\u4e00\u6837\u3002\u8fd9\u5bf9\u4e8e\u5904\u7406\u5927\u89c4\u6a21\u6570\u636e\u975e\u5e38\u6709\u5229\u3002Stream \u6a21\u5f0f\u4f7f\u7528 EventEmitter API\uff0c\u4f1a\u91ca\u653e\u4e09\u4e2a\u4e8b\u4ef6\u3002</p>\n<ul>\n<li><code>data</code>\u4e8b\u4ef6\uff1a\u4e0b\u4e00\u5757\u6570\u636e\u5757\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\u3002</li>\n<li><code>end</code>\u4e8b\u4ef6\uff1a\u6574\u4e2a\u201c\u6570\u636e\u6d41\u201d\u5904\u7406\u201c\u5b8c\u4e86\u3002</li>\n<li><code>error</code>\u4e8b\u4ef6\uff1a\u53d1\u751f\u9519\u8bef\u3002</li>\n</ul>\n<p>\u4f7f\u7528<code>Promise.race()</code>\u51fd\u6570\uff0c\u53ef\u4ee5\u5224\u65ad\u8fd9\u4e09\u4e2a\u4e8b\u4ef6\u4e4b\u4e2d\u54ea\u4e00\u4e2a\u6700\u5148\u53d1\u751f\uff0c\u53ea\u6709\u5f53<code>data</code>\u4e8b\u4ef6\u6700\u5148\u53d1\u751f\u65f6\uff0c\u624d\u8fdb\u5165\u4e0b\u4e00\u4e2a\u6570\u636e\u5757\u7684\u5904\u7406\u3002\u4ece\u800c\uff0c\u6211\u4eec\u53ef\u4ee5\u901a\u8fc7\u4e00\u4e2a<code>while</code>\u5faa\u73af\uff0c\u5b8c\u6210\u6240\u6709\u6570\u636e\u7684\u8bfb\u53d6\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> co = <span class="hljs-built_in">require</span>(<span class="hljs-string">\'co\'</span>);\n<span class="hljs-keyword">const</span> fs = <span class="hljs-built_in">require</span>(<span class="hljs-string">\'fs\'</span>);\n\n<span class="hljs-keyword">const</span> stream = fs.createReadStream(<span class="hljs-string">\'./les_miserables.txt\'</span>);\n<span class="hljs-keyword">let</span> valjeanCount = <span class="hljs-number">0</span>;\n\nco(<span class="hljs-function"><span class="hljs-keyword">function</span>*(<span class="hljs-params"></span>) </span>{\n  <span class="hljs-keyword">while</span>(<span class="hljs-literal">true</span>) {\n    <span class="hljs-keyword">const</span> res = <span class="hljs-keyword">yield</span> <span class="hljs-built_in">Promise</span>.race([\n      <span class="hljs-keyword">new</span> <span class="hljs-built_in">Promise</span>(<span class="hljs-function"><span class="hljs-params">resolve</span> =&gt;</span> stream.once(<span class="hljs-string">\'data\'</span>, resolve)),\n      <span class="hljs-keyword">new</span> <span class="hljs-built_in">Promise</span>(<span class="hljs-function"><span class="hljs-params">resolve</span> =&gt;</span> stream.once(<span class="hljs-string">\'end\'</span>, resolve)),\n      <span class="hljs-keyword">new</span> <span class="hljs-built_in">Promise</span>(<span class="hljs-function">(<span class="hljs-params">resolve, reject</span>) =&gt;</span> stream.once(<span class="hljs-string">\'error\'</span>, reject))\n    ]);\n    <span class="hljs-keyword">if</span> (!res) {\n      <span class="hljs-keyword">break</span>;\n    }\n    stream.removeAllListeners(<span class="hljs-string">\'data\'</span>);\n    stream.removeAllListeners(<span class="hljs-string">\'end\'</span>);\n    stream.removeAllListeners(<span class="hljs-string">\'error\'</span>);\n    valjeanCount += (res.toString().match(<span class="hljs-regexp">/valjean/ig</span>) || []).length;\n  }\n  <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'count:\'</span>, valjeanCount); <span class="hljs-comment">// count: 1120</span>\n});</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u91c7\u7528 Stream \u6a21\u5f0f\u8bfb\u53d6\u300a\u60b2\u60e8\u4e16\u754c\u300b\u7684\u6587\u672c\u6587\u4ef6\uff0c\u5bf9\u4e8e\u6bcf\u4e2a\u6570\u636e\u5757\u90fd\u4f7f\u7528<code>stream.once</code>\u65b9\u6cd5\uff0c\u5728<code>data</code>\u3001<code>end</code>\u3001<code>error</code>\u4e09\u4e2a\u4e8b\u4ef6\u4e0a\u6dfb\u52a0\u4e00\u6b21\u6027\u56de\u8c03\u51fd\u6570\u3002\u53d8\u91cf<code>res</code>\u53ea\u6709\u5728<code>data</code>\u4e8b\u4ef6\u53d1\u751f\u65f6\u624d\u6709\u503c\uff0c\u7136\u540e\u7d2f\u52a0\u6bcf\u4e2a\u6570\u636e\u5757\u4e4b\u4e2d<code>valjean</code>\u8fd9\u4e2a\u8bcd\u51fa\u73b0\u7684\u6b21\u6570\u3002</p>\n'}}]);