(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([[318],{UFWP:function(s,n){s.exports='<h1 id="gulp">gulp</h1>\n<p>\u7b80\u5355\u7684\u8bf4\uff0c\u5c31\u662f4\u4e2a\u65b9\u6cd5\n<code>gulp.src()</code>\u8bfb\u53d6\u6587\u4ef6\u3001<code>gulp.pipe()</code>\u5bf9\u5176\u8fdb\u884c\u6574\u7406\u3001<code>gulp.dest()</code>\u628a\u6574\u7406\u540e\u7684\u6570\u636e\u4fdd\u5b58\u5230\u6587\u4ef6\u3001<code>gulp.tast()</code>\u521b\u5efa\u6216\u5206\u914d\u4efb\u52a1\u3001<code>gulp.watch()</code>\u76d1\u89c6\u6587\u4ef6</p>\n<h2 id="gulpsrc">gulp.src()</h2>\n<p><code>gulp.src(globs, options)</code></p>\n<p>\u8f93\u51fa\uff08Emits\uff09\u7b26\u5408\u6240\u63d0\u4f9b\u7684\u5339\u914d\u6a21\u5f0f\uff08glob\uff09\u6216\u8005\u5339\u914d\u6a21\u5f0f\u7684\u6570\u7ec4\uff08array of globs\uff09\u7684\u6587\u4ef6\u3002 \u5c06\u8fd4\u56de\u4e00\u4e2a Vinyl files \u7684 stream \u5b83\u53ef\u4ee5\u88ab piped \u5230\u522b\u7684\u63d2\u4ef6\u4e2d\u3002\u4e5f\u5c31\u662f\u8bfb\u53d6\u6587\u4ef6\uff0c\u5bf9\u5176\u8fdb\u884c\u76f8\u5e94\u64cd\u4f5c</p>\n<h3 id="1\u3001\u53c2\u6570globs">1\u3001\u53c2\u6570globs</h3>\n<p>\u7c7b\u578b\uff1a String \u6216 Array</p>\n<p>\u6240\u8981\u8bfb\u53d6\u7684 glob \u6216\u8005\u5305\u542b globs \u7684\u6570\u7ec4\u3002\u4e5f\u5c31\u662f\u6587\u4ef6\u7684\u8def\u5f84</p>\n<h3 id="2\u3001\u53c2\u6570options">2\u3001\u53c2\u6570options</h3>\n<pre><code class="language-javascript"><span class="hljs-string">{</span>\n<span class="hljs-attr">    buffer:</span> <span class="hljs-literal">true</span><span class="hljs-string">|false,</span>\n<span class="hljs-attr">    read:</span> <span class="hljs-literal">true</span><span class="hljs-string">|false,</span>\n<span class="hljs-attr">    base:</span> <span class="hljs-string">string</span>\n<span class="hljs-string">}</span></code></pre>\n<p>\u7c7b\u578b\uff1a Object</p>\n<p>\u901a\u8fc7 glob-stream \u6240\u4f20\u9012\u7ed9 node-glob \u7684\u53c2\u6570\u3002</p>\n<p>\u9664\u4e86 node-glob \u548c glob-stream \u6240\u652f\u6301\u7684\u53c2\u6570\u5916\uff0cgulp \u589e\u52a0\u4e86\u4e00\u4e9b\u989d\u5916\u7684\u9009\u9879\u53c2\u6570\uff1a</p>\n<h4 id="1-buffer">(1) buffer</h4>\n<p>\u7c7b\u578b\uff1aBoolean</p>\n<p>\u9ed8\u8ba4\u503c\uff1atrue</p>\n<p>\u5982\u679c\u8be5\u9879\u88ab\u8bbe\u7f6e\u4e3a false\uff0c\u90a3\u4e48\u5c06\u4f1a\u4ee5 stream \u65b9\u5f0f\u8fd4\u56de file.contents \u800c\u4e0d\u662f\u6587\u4ef6 buffer \u7684\u5f62\u5f0f\u3002\u8fd9\u5728\u5904\u7406\u4e00\u4e9b\u5927\u6587\u4ef6\u7684\u65f6\u5019\u5c06\u4f1a\u5f88\u6709\u7528\u3002</p>\n<p><strong>\u6ce8\u610f\uff1a</strong>\n\u63d2\u4ef6\u53ef\u80fd\u5e76\u4e0d\u4f1a\u5b9e\u73b0\u5bf9 stream \u7684\u652f\u6301\u3002</p>\n<h4 id="2-read">(2) read</h4>\n<p>\u7c7b\u578b\uff1a Boolean</p>\n<p>\u9ed8\u8ba4\u503c\uff1a true</p>\n<p>\u5982\u679c\u8be5\u9879\u88ab\u8bbe\u7f6e\u4e3a false\uff0c \u90a3\u4e48 file.contents \u4f1a\u8fd4\u56de\u7a7a\u503c\uff08null\uff09\uff0c\u4e5f\u5c31\u662f\u5e76\u4e0d\u4f1a\u53bb\u8bfb\u53d6\u6587\u4ef6\u3002</p>\n<h4 id="3-base">(3) base</h4>\n<p>\u7c7b\u578b\uff1a String</p>\n<p>\u9ed8\u8ba4\u503c\uff1a \u5c06\u4f1a\u52a0\u5728 glob \u4e4b\u524d (\u8bf7\u770b glob2base)</p>\n<p>\u5982, \u8bf7\u60f3\u50cf\u4e00\u4e0b\u5728\u4e00\u4e2a\u8def\u5f84\u4e3a client/js/somedir \u7684\u76ee\u5f55\u4e2d\uff0c\u6709\u4e00\u4e2a\u6587\u4ef6\u53eb somefile.js \uff1a</p>\n<h3 id="3\u3001\u4f8b\u5b50">3\u3001\u4f8b\u5b50</h3>\n<pre><code class="language-javascript"><span class="hljs-selector-tag">gulp</span><span class="hljs-selector-class">.src</span>(<span class="hljs-string">\'client/templates/*.jade\'</span>)\n    <span class="hljs-selector-class">.pipe</span>(jade())\n    <span class="hljs-selector-class">.pipe</span>(minify())\n    <span class="hljs-selector-class">.pipe</span>(gulp.dest(<span class="hljs-string">\'build/minified_templates\'</span>));\n\n<span class="hljs-selector-tag">gulp</span><span class="hljs-selector-class">.src</span>(<span class="hljs-string">\'client/js/**/*.js\'</span>)   <span class="hljs-comment">// \u5339\u914d \'client/js/somedir/somefile.js\' \u5e76\u4e14\u5c06 `base` \u89e3\u6790\u4e3a `client/js/`</span>\n    <span class="hljs-selector-class">.pipe</span>(minify())\n    <span class="hljs-selector-class">.pipe</span>(gulp.dest(<span class="hljs-string">\'build\'</span>));  <span class="hljs-comment">// \u5199\u5165 \'build/somedir/somefile.js\'</span>\n\n<span class="hljs-selector-tag">gulp</span><span class="hljs-selector-class">.src</span>(<span class="hljs-string">\'client/js/**/*.js\'</span>, { <span class="hljs-attribute">base</span>: <span class="hljs-string">\'client\'</span> })\n    <span class="hljs-selector-class">.pipe</span>(minify())\n    <span class="hljs-selector-class">.pipe</span>(gulp.dest(<span class="hljs-string">\'build\'</span>));  <span class="hljs-comment">// \u5199\u5165 \'build/js/somedir/somefile.js\'</span></code></pre>\n<h2 id="gulpdest">gulp.dest()</h2>\n<p><code>gulp.dest(path, options)</code></p>\n<p>\u628a\u5904\u7406\u540e\u7684\u6570\u636e\u6d41\u5199\u5165\u5230\u6587\u4ef6\u4e2d\uff0c\u80fd\u88ab pipe \u8fdb\u6765\uff0c\u5e76\u4e14\u5c06\u4f1a\u5199\u6587\u4ef6\u3002\u5e76\u4e14\u91cd\u65b0\u8f93\u51fa\uff08emits\uff09\u6240\u6709\u6570\u636e\uff0c\u56e0\u6b64\u4f60\u53ef\u4ee5\u5c06\u5b83 pipe \u5230\u591a\u4e2a\u6587\u4ef6\u5939\u3002\u5982\u679c\u67d0\u6587\u4ef6\u5939\u4e0d\u5b58\u5728\uff0c\u5c06\u4f1a\u81ea\u52a8\u521b\u5efa\u5b83\u3002</p>\n<h3 id="1\u3001\u53c2\u6570path">1\u3001\u53c2\u6570path</h3>\n<p>\u7c7b\u578b\uff1a String | Function</p>\n<p>\u6587\u4ef6\u5c06\u88ab\u5199\u5165\u7684\u8def\u5f84\uff08\u8f93\u51fa\u76ee\u5f55\uff09\u3002\u4e5f\u53ef\u4ee5\u4f20\u5165\u4e00\u4e2a\u51fd\u6570\uff0c\u5728\u51fd\u6570\u4e2d\u8fd4\u56de\u76f8\u5e94\u8def\u5f84</p>\n<h3 id="2\u3001\u53c2\u6570option">2\u3001\u53c2\u6570option</h3>\n<pre><code class="language-javascript">{\n    <span class="hljs-attribute">cwd</span>: <span class="hljs-string">"string"</span>,\n    mode: <span class="hljs-string">"string"</span>\n}</code></pre>\n<p>\u7c7b\u578b\uff1a Object</p>\n<h4 id="cwd">cwd</h4>\n<p>\u7c7b\u578b\uff1a String</p>\n<p>\u9ed8\u8ba4\u503c\uff1a process.cwd()</p>\n<p>\u8f93\u51fa\u76ee\u5f55\u7684 cwd \u53c2\u6570\uff0c\u53ea\u5728\u6240\u7ed9\u7684\u8f93\u51fa\u76ee\u5f55\u662f\u76f8\u5bf9\u8def\u5f84\u65f6\u5019\u6709\u6548\u3002</p>\n<h4 id="mode">mode</h4>\n<p>\u7c7b\u578b\uff1a String</p>\n<p>\u9ed8\u8ba4\u503c\uff1a 0777</p>\n<p>\u516b\u8fdb\u5236\u6743\u9650\u5b57\u7b26\uff0c\u7528\u4ee5\u5b9a\u4e49\u6240\u6709\u5728\u8f93\u51fa\u76ee\u5f55\u4e2d\u6240\u521b\u5efa\u7684\u76ee\u5f55\u7684\u6743\u9650\u3002</p>\n<h3 id="3\u3001\u4f8b\u5b50-1">3\u3001\u4f8b\u5b50</h3>\n<pre><code class="language-javascript"><span class="hljs-selector-tag">gulp</span><span class="hljs-selector-class">.src</span>(<span class="hljs-string">\'./client/templates/*.jade\'</span>)\n    <span class="hljs-selector-class">.pipe</span>(jade())\n      <span class="hljs-selector-class">.pipe</span>(gulp.dest(<span class="hljs-string">\'./build/templates\'</span>))\n      <span class="hljs-selector-class">.pipe</span>(minify())\n      <span class="hljs-selector-class">.pipe</span>(gulp.dest(<span class="hljs-string">\'./build/minified_templates\'</span>));</code></pre>\n<h2 id="gulptask">gulp.task()</h2>\n<p>\u521b\u5efa\u4efb\u52a1\u6216\u8005\u5206\u914d\u4efb\u52a1</p>\n<pre><code class="language-javascript">gulp.task(<span class="hljs-string">\'somename\'</span>, <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">()</span> </span>{\n    <span class="hljs-comment">//\u505a\u4e00\u4e9b\u4e8b</span>\n});</code></pre>\n<p><code>gulp.task(name, [deps], fn)</code></p>\n<h3 id="1\u3001\u53c2\u6570name">1\u3001\u53c2\u6570name</h3>\n<p>\u7c7b\u578b\uff1a String</p>\n<p>\u4efb\u52a1\u7684\u540d\u5b57\uff0c\u5982\u679c\u4f60\u9700\u8981\u5728\u547d\u4ee4\u884c\u4e2d\u8fd0\u884c\u4f60\u7684\u67d0\u4e9b\u4efb\u52a1\uff0c\u90a3\u4e48\uff0c\u8bf7\u4e0d\u8981\u5728\u540d\u5b57\u4e2d\u4f7f\u7528\u7a7a\u683c\u3002</p>\n<h3 id="2\u3001\u53c2\u6570deps">2\u3001\u53c2\u6570deps</h3>\n<p>\u7c7b\u578b\uff1a Array</p>\n<p>\u4e00\u4e2a\u5305\u542b\u4efb\u52a1\u5217\u8868\u7684\u6570\u7ec4\uff0c\u8fd9\u4e9b\u4efb\u52a1\u4f1a\u5728\u4f60\u5f53\u524d\u4efb\u52a1\u8fd0\u884c\u4e4b\u524d\u5b8c\u6210\u3002</p>\n<pre><code class="language-javascript">gulp.task(<span class="hljs-string">\'mytask\'</span>, [<span class="hljs-string">\'array\'</span>, <span class="hljs-string">\'of\'</span>, <span class="hljs-string">\'task\'</span>, <span class="hljs-string">\'names\'</span>], <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">()</span> </span>{\n    <span class="hljs-comment">// \u505a\u4e00\u4e9b\u4e8b</span>\n});</code></pre>\n<p><strong>\u6ce8\u610f\uff1a</strong>\n\u4f60\u7684\u4efb\u52a1\u662f\u5426\u5728\u8fd9\u4e9b\u524d\u7f6e\u4f9d\u8d56\u7684\u4efb\u52a1\u5b8c\u6210\u4e4b\u524d\u8fd0\u884c\u4e86\uff1f\u8bf7\u4e00\u5b9a\u8981\u786e\u4fdd\u4f60\u6240\u4f9d\u8d56\u7684\u4efb\u52a1\u5217\u8868\u4e2d\u7684\u4efb\u52a1\u90fd\u4f7f\u7528\u4e86\u6b63\u786e\u7684\u5f02\u6b65\u6267\u884c\u65b9\u5f0f\uff1a\u4f7f\u7528\u4e00\u4e2a callback\uff0c\u6216\u8005\u8fd4\u56de\u4e00\u4e2a promise \u6216 stream\u3002</p>\n<h3 id="3\u3001\u53c2\u6570fn">3\u3001\u53c2\u6570fn</h3>\n<p>\u8be5\u51fd\u6570\u5b9a\u4e49\u4efb\u52a1\u6240\u8981\u6267\u884c\u7684\u4e00\u4e9b\u64cd\u4f5c\u3002\u901a\u5e38\u6765\u8bf4\uff0c\u5b83\u4f1a\u662f\u8fd9\u79cd\u5f62\u5f0f\uff1a<code>gulp.src().pipe(someplugin())</code></p>\n<p>\u4efb\u52a1\u53ef\u4ee5\u5f02\u6b65\u6267\u884c\uff0c\u5982\u679c fn \u80fd\u505a\u5230\u4ee5\u4e0b\u5176\u4e2d\u4e00\u70b9\uff1a</p>\n<h4 id="1-\u63a5\u53d7\u4e00\u4e2a-callback">(1) \u63a5\u53d7\u4e00\u4e2a callback</h4>\n<pre><code class="language-javascript"><span class="hljs-comment">// \u5728 shell \u4e2d\u6267\u884c\u4e00\u4e2a\u547d\u4ee4</span>\nvar <span class="hljs-built_in">exec</span> = require(<span class="hljs-string">\'child_process\'</span>).<span class="hljs-built_in">exec</span>;\n\ngulp.task(<span class="hljs-string">\'jekyll\'</span>, <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(cb)</span> {</span>\n      <span class="hljs-comment">// \u7f16\u8bd1 Jekyll</span>\n      <span class="hljs-built_in">exec</span>(<span class="hljs-string">\'jekyll build\'</span>, <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(err)</span> {</span>\n        <span class="hljs-keyword">if</span> (err)\n            <span class="hljs-keyword">return</span> cb(err); <span class="hljs-comment">// \u8fd4\u56de error</span>\n\n        cb();               <span class="hljs-comment">// \u5b8c\u6210 task</span>\n      });\n});</code></pre>\n<h4 id="2-\u8fd4\u56de\u4e00\u4e2a-stream">(2) \u8fd4\u56de\u4e00\u4e2a stream</h4>\n<pre><code class="language-javascript">gulp.task(<span class="hljs-string">\'somename\'</span>, <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">()</span> </span>{\n      <span class="hljs-keyword">var</span> stream = gulp.src(<span class="hljs-string">\'client/**/*.js\'</span>)\n        .pipe(minify())\n        .pipe(gulp.dest(<span class="hljs-string">\'build\'</span>));\n\n      <span class="hljs-keyword">return</span> stream;\n});</code></pre>\n<h4 id="3-\u8fd4\u56de\u4e00\u4e2a-promise">(3) \u8fd4\u56de\u4e00\u4e2a promise</h4>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> Q = <span class="hljs-built_in">require</span>(<span class="hljs-string">\'q\'</span>);\n\ngulp.task(<span class="hljs-string">\'somename\'</span>, <span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params"></span>) </span>{\n      <span class="hljs-keyword">var</span> deferred = Q.defer();\n\n      <span class="hljs-comment">// \u6267\u884c\u5f02\u6b65\u7684\u64cd\u4f5c</span>\n      setTimeout(<span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params"></span>) </span>{\n        deferred.resolve();\n      }, <span class="hljs-number">1</span>);\n\n      <span class="hljs-keyword">return</span> deferred.promise;\n});</code></pre>\n<p><strong>\u6ce8\u610f\uff1a</strong>\n\u9ed8\u8ba4\u7684\uff0ctask \u5c06\u4ee5\u6700\u5927\u7684\u5e76\u53d1\u6570\u6267\u884c\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0cgulp \u4f1a\u4e00\u6b21\u6027\u8fd0\u884c\u6240\u6709\u7684 task \u5e76\u4e14\u4e0d\u505a\u4efb\u4f55\u7b49\u5f85\u3002</p>\n<p>\u5982\u679c\u4f60\u60f3\u8981\u521b\u5efa\u4e00\u4e2a\u5e8f\u5217\u5316\u7684 task \u961f\u5217\uff0c\u5e76\u4ee5\u7279\u5b9a\u7684\u987a\u5e8f\u6267\u884c\uff0c\u4f60\u9700\u8981\u505a\u4e24\u4ef6\u4e8b\uff1a</p>\n<ul>\n<li>\u7ed9\u51fa\u4e00\u4e2a\u63d0\u793a\uff0c\u6765\u544a\u77e5 task \u4ec0\u4e48\u65f6\u5019\u6267\u884c\u5b8c\u6bd5\uff0c</li>\n<li>\u5e76\u4e14\u518d\u7ed9\u51fa\u4e00\u4e2a\u63d0\u793a\uff0c\u6765\u544a\u77e5\u4e00\u4e2a task \u4f9d\u8d56\u53e6\u4e00\u4e2a task \u7684\u5b8c\u6210\u3002</li>\n</ul>\n<p>\u5bf9\u4e8e\u8fd9\u4e2a\u4f8b\u5b50\uff0c\u8ba9\u6211\u4eec\u5148\u5047\u5b9a\u4f60\u6709\u4e24\u4e2a task\uff0c&quot;one&quot; \u548c &quot;two&quot;\uff0c\u5e76\u4e14\u4f60\u5e0c\u671b\u5b83\u4eec\u6309\u7167\u8fd9\u4e2a\u987a\u5e8f\u6267\u884c\uff1a</p>\n<ul>\n<li>\u5728 &quot;one&quot; \u4e2d\uff0c\u4f60\u52a0\u5165\u4e00\u4e2a\u63d0\u793a\uff0c\u6765\u544a\u77e5\u4ec0\u4e48\u65f6\u5019\u5b83\u4f1a\u5b8c\u6210\uff1a\u53ef\u4ee5\u518d\u5b8c\u6210\u65f6\u5019\u8fd4\u56de\u4e00\u4e2a callback\uff0c\u6216\u8005\u8fd4\u56de\u4e00\u4e2a promise \u6216 stream\uff0c\u8fd9\u6837\u7cfb\u7edf\u4f1a\u53bb\u7b49\u5f85\u5b83\u5b8c\u6210\u3002</li>\n<li>\u5728 &quot;two&quot; \u4e2d\uff0c\u4f60\u9700\u8981\u6dfb\u52a0\u4e00\u4e2a\u63d0\u793a\u6765\u544a\u8bc9\u7cfb\u7edf\u5b83\u9700\u8981\u4f9d\u8d56\u7b2c\u4e00\u4e2a task \u5b8c\u6210\u3002</li>\n</ul>\n<p>\u56e0\u6b64\uff0c\u8fd9\u4e2a\u4f8b\u5b50\u7684\u5b9e\u9645\u4ee3\u7801\u5c06\u4f1a\u662f\u8fd9\u6837\uff1a</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> gulp = <span class="hljs-built_in">require</span>(<span class="hljs-string">\'gulp\'</span>);\n\n<span class="hljs-comment">// \u8fd4\u56de\u4e00\u4e2a callback\uff0c\u56e0\u6b64\u7cfb\u7edf\u53ef\u4ee5\u77e5\u9053\u5b83\u4ec0\u4e48\u65f6\u5019\u5b8c\u6210</span>\ngulp.task(<span class="hljs-string">\'one\'</span>, <span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params">cb</span>) </span>{\n    <span class="hljs-comment">// \u505a\u4e00\u4e9b\u4e8b -- \u5f02\u6b65\u7684\u6216\u8005\u5176\u4ed6\u7684</span>\n    cb(err);    <span class="hljs-comment">// \u5982\u679c err \u4e0d\u662f null \u6216 undefined\uff0c\u5219\u4f1a\u505c\u6b62\u6267\u884c\uff0c\u4e14\u6ce8\u610f\uff0c\u8fd9\u6837\u4ee3\u8868\u6267\u884c\u5931\u8d25\u4e86</span>\n});\n\n<span class="hljs-comment">// \u5b9a\u4e49\u4e00\u4e2a\u6240\u4f9d\u8d56\u7684 task \u5fc5\u987b\u5728\u8fd9\u4e2a task \u6267\u884c\u4e4b\u524d\u5b8c\u6210</span>\ngulp.task(<span class="hljs-string">\'two\'</span>, [<span class="hljs-string">\'one\'</span>], <span class="hljs-function"><span class="hljs-keyword">function</span>(<span class="hljs-params"></span>) </span>{\n    <span class="hljs-comment">// \'one\' \u5b8c\u6210\u540e</span>\n});\n\ngulp.task(<span class="hljs-string">\'default\'</span>, [<span class="hljs-string">\'one\'</span>, <span class="hljs-string">\'two\'</span>]);</code></pre>\n<h2 id="gulpwatch">gulp.watch()</h2>\n<p><code>gulp.watch(glob ,[opts], tasks)</code></p>\n<p><code>gulp.watch(glob [opts, cb])</code></p>\n<p>\u76d1\u89c6\u6587\u4ef6\uff0c\u5e76\u4e14\u53ef\u4ee5\u5728\u6587\u4ef6\u53d1\u751f\u6539\u52a8\u65f6\u5019\u505a\u4e00\u4e9b\u4e8b\u60c5\u3002\u5b83\u603b\u4f1a\u8fd4\u56de\u4e00\u4e2a EventEmitter \u6765\u53d1\u5c04\uff08emit\uff09change \u4e8b\u4ef6\u3002</p>\n<h3 id="1\u3001gulpwatchglob-opts-tasks">1\u3001gulp.watch(glob, [opts], tasks)</h3>\n<h4 id="glob">glob</h4>\n<p>\u7c7b\u578b\uff1a String or Array</p>\n<p>\u4e00\u4e2a glob \u5b57\u7b26\u4e32\uff0c\u6216\u8005\u4e00\u4e2a\u5305\u542b\u591a\u4e2a glob\u5b57\u7b26\u4e32\u7684\u6570\u7ec4\uff0c\u7528\u6765\u6307\u5b9a\u5177\u4f53\u76d1\u63a7\u54ea\u4e9b\u6587\u4ef6\u7684\u53d8\u52a8\u3002</p>\n<h4 id="opts">opts</h4>\n<p>\u7c7b\u578b\uff1a Object</p>\n<p>\u4f20\u7ed9 gaze \u7684\u53c2\u6570\u3002</p>\n<h4 id="tasks">tasks</h4>\n<p>\u7c7b\u578b\uff1a Array</p>\n<p>\u9700\u8981\u5728\u6587\u4ef6\u53d8\u52a8\u540e\u6267\u884c\u7684\u4e00\u4e2a\u6216\u8005\u591a\u4e2a\u901a\u8fc7 gulp.task() \u521b\u5efa\u7684 task \u7684\u540d\u5b57</p>\n<pre><code class="language-javascript">var watcher = gulp.watch(<span class="hljs-string">\'js/**/*.js\'</span>, [<span class="hljs-string">\'uglify\'</span>, <span class="hljs-string">\'reload\'</span>]);\n\nwatcher.on(<span class="hljs-string">\'change\'</span>, <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(event)</span></span> {\n      console.<span class="hljs-built_in">log</span>(<span class="hljs-string">\'File \'</span> + event.<span class="hljs-built_in">path</span> + <span class="hljs-string">\' was \'</span> + event.<span class="hljs-built_in">type</span> + <span class="hljs-string">\', running tasks...\'</span>);\n});</code></pre>\n<h3 id="2\u3001gulpwatchglob-opts-cb">2\u3001gulp.watch(glob, [opts, cb])</h3>\n<h4 id="glob-1">glob</h4>\n<p>\u7c7b\u578b\uff1a String or Array</p>\n<p>\u4e00\u4e2a glob \u5b57\u7b26\u4e32\uff0c\u6216\u8005\u4e00\u4e2a\u5305\u542b\u591a\u4e2a glob \u5b57\u7b26\u4e32\u7684\u6570\u7ec4\uff0c\u7528\u6765\u6307\u5b9a\u5177\u4f53\u76d1\u63a7\u54ea\u4e9b\u6587\u4ef6\u7684\u53d8\u52a8\u3002</p>\n<h4 id="opts-1">opts</h4>\n<p>\u7c7b\u578b\uff1a Object</p>\n<p>\u4f20\u7ed9 gaze \u7684\u53c2\u6570\u3002</p>\n<h4 id="cbevent">cb(event)</h4>\n<p><strong>event.type</strong>\n\u7c7b\u578b\uff1a String</p>\n<p>\u53d1\u751f\u7684\u53d8\u52a8\u7684\u7c7b\u578b\uff1aadded, changed \u6216\u8005 deleted\u3002</p>\n<p><strong>event.path</strong>\n\u7c7b\u578b\uff1a String</p>\n<p>\u89e6\u53d1\u4e86\u8be5\u4e8b\u4ef6\u7684\u6587\u4ef6\u7684\u8def\u5f84\u3002</p>\n<p>\u7c7b\u578b\uff1a Function</p>\n<p>\u6bcf\u6b21\u53d8\u52a8\u9700\u8981\u6267\u884c\u7684 callback</p>\n<pre><code class="language-javascript">gulp.watch(<span class="hljs-string">\'js/**/*.js\'</span>, <span class="hljs-function"><span class="hljs-keyword">function</span><span class="hljs-params">(event)</span></span> {\n    console.<span class="hljs-built_in">log</span>(<span class="hljs-string">\'File \'</span> + event.<span class="hljs-built_in">path</span> + <span class="hljs-string">\' was \'</span> + event.<span class="hljs-built_in">type</span> + <span class="hljs-string">\', running tasks...\'</span>);\n});</code></pre>\n<p>callback \u4f1a\u88ab\u4f20\u5165\u4e00\u4e2a\u540d\u4e3a event \u7684\u5bf9\u8c61\u3002\u8fd9\u4e2a\u5bf9\u8c61\u63cf\u8ff0\u4e86\u6240\u76d1\u63a7\u5230\u7684\u53d8\u52a8</p>\n<h2 id="\u5e38\u7528\u5de5\u5177">\u5e38\u7528\u5de5\u5177</h2>\n<p>1\u3001browser-sync</p>\n<p>2\u3001browserify</p>\n<p>3\u3001del</p>\n<p>4\u3001gulp-browserify</p>\n<p>5\u3001gulp-css-spriter</p>\n<p>6\u3001gulp-file-include</p>\n<p>7\u3001gulp-jshint</p>\n<p>8\u3001gulp-less</p>\n<p>9\u3001gulp-minify-css</p>\n<p>10\u3001gulp-rename</p>\n<p>11\u3001gulp-sourcemaps</p>\n<p>12\u3001gulp-tap</p>\n<p>13\u3001gulp-uglify</p>\n<p>14\u3001gulp-util</p>\n<p>15\u3001js-beautify</p>\n<p>16\u3001jshint-stylish</p>\n<p>17\u3001readable-stream</p>\n<p>18\u3001through2</p>\n<p>19\u3001vinyl</p>\n<p>20\u3001vinyl-buffer</p>\n<p>21\u3001vinyl-source-stream</p>\n<p>22\u3001vinyl-transform</p>\n'}}]);