(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([[41],{"7IgE":function(s,a){s.exports='<h1 id="class-\u7684\u7ee7\u627f">Class \u7684\u7ee7\u627f</h1>\n<h2 id="\u7b80\u4ecb">\u7b80\u4ecb</h2>\n<p>Class \u53ef\u4ee5\u901a\u8fc7<code>extends</code>\u5173\u952e\u5b57\u5b9e\u73b0\u7ee7\u627f\uff0c\u8fd9\u6bd4 ES5 \u7684\u901a\u8fc7\u4fee\u6539\u539f\u578b\u94fe\u5b9e\u73b0\u7ee7\u627f\uff0c\u8981\u6e05\u6670\u548c\u65b9\u4fbf\u5f88\u591a\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">Point</span> </span>{\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">ColorPoint</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Point</span> </span>{\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u5b9a\u4e49\u4e86\u4e00\u4e2a<code>ColorPoint</code>\u7c7b\uff0c\u8be5\u7c7b\u901a\u8fc7<code>extends</code>\u5173\u952e\u5b57\uff0c\u7ee7\u627f\u4e86<code>Point</code>\u7c7b\u7684\u6240\u6709\u5c5e\u6027\u548c\u65b9\u6cd5\u3002\u4f46\u662f\u7531\u4e8e\u6ca1\u6709\u90e8\u7f72\u4efb\u4f55\u4ee3\u7801\uff0c\u6240\u4ee5\u8fd9\u4e24\u4e2a\u7c7b\u5b8c\u5168\u4e00\u6837\uff0c\u7b49\u4e8e\u590d\u5236\u4e86\u4e00\u4e2a<code>Point</code>\u7c7b\u3002\u4e0b\u9762\uff0c\u6211\u4eec\u5728<code>ColorPoint</code>\u5185\u90e8\u52a0\u4e0a\u4ee3\u7801\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">ColorPoint</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Point</span> </span>{\n  constructor(x, y, color) {\n    <span class="hljs-keyword">super</span>(x, y); <span class="hljs-comment">// \u8c03\u7528\u7236\u7c7b\u7684constructor(x, y)</span>\n    <span class="hljs-keyword">this</span>.color = color;\n  }\n\n  toString() {\n    <span class="hljs-keyword">return</span> <span class="hljs-keyword">this</span>.color + \' \' + <span class="hljs-keyword">super</span>.toString(); <span class="hljs-comment">// \u8c03\u7528\u7236\u7c7b\u7684toString()</span>\n  }\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>constructor</code>\u65b9\u6cd5\u548c<code>toString</code>\u65b9\u6cd5\u4e4b\u4e2d\uff0c\u90fd\u51fa\u73b0\u4e86<code>super</code>\u5173\u952e\u5b57\uff0c\u5b83\u5728\u8fd9\u91cc\u8868\u793a\u7236\u7c7b\u7684\u6784\u9020\u51fd\u6570\uff0c\u7528\u6765\u65b0\u5efa\u7236\u7c7b\u7684<code>this</code>\u5bf9\u8c61\u3002</p>\n<p>\u5b50\u7c7b\u5fc5\u987b\u5728<code>constructor</code>\u65b9\u6cd5\u4e2d\u8c03\u7528<code>super</code>\u65b9\u6cd5\uff0c\u5426\u5219\u65b0\u5efa\u5b9e\u4f8b\u65f6\u4f1a\u62a5\u9519\u3002\u8fd9\u662f\u56e0\u4e3a\u5b50\u7c7b\u6ca1\u6709\u81ea\u5df1\u7684<code>this</code>\u5bf9\u8c61\uff0c\u800c\u662f\u7ee7\u627f\u7236\u7c7b\u7684<code>this</code>\u5bf9\u8c61\uff0c\u7136\u540e\u5bf9\u5176\u8fdb\u884c\u52a0\u5de5\u3002\u5982\u679c\u4e0d\u8c03\u7528<code>super</code>\u65b9\u6cd5\uff0c\u5b50\u7c7b\u5c31\u5f97\u4e0d\u5230<code>this</code>\u5bf9\u8c61\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">Point</span> </span>{ <span class="hljs-comment">/* ... */</span> }\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">ColorPoint</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Point</span> </span>{\n  constructor() {\n  }\n}\n\nlet cp = <span class="hljs-keyword">new</span> <span class="hljs-type">ColorPoint</span>(); <span class="hljs-comment">// ReferenceError</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>ColorPoint</code>\u7ee7\u627f\u4e86\u7236\u7c7b<code>Point</code>\uff0c\u4f46\u662f\u5b83\u7684\u6784\u9020\u51fd\u6570\u6ca1\u6709\u8c03\u7528<code>super</code>\u65b9\u6cd5\uff0c\u5bfc\u81f4\u65b0\u5efa\u5b9e\u4f8b\u65f6\u62a5\u9519\u3002</p>\n<p>ES5 \u7684\u7ee7\u627f\uff0c\u5b9e\u8d28\u662f\u5148\u521b\u9020\u5b50\u7c7b\u7684\u5b9e\u4f8b\u5bf9\u8c61<code>this</code>\uff0c\u7136\u540e\u518d\u5c06\u7236\u7c7b\u7684\u65b9\u6cd5\u6dfb\u52a0\u5230<code>this</code>\u4e0a\u9762\uff08<code>Parent.apply(this)</code>\uff09\u3002ES6 \u7684\u7ee7\u627f\u673a\u5236\u5b8c\u5168\u4e0d\u540c\uff0c\u5b9e\u8d28\u662f\u5148\u521b\u9020\u7236\u7c7b\u7684\u5b9e\u4f8b\u5bf9\u8c61<code>this</code>\uff08\u6240\u4ee5\u5fc5\u987b\u5148\u8c03\u7528<code>super</code>\u65b9\u6cd5\uff09\uff0c\u7136\u540e\u518d\u7528\u5b50\u7c7b\u7684\u6784\u9020\u51fd\u6570\u4fee\u6539<code>this</code>\u3002</p>\n<p>\u5982\u679c\u5b50\u7c7b\u6ca1\u6709\u5b9a\u4e49<code>constructor</code>\u65b9\u6cd5\uff0c\u8fd9\u4e2a\u65b9\u6cd5\u4f1a\u88ab\u9ed8\u8ba4\u6dfb\u52a0\uff0c\u4ee3\u7801\u5982\u4e0b\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u4e0d\u7ba1\u6709\u6ca1\u6709\u663e\u5f0f\u5b9a\u4e49\uff0c\u4efb\u4f55\u4e00\u4e2a\u5b50\u7c7b\u90fd\u6709<code>constructor</code>\u65b9\u6cd5\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">ColorPoint</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Point</span> </span>{\n}\n\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">ColorPoint</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Point</span> </span>{\n  constructor(...args) {\n    <span class="hljs-keyword">super</span>(...args);\n  }\n}</code></pre>\n<p>\u53e6\u4e00\u4e2a\u9700\u8981\u6ce8\u610f\u7684\u5730\u65b9\u662f\uff0c\u5728\u5b50\u7c7b\u7684\u6784\u9020\u51fd\u6570\u4e2d\uff0c\u53ea\u6709\u8c03\u7528<code>super</code>\u4e4b\u540e\uff0c\u624d\u53ef\u4ee5\u4f7f\u7528<code>this</code>\u5173\u952e\u5b57\uff0c\u5426\u5219\u4f1a\u62a5\u9519\u3002\u8fd9\u662f\u56e0\u4e3a\u5b50\u7c7b\u5b9e\u4f8b\u7684\u6784\u5efa\uff0c\u662f\u57fa\u4e8e\u5bf9\u7236\u7c7b\u5b9e\u4f8b\u52a0\u5de5\uff0c\u53ea\u6709<code>super</code>\u65b9\u6cd5\u624d\u80fd\u8fd4\u56de\u7236\u7c7b\u5b9e\u4f8b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">Point</span> </span>{\n  constructor(x, y) {\n    <span class="hljs-keyword">this</span>.x = x;\n    <span class="hljs-keyword">this</span>.y = y;\n  }\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">ColorPoint</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Point</span> </span>{\n  constructor(x, y, color) {\n    <span class="hljs-keyword">this</span>.color = color; <span class="hljs-comment">// ReferenceError</span>\n    <span class="hljs-keyword">super</span>(x, y);\n    <span class="hljs-keyword">this</span>.color = color; <span class="hljs-comment">// \u6b63\u786e</span>\n  }\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b50\u7c7b\u7684<code>constructor</code>\u65b9\u6cd5\u6ca1\u6709\u8c03\u7528<code>super</code>\u4e4b\u524d\uff0c\u5c31\u4f7f\u7528<code>this</code>\u5173\u952e\u5b57\uff0c\u7ed3\u679c\u62a5\u9519\uff0c\u800c\u653e\u5728<code>super</code>\u65b9\u6cd5\u4e4b\u540e\u5c31\u662f\u6b63\u786e\u7684\u3002</p>\n<p>\u4e0b\u9762\u662f\u751f\u6210\u5b50\u7c7b\u5b9e\u4f8b\u7684\u4ee3\u7801\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">let</span> cp = <span class="hljs-keyword">new</span> ColorPoint(<span class="hljs-number">25</span>, <span class="hljs-number">8</span>, <span class="hljs-string">\'green\'</span>);\n\ncp <span class="hljs-keyword">instanceof</span> ColorPoint <span class="hljs-comment">// true</span>\ncp <span class="hljs-keyword">instanceof</span> Point <span class="hljs-comment">// true</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b9e\u4f8b\u5bf9\u8c61<code>cp</code>\u540c\u65f6\u662f<code>ColorPoint</code>\u548c<code>Point</code>\u4e24\u4e2a\u7c7b\u7684\u5b9e\u4f8b\uff0c\u8fd9\u4e0e ES5 \u7684\u884c\u4e3a\u5b8c\u5168\u4e00\u81f4\u3002</p>\n<p>\u6700\u540e\uff0c\u7236\u7c7b\u7684\u9759\u6001\u65b9\u6cd5\uff0c\u4e5f\u4f1a\u88ab\u5b50\u7c7b\u7ee7\u627f\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{\n  static hello() {\n    console.log(<span class="hljs-symbol">\'hello</span> world\');\n  }\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n}\n\n<span class="hljs-type">B</span>.hello()  <span class="hljs-comment">// hello world</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>hello()</code>\u662f<code>A</code>\u7c7b\u7684\u9759\u6001\u65b9\u6cd5\uff0c<code>B</code>\u7ee7\u627f<code>A</code>\uff0c\u4e5f\u7ee7\u627f\u4e86<code>A</code>\u7684\u9759\u6001\u65b9\u6cd5\u3002</p>\n<h2 id="objectgetprototypeof">Object.getPrototypeOf()</h2>\n<p><code>Object.getPrototypeOf</code>\u65b9\u6cd5\u53ef\u4ee5\u7528\u6765\u4ece\u5b50\u7c7b\u4e0a\u83b7\u53d6\u7236\u7c7b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>get<span class="hljs-constructor">PrototypeOf(ColorPoint)</span><span class="hljs-operator"> === </span>Point\n<span class="hljs-comment">// true</span></code></pre>\n<p>\u56e0\u6b64\uff0c\u53ef\u4ee5\u4f7f\u7528\u8fd9\u4e2a\u65b9\u6cd5\u5224\u65ad\uff0c\u4e00\u4e2a\u7c7b\u662f\u5426\u7ee7\u627f\u4e86\u53e6\u4e00\u4e2a\u7c7b\u3002</p>\n<h2 id="super-\u5173\u952e\u5b57">super \u5173\u952e\u5b57</h2>\n<p><code>super</code>\u8fd9\u4e2a\u5173\u952e\u5b57\uff0c\u65e2\u53ef\u4ee5\u5f53\u4f5c\u51fd\u6570\u4f7f\u7528\uff0c\u4e5f\u53ef\u4ee5\u5f53\u4f5c\u5bf9\u8c61\u4f7f\u7528\u3002\u5728\u8fd9\u4e24\u79cd\u60c5\u51b5\u4e0b\uff0c\u5b83\u7684\u7528\u6cd5\u5b8c\u5168\u4e0d\u540c\u3002</p>\n<p>\u7b2c\u4e00\u79cd\u60c5\u51b5\uff0c<code>super</code>\u4f5c\u4e3a\u51fd\u6570\u8c03\u7528\u65f6\uff0c\u4ee3\u8868\u7236\u7c7b\u7684\u6784\u9020\u51fd\u6570\u3002ES6 \u8981\u6c42\uff0c\u5b50\u7c7b\u7684\u6784\u9020\u51fd\u6570\u5fc5\u987b\u6267\u884c\u4e00\u6b21<code>super</code>\u51fd\u6570\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">super</span>();\n  }\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b50\u7c7b<code>B</code>\u7684\u6784\u9020\u51fd\u6570\u4e4b\u4e2d\u7684<code>super()</code>\uff0c\u4ee3\u8868\u8c03\u7528\u7236\u7c7b\u7684\u6784\u9020\u51fd\u6570\u3002\u8fd9\u662f\u5fc5\u987b\u7684\uff0c\u5426\u5219 JavaScript \u5f15\u64ce\u4f1a\u62a5\u9519\u3002</p>\n<p>\u6ce8\u610f\uff0c<code>super</code>\u867d\u7136\u4ee3\u8868\u4e86\u7236\u7c7b<code>A</code>\u7684\u6784\u9020\u51fd\u6570\uff0c\u4f46\u662f\u8fd4\u56de\u7684\u662f\u5b50\u7c7b<code>B</code>\u7684\u5b9e\u4f8b\uff0c\u5373<code>super</code>\u5185\u90e8\u7684<code>this</code>\u6307\u7684\u662f<code>B</code>\uff0c\u56e0\u6b64<code>super()</code>\u5728\u8fd9\u91cc\u76f8\u5f53\u4e8e<code>A.prototype.constructor.call(this)</code>\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    console.log(<span class="hljs-keyword">new</span>.target.name);\n  }\n}\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">super</span>();\n  }\n}\n<span class="hljs-keyword">new</span> <span class="hljs-type">A</span>() <span class="hljs-comment">// A</span>\n<span class="hljs-keyword">new</span> <span class="hljs-type">B</span>() <span class="hljs-comment">// B</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>new.target</code>\u6307\u5411\u5f53\u524d\u6b63\u5728\u6267\u884c\u7684\u51fd\u6570\u3002\u53ef\u4ee5\u770b\u5230\uff0c\u5728<code>super()</code>\u6267\u884c\u65f6\uff0c\u5b83\u6307\u5411\u7684\u662f\u5b50\u7c7b<code>B</code>\u7684\u6784\u9020\u51fd\u6570\uff0c\u800c\u4e0d\u662f\u7236\u7c7b<code>A</code>\u7684\u6784\u9020\u51fd\u6570\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c<code>super()</code>\u5185\u90e8\u7684<code>this</code>\u6307\u5411\u7684\u662f<code>B</code>\u3002</p>\n<p>\u4f5c\u4e3a\u51fd\u6570\u65f6\uff0c<code>super()</code>\u53ea\u80fd\u7528\u5728\u5b50\u7c7b\u7684\u6784\u9020\u51fd\u6570\u4e4b\u4e2d\uff0c\u7528\u5728\u5176\u4ed6\u5730\u65b9\u5c31\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  m() {\n    <span class="hljs-keyword">super</span>(); <span class="hljs-comment">// \u62a5\u9519</span>\n  }\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>super()</code>\u7528\u5728<code>B</code>\u7c7b\u7684<code>m</code>\u65b9\u6cd5\u4e4b\u4e2d\uff0c\u5c31\u4f1a\u9020\u6210\u53e5\u6cd5\u9519\u8bef\u3002</p>\n<p>\u7b2c\u4e8c\u79cd\u60c5\u51b5\uff0c<code>super</code>\u4f5c\u4e3a\u5bf9\u8c61\u65f6\uff0c\u5728\u666e\u901a\u65b9\u6cd5\u4e2d\uff0c\u6307\u5411\u7236\u7c7b\u7684\u539f\u578b\u5bf9\u8c61\uff1b\u5728\u9759\u6001\u65b9\u6cd5\u4e2d\uff0c\u6307\u5411\u7236\u7c7b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{\n  p() {\n    <span class="hljs-keyword">return</span> <span class="hljs-number">2</span>;\n  }\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">super</span>();\n    console.log(<span class="hljs-keyword">super</span>.p()); <span class="hljs-comment">// 2</span>\n  }\n}\n\nlet b = <span class="hljs-keyword">new</span> <span class="hljs-type">B</span>();</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b50\u7c7b<code>B</code>\u5f53\u4e2d\u7684<code>super.p()</code>\uff0c\u5c31\u662f\u5c06<code>super</code>\u5f53\u4f5c\u4e00\u4e2a\u5bf9\u8c61\u4f7f\u7528\u3002\u8fd9\u65f6\uff0c<code>super</code>\u5728\u666e\u901a\u65b9\u6cd5\u4e4b\u4e2d\uff0c\u6307\u5411<code>A.prototype</code>\uff0c\u6240\u4ee5<code>super.p()</code>\u5c31\u76f8\u5f53\u4e8e<code>A.prototype.p()</code>\u3002</p>\n<p>\u8fd9\u91cc\u9700\u8981\u6ce8\u610f\uff0c\u7531\u4e8e<code>super</code>\u6307\u5411\u7236\u7c7b\u7684\u539f\u578b\u5bf9\u8c61\uff0c\u6240\u4ee5\u5b9a\u4e49\u5728\u7236\u7c7b\u5b9e\u4f8b\u4e0a\u7684\u65b9\u6cd5\u6216\u5c5e\u6027\uff0c\u662f\u65e0\u6cd5\u901a\u8fc7<code>super</code>\u8c03\u7528\u7684\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">this</span>.p = <span class="hljs-number">2</span>;\n  }\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  get m() {\n    <span class="hljs-keyword">return</span> <span class="hljs-keyword">super</span>.p;\n  }\n}\n\nlet b = <span class="hljs-keyword">new</span> <span class="hljs-type">B</span>();\nb.m <span class="hljs-comment">// undefined</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>p</code>\u662f\u7236\u7c7b<code>A</code>\u5b9e\u4f8b\u7684\u5c5e\u6027\uff0c<code>super.p</code>\u5c31\u5f15\u7528\u4e0d\u5230\u5b83\u3002</p>\n<p>\u5982\u679c\u5c5e\u6027\u5b9a\u4e49\u5728\u7236\u7c7b\u7684\u539f\u578b\u5bf9\u8c61\u4e0a\uff0c<code>super</code>\u5c31\u53ef\u4ee5\u53d6\u5230\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{}\n<span class="hljs-type">A</span>.prototype.x = <span class="hljs-number">2</span>;\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">super</span>();\n    console.log(<span class="hljs-keyword">super</span>.x) <span class="hljs-comment">// 2</span>\n  }\n}\n\nlet b = <span class="hljs-keyword">new</span> <span class="hljs-type">B</span>();</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5c5e\u6027<code>x</code>\u662f\u5b9a\u4e49\u5728<code>A.prototype</code>\u4e0a\u9762\u7684\uff0c\u6240\u4ee5<code>super.x</code>\u53ef\u4ee5\u53d6\u5230\u5b83\u7684\u503c\u3002</p>\n<p>ES6 \u89c4\u5b9a\uff0c\u901a\u8fc7<code>super</code>\u8c03\u7528\u7236\u7c7b\u7684\u65b9\u6cd5\u65f6\uff0c\u65b9\u6cd5\u5185\u90e8\u7684<code>this</code>\u6307\u5411\u5b50\u7c7b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">this</span>.x = <span class="hljs-number">1</span>;\n  }\n  print() {\n    console.log(<span class="hljs-keyword">this</span>.x);\n  }\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">super</span>();\n    <span class="hljs-keyword">this</span>.x = <span class="hljs-number">2</span>;\n  }\n  m() {\n    <span class="hljs-keyword">super</span>.print();\n  }\n}\n\nlet b = <span class="hljs-keyword">new</span> <span class="hljs-type">B</span>();\nb.m() <span class="hljs-comment">// 2</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>super.print()</code>\u867d\u7136\u8c03\u7528\u7684\u662f<code>A.prototype.print()</code>\uff0c\u4f46\u662f<code>A.prototype.print()</code>\u5185\u90e8\u7684<code>this</code>\u6307\u5411\u5b50\u7c7b<code>B</code>\uff0c\u5bfc\u81f4\u8f93\u51fa\u7684\u662f<code>2</code>\uff0c\u800c\u4e0d\u662f<code>1</code>\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5b9e\u9645\u4e0a\u6267\u884c\u7684\u662f<code>super.print.call(this)</code>\u3002</p>\n<p>\u7531\u4e8e<code>this</code>\u6307\u5411\u5b50\u7c7b\uff0c\u6240\u4ee5\u5982\u679c\u901a\u8fc7<code>super</code>\u5bf9\u67d0\u4e2a\u5c5e\u6027\u8d4b\u503c\uff0c\u8fd9\u65f6<code>super</code>\u5c31\u662f<code>this</code>\uff0c\u8d4b\u503c\u7684\u5c5e\u6027\u4f1a\u53d8\u6210\u5b50\u7c7b\u5b9e\u4f8b\u7684\u5c5e\u6027\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">this</span>.x = <span class="hljs-number">1</span>;\n  }\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">super</span>();\n    <span class="hljs-keyword">this</span>.x = <span class="hljs-number">2</span>;\n    <span class="hljs-keyword">super</span>.x = <span class="hljs-number">3</span>;\n    console.log(<span class="hljs-keyword">super</span>.x); <span class="hljs-comment">// undefined</span>\n    console.log(<span class="hljs-keyword">this</span>.x); <span class="hljs-comment">// 3</span>\n  }\n}\n\nlet b = <span class="hljs-keyword">new</span> <span class="hljs-type">B</span>();</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>super.x</code>\u8d4b\u503c\u4e3a<code>3</code>\uff0c\u8fd9\u65f6\u7b49\u540c\u4e8e\u5bf9<code>this.x</code>\u8d4b\u503c\u4e3a<code>3</code>\u3002\u800c\u5f53\u8bfb\u53d6<code>super.x</code>\u7684\u65f6\u5019\uff0c\u8bfb\u7684\u662f<code>A.prototype.x</code>\uff0c\u6240\u4ee5\u8fd4\u56de<code>undefined</code>\u3002</p>\n<p>\u5982\u679c<code>super</code>\u4f5c\u4e3a\u5bf9\u8c61\uff0c\u7528\u5728\u9759\u6001\u65b9\u6cd5\u4e4b\u4e2d\uff0c\u8fd9\u65f6<code>super</code>\u5c06\u6307\u5411\u7236\u7c7b\uff0c\u800c\u4e0d\u662f\u7236\u7c7b\u7684\u539f\u578b\u5bf9\u8c61\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">Parent</span> </span>{\n  static myMethod(msg) {\n    console.log(<span class="hljs-symbol">\'stati</span>c\', msg);\n  }\n\n  myMethod(msg) {\n    console.log(<span class="hljs-symbol">\'instanc</span>e\', msg);\n  }\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">Child</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Parent</span> </span>{\n  static myMethod(msg) {\n    <span class="hljs-keyword">super</span>.myMethod(msg);\n  }\n\n  myMethod(msg) {\n    <span class="hljs-keyword">super</span>.myMethod(msg);\n  }\n}\n\n<span class="hljs-type">Child</span>.myMethod(<span class="hljs-number">1</span>); <span class="hljs-comment">// static 1</span>\n\n<span class="hljs-keyword">var</span> child = <span class="hljs-keyword">new</span> <span class="hljs-type">Child</span>();\nchild.myMethod(<span class="hljs-number">2</span>); <span class="hljs-comment">// instance 2</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>super</code>\u5728\u9759\u6001\u65b9\u6cd5\u4e4b\u4e2d\u6307\u5411\u7236\u7c7b\uff0c\u5728\u666e\u901a\u65b9\u6cd5\u4e4b\u4e2d\u6307\u5411\u7236\u7c7b\u7684\u539f\u578b\u5bf9\u8c61\u3002</p>\n<p>\u6ce8\u610f\uff0c\u4f7f\u7528<code>super</code>\u7684\u65f6\u5019\uff0c\u5fc5\u987b\u663e\u5f0f\u6307\u5b9a\u662f\u4f5c\u4e3a\u51fd\u6570\u3001\u8fd8\u662f\u4f5c\u4e3a\u5bf9\u8c61\u4f7f\u7528\uff0c\u5426\u5219\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">super</span>();\n    console.log(<span class="hljs-keyword">super</span>); <span class="hljs-comment">// \u62a5\u9519</span>\n  }\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>console.log(super)</code>\u5f53\u4e2d\u7684<code>super</code>\uff0c\u65e0\u6cd5\u770b\u51fa\u662f\u4f5c\u4e3a\u51fd\u6570\u4f7f\u7528\uff0c\u8fd8\u662f\u4f5c\u4e3a\u5bf9\u8c61\u4f7f\u7528\uff0c\u6240\u4ee5 JavaScript \u5f15\u64ce\u89e3\u6790\u4ee3\u7801\u7684\u65f6\u5019\u5c31\u4f1a\u62a5\u9519\u3002\u8fd9\u65f6\uff0c\u5982\u679c\u80fd\u6e05\u6670\u5730\u8868\u660e<code>super</code>\u7684\u6570\u636e\u7c7b\u578b\uff0c\u5c31\u4e0d\u4f1a\u62a5\u9519\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">super</span>();\n    console.log(<span class="hljs-keyword">super</span>.valueOf() instanceof <span class="hljs-type">B</span>); <span class="hljs-comment">// true</span>\n  }\n}\n\nlet b = <span class="hljs-keyword">new</span> <span class="hljs-type">B</span>();</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>super.valueOf()</code>\u8868\u660e<code>super</code>\u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c\u56e0\u6b64\u5c31\u4e0d\u4f1a\u62a5\u9519\u3002\u540c\u65f6\uff0c\u7531\u4e8e<code>super</code>\u4f7f\u5f97<code>this</code>\u6307\u5411<code>B</code>\uff0c\u6240\u4ee5<code>super.valueOf()</code>\u8fd4\u56de\u7684\u662f\u4e00\u4e2a<code>B</code>\u7684\u5b9e\u4f8b\u3002</p>\n<p>\u6700\u540e\uff0c\u7531\u4e8e\u5bf9\u8c61\u603b\u662f\u7ee7\u627f\u5176\u4ed6\u5bf9\u8c61\u7684\uff0c\u6240\u4ee5\u53ef\u4ee5\u5728\u4efb\u610f\u4e00\u4e2a\u5bf9\u8c61\u4e2d\uff0c\u4f7f\u7528<code>super</code>\u5173\u952e\u5b57\u3002</p>\n<pre><code class="language-javascript">var obj = {\n  <span class="hljs-keyword">to</span><span class="hljs-constructor">String()</span> {\n    return <span class="hljs-string">"MyObject: "</span> + super.<span class="hljs-keyword">to</span><span class="hljs-constructor">String()</span>;\n  }\n};\n\nobj.<span class="hljs-keyword">to</span><span class="hljs-constructor">String()</span>; <span class="hljs-comment">// MyObject: [object Object]</span></code></pre>\n<h2 id="\u7c7b\u7684-prototype-\u5c5e\u6027\u548c__proto__\u5c5e\u6027">\u7c7b\u7684 prototype \u5c5e\u6027\u548c__proto__\u5c5e\u6027</h2>\n<p>\u5927\u591a\u6570\u6d4f\u89c8\u5668\u7684 ES5 \u5b9e\u73b0\u4e4b\u4e2d\uff0c\u6bcf\u4e00\u4e2a\u5bf9\u8c61\u90fd\u6709<code>__proto__</code>\u5c5e\u6027\uff0c\u6307\u5411\u5bf9\u5e94\u7684\u6784\u9020\u51fd\u6570\u7684<code>prototype</code>\u5c5e\u6027\u3002Class \u4f5c\u4e3a\u6784\u9020\u51fd\u6570\u7684\u8bed\u6cd5\u7cd6\uff0c\u540c\u65f6\u6709<code>prototype</code>\u5c5e\u6027\u548c<code>__proto__</code>\u5c5e\u6027\uff0c\u56e0\u6b64\u540c\u65f6\u5b58\u5728\u4e24\u6761\u7ee7\u627f\u94fe\u3002</p>\n<p>\uff081\uff09\u5b50\u7c7b\u7684<code>__proto__</code>\u5c5e\u6027\uff0c\u8868\u793a\u6784\u9020\u51fd\u6570\u7684\u7ee7\u627f\uff0c\u603b\u662f\u6307\u5411\u7236\u7c7b\u3002</p>\n<p>\uff082\uff09\u5b50\u7c7b<code>prototype</code>\u5c5e\u6027\u7684<code>__proto__</code>\u5c5e\u6027\uff0c\u8868\u793a\u65b9\u6cd5\u7684\u7ee7\u627f\uff0c\u603b\u662f\u6307\u5411\u7236\u7c7b\u7684<code>prototype</code>\u5c5e\u6027\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> </span>{\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n}\n\n<span class="hljs-type">B</span>.__proto__ === <span class="hljs-type">A</span> <span class="hljs-comment">// true</span>\n<span class="hljs-type">B</span>.prototype.__proto__ === <span class="hljs-type">A</span>.prototype <span class="hljs-comment">// true</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b50\u7c7b<code>B</code>\u7684<code>__proto__</code>\u5c5e\u6027\u6307\u5411\u7236\u7c7b<code>A</code>\uff0c\u5b50\u7c7b<code>B</code>\u7684<code>prototype</code>\u5c5e\u6027\u7684<code>__proto__</code>\u5c5e\u6027\u6307\u5411\u7236\u7c7b<code>A</code>\u7684<code>prototype</code>\u5c5e\u6027\u3002</p>\n<p>\u8fd9\u6837\u7684\u7ed3\u679c\u662f\u56e0\u4e3a\uff0c\u7c7b\u7684\u7ee7\u627f\u662f\u6309\u7167\u4e0b\u9762\u7684\u6a21\u5f0f\u5b9e\u73b0\u7684\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">class</span> A {\n}\n\n<span class="hljs-keyword">class</span> B {\n}\n\n<span class="hljs-comment">// B \u7684\u5b9e\u4f8b\u7ee7\u627f A \u7684\u5b9e\u4f8b</span>\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>set<span class="hljs-constructor">PrototypeOf(B.<span class="hljs-params">prototype</span>, A.<span class="hljs-params">prototype</span>)</span>;\n\n<span class="hljs-comment">// B \u7684\u5b9e\u4f8b\u7ee7\u627f A \u7684\u9759\u6001\u5c5e\u6027</span>\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>set<span class="hljs-constructor">PrototypeOf(B, A)</span>;\n\nconst b = <span class="hljs-keyword">new</span> <span class="hljs-constructor">B()</span>;</code></pre>\n<p>\u300a\u5bf9\u8c61\u7684\u6269\u5c55\u300b\u4e00\u7ae0\u7ed9\u51fa\u8fc7<code>Object.setPrototypeOf</code>\u65b9\u6cd5\u7684\u5b9e\u73b0\u3002</p>\n<pre><code class="language-javascript">Object.setPrototypeOf = <span class="hljs-keyword">function</span> <span class="hljs-title"></span>(obj, proto) {\n  obj.__proto__ = proto;\n  <span class="hljs-keyword">return</span> <span class="hljs-type">obj</span>;\n}</code></pre>\n<p>\u56e0\u6b64\uff0c\u5c31\u5f97\u5230\u4e86\u4e0a\u9762\u7684\u7ed3\u679c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>set<span class="hljs-constructor">PrototypeOf(B.<span class="hljs-params">prototype</span>, A.<span class="hljs-params">prototype</span>)</span>;\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">B</span>.</span></span>prototype.__proto__ = <span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">A</span>.</span></span>prototype;\n\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>set<span class="hljs-constructor">PrototypeOf(B, A)</span>;\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">B</span>.</span><span class="hljs-module"><span class="hljs-identifier">__proto__</span> </span></span>= A;</code></pre>\n<p>\u8fd9\u4e24\u6761\u7ee7\u627f\u94fe\uff0c\u53ef\u4ee5\u8fd9\u6837\u7406\u89e3\uff1a\u4f5c\u4e3a\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5b50\u7c7b\uff08<code>B</code>\uff09\u7684\u539f\u578b\uff08<code>__proto__</code>\u5c5e\u6027\uff09\u662f\u7236\u7c7b\uff08<code>A</code>\uff09\uff1b\u4f5c\u4e3a\u4e00\u4e2a\u6784\u9020\u51fd\u6570\uff0c\u5b50\u7c7b\uff08<code>B</code>\uff09\u7684\u539f\u578b\u5bf9\u8c61\uff08<code>prototype</code>\u5c5e\u6027\uff09\u662f\u7236\u7c7b\u7684\u539f\u578b\u5bf9\u8c61\uff08<code>prototype</code>\u5c5e\u6027\uff09\u7684\u5b9e\u4f8b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>create(<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">A</span>.</span></span>prototype);\n<span class="hljs-comment">// \u7b49\u540c\u4e8e</span>\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">B</span>.</span></span>prototype.__proto__ = <span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">A</span>.</span></span>prototype;</code></pre>\n<h3 id="extends-\u7684\u7ee7\u627f\u76ee\u6807">extends \u7684\u7ee7\u627f\u76ee\u6807</h3>\n<p><code>extends</code>\u5173\u952e\u5b57\u540e\u9762\u53ef\u4ee5\u8ddf\u591a\u79cd\u7c7b\u578b\u7684\u503c\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">B</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">A</span> </span>{\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684<code>A</code>\uff0c\u53ea\u8981\u662f\u4e00\u4e2a\u6709<code>prototype</code>\u5c5e\u6027\u7684\u51fd\u6570\uff0c\u5c31\u80fd\u88ab<code>B</code>\u7ee7\u627f\u3002\u7531\u4e8e\u51fd\u6570\u90fd\u6709<code>prototype</code>\u5c5e\u6027\uff08\u9664\u4e86<code>Function.prototype</code>\u51fd\u6570\uff09\uff0c\u56e0\u6b64<code>A</code>\u53ef\u4ee5\u662f\u4efb\u610f\u51fd\u6570\u3002</p>\n<p>\u4e0b\u9762\uff0c\u8ba8\u8bba\u4e09\u79cd\u7279\u6b8a\u60c5\u51b5\u3002</p>\n<p>\u7b2c\u4e00\u79cd\u7279\u6b8a\u60c5\u51b5\uff0c\u5b50\u7c7b\u7ee7\u627f<code>Object</code>\u7c7b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Object</span> </span>{\n}\n\n<span class="hljs-type">A</span>.__proto__ === <span class="hljs-type">Object</span> <span class="hljs-comment">// true</span>\n<span class="hljs-type">A</span>.prototype.__proto__ === <span class="hljs-type">Object</span>.prototype <span class="hljs-comment">// true</span></code></pre>\n<p>\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c<code>A</code>\u5176\u5b9e\u5c31\u662f\u6784\u9020\u51fd\u6570<code>Object</code>\u7684\u590d\u5236\uff0c<code>A</code>\u7684\u5b9e\u4f8b\u5c31\u662f<code>Object</code>\u7684\u5b9e\u4f8b\u3002</p>\n<p>\u7b2c\u4e8c\u79cd\u7279\u6b8a\u60c5\u51b5\uff0c\u4e0d\u5b58\u5728\u4efb\u4f55\u7ee7\u627f\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">class</span> A {\n}\n\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">A</span>.</span><span class="hljs-module"><span class="hljs-identifier">__proto__</span> </span><span class="hljs-operator">==</span></span>= <span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Function</span>.</span></span>prototype <span class="hljs-comment">// true</span>\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">A</span>.</span></span>prototype.__proto__<span class="hljs-operator"> === </span><span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>prototype <span class="hljs-comment">// true</span></code></pre>\n<p>\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c<code>A</code>\u4f5c\u4e3a\u4e00\u4e2a\u57fa\u7c7b\uff08\u5373\u4e0d\u5b58\u5728\u4efb\u4f55\u7ee7\u627f\uff09\uff0c\u5c31\u662f\u4e00\u4e2a\u666e\u901a\u51fd\u6570\uff0c\u6240\u4ee5\u76f4\u63a5\u7ee7\u627f<code>Function.prototype</code>\u3002\u4f46\u662f\uff0c<code>A</code>\u8c03\u7528\u540e\u8fd4\u56de\u4e00\u4e2a\u7a7a\u5bf9\u8c61\uff08\u5373<code>Object</code>\u5b9e\u4f8b\uff09\uff0c\u6240\u4ee5<code>A.prototype.__proto__</code>\u6307\u5411\u6784\u9020\u51fd\u6570\uff08<code>Object</code>\uff09\u7684<code>prototype</code>\u5c5e\u6027\u3002</p>\n<p>\u7b2c\u4e09\u79cd\u7279\u6b8a\u60c5\u51b5\uff0c\u5b50\u7c7b\u7ee7\u627f<code>null</code>\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">A</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">null</span> </span>{\n}\n\n<span class="hljs-type">A</span>.__proto__ === <span class="hljs-type">Function</span>.prototype <span class="hljs-comment">// true</span>\n<span class="hljs-type">A</span>.prototype.__proto__ === undefined <span class="hljs-comment">// true</span></code></pre>\n<p>\u8fd9\u79cd\u60c5\u51b5\u4e0e\u7b2c\u4e8c\u79cd\u60c5\u51b5\u975e\u5e38\u50cf\u3002<code>A</code>\u4e5f\u662f\u4e00\u4e2a\u666e\u901a\u51fd\u6570\uff0c\u6240\u4ee5\u76f4\u63a5\u7ee7\u627f<code>Function.prototype</code>\u3002\u4f46\u662f\uff0c<code>A</code>\u8c03\u7528\u540e\u8fd4\u56de\u7684\u5bf9\u8c61\u4e0d\u7ee7\u627f\u4efb\u4f55\u65b9\u6cd5\uff0c\u6240\u4ee5\u5b83\u7684<code>__proto__</code>\u6307\u5411<code>Function.prototype</code>\uff0c\u5373\u5b9e\u8d28\u4e0a\u6267\u884c\u4e86\u4e0b\u9762\u7684\u4ee3\u7801\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">C</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">null</span> </span>{\n  constructor() { <span class="hljs-keyword">return</span> <span class="hljs-type">Object</span>.create(<span class="hljs-literal">null</span>); }\n}</code></pre>\n<h3 id="\u5b9e\u4f8b\u7684-__proto__-\u5c5e\u6027">\u5b9e\u4f8b\u7684 __proto__ \u5c5e\u6027</h3>\n<p>\u5b50\u7c7b\u5b9e\u4f8b\u7684<code>__proto__</code>\u5c5e\u6027\u7684<code>__proto__</code>\u5c5e\u6027\uff0c\u6307\u5411\u7236\u7c7b\u5b9e\u4f8b\u7684<code>__proto__</code>\u5c5e\u6027\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5b50\u7c7b\u7684\u539f\u578b\u7684\u539f\u578b\uff0c\u662f\u7236\u7c7b\u7684\u539f\u578b\u3002</p>\n<pre><code class="language-javascript">var p1 = <span class="hljs-keyword">new</span> <span class="hljs-constructor">Point(2, 3)</span>;\nvar p2 = <span class="hljs-keyword">new</span> <span class="hljs-constructor">ColorPoint(2, 3, \'<span class="hljs-params">red</span>\')</span>;\n\np2.__proto__<span class="hljs-operator"> === </span>p1.__proto__ <span class="hljs-comment">// false</span>\np2.<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">__proto__</span>.</span><span class="hljs-module"><span class="hljs-identifier">__proto__</span> </span><span class="hljs-operator">==</span></span>= p1.__proto__ <span class="hljs-comment">// true</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>ColorPoint</code>\u7ee7\u627f\u4e86<code>Point</code>\uff0c\u5bfc\u81f4\u524d\u8005\u539f\u578b\u7684\u539f\u578b\u662f\u540e\u8005\u7684\u539f\u578b\u3002</p>\n<p>\u56e0\u6b64\uff0c\u901a\u8fc7\u5b50\u7c7b\u5b9e\u4f8b\u7684<code>__proto__.__proto__</code>\u5c5e\u6027\uff0c\u53ef\u4ee5\u4fee\u6539\u7236\u7c7b\u5b9e\u4f8b\u7684\u884c\u4e3a\u3002</p>\n<pre><code class="language-javascript">p2.__proto__.__proto__.printName = <span class="hljs-function"><span class="hljs-keyword">function</span> (<span class="hljs-params"></span>) </span>{\n  <span class="hljs-built_in">console</span>.log(<span class="hljs-string">\'Ha\'</span>);\n};\n\np1.printName() <span class="hljs-comment">// "Ha"</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u5728<code>ColorPoint</code>\u7684\u5b9e\u4f8b<code>p2</code>\u4e0a\u5411<code>Point</code>\u7c7b\u6dfb\u52a0\u65b9\u6cd5\uff0c\u7ed3\u679c\u5f71\u54cd\u5230\u4e86<code>Point</code>\u7684\u5b9e\u4f8b<code>p1</code>\u3002</p>\n<h2 id="\u539f\u751f\u6784\u9020\u51fd\u6570\u7684\u7ee7\u627f">\u539f\u751f\u6784\u9020\u51fd\u6570\u7684\u7ee7\u627f</h2>\n<p>\u539f\u751f\u6784\u9020\u51fd\u6570\u662f\u6307\u8bed\u8a00\u5185\u7f6e\u7684\u6784\u9020\u51fd\u6570\uff0c\u901a\u5e38\u7528\u6765\u751f\u6210\u6570\u636e\u7ed3\u6784\u3002ECMAScript \u7684\u539f\u751f\u6784\u9020\u51fd\u6570\u5927\u81f4\u6709\u4e0b\u9762\u8fd9\u4e9b\u3002</p>\n<ul>\n<li>Boolean()</li>\n<li>Number()</li>\n<li>String()</li>\n<li>Array()</li>\n<li>Date()</li>\n<li>Function()</li>\n<li>RegExp()</li>\n<li>Error()</li>\n<li>Object()</li>\n</ul>\n<p>\u4ee5\u524d\uff0c\u8fd9\u4e9b\u539f\u751f\u6784\u9020\u51fd\u6570\u662f\u65e0\u6cd5\u7ee7\u627f\u7684\uff0c\u6bd4\u5982\uff0c\u4e0d\u80fd\u81ea\u5df1\u5b9a\u4e49\u4e00\u4e2a<code>Array</code>\u7684\u5b50\u7c7b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">function</span> <span class="hljs-constructor">MyArray()</span> {\n  <span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Array</span>.</span></span>apply(this, arguments);\n}\n\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">MyArray</span>.</span></span>prototype = <span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>create(<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Array</span>.</span></span>prototype, {\n  constructor: {\n    value: MyArray,\n    writable: <span class="hljs-literal">true</span>,\n    configurable: <span class="hljs-literal">true</span>,\n    enumerable: <span class="hljs-literal">true</span>\n  }\n});</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u5b9a\u4e49\u4e86\u4e00\u4e2a\u7ee7\u627f Array \u7684<code>MyArray</code>\u7c7b\u3002\u4f46\u662f\uff0c\u8fd9\u4e2a\u7c7b\u7684\u884c\u4e3a\u4e0e<code>Array</code>\u5b8c\u5168\u4e0d\u4e00\u81f4\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">var</span> colors = <span class="hljs-keyword">new</span> <span class="hljs-type">MyArray</span>();\ncolors[<span class="hljs-number">0</span>] = <span class="hljs-string">"red"</span>;\ncolors.length  <span class="hljs-comment">// 0</span>\n\ncolors.length = <span class="hljs-number">0</span>;\ncolors[<span class="hljs-number">0</span>]  <span class="hljs-comment">// "red"</span></code></pre>\n<p>\u4e4b\u6240\u4ee5\u4f1a\u53d1\u751f\u8fd9\u79cd\u60c5\u51b5\uff0c\u662f\u56e0\u4e3a\u5b50\u7c7b\u65e0\u6cd5\u83b7\u5f97\u539f\u751f\u6784\u9020\u51fd\u6570\u7684\u5185\u90e8\u5c5e\u6027\uff0c\u901a\u8fc7<code>Array.apply()</code>\u6216\u8005\u5206\u914d\u7ed9\u539f\u578b\u5bf9\u8c61\u90fd\u4e0d\u884c\u3002\u539f\u751f\u6784\u9020\u51fd\u6570\u4f1a\u5ffd\u7565<code>apply</code>\u65b9\u6cd5\u4f20\u5165\u7684<code>this</code>\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u539f\u751f\u6784\u9020\u51fd\u6570\u7684<code>this</code>\u65e0\u6cd5\u7ed1\u5b9a\uff0c\u5bfc\u81f4\u62ff\u4e0d\u5230\u5185\u90e8\u5c5e\u6027\u3002</p>\n<p>ES5 \u662f\u5148\u65b0\u5efa\u5b50\u7c7b\u7684\u5b9e\u4f8b\u5bf9\u8c61<code>this</code>\uff0c\u518d\u5c06\u7236\u7c7b\u7684\u5c5e\u6027\u6dfb\u52a0\u5230\u5b50\u7c7b\u4e0a\uff0c\u7531\u4e8e\u7236\u7c7b\u7684\u5185\u90e8\u5c5e\u6027\u65e0\u6cd5\u83b7\u53d6\uff0c\u5bfc\u81f4\u65e0\u6cd5\u7ee7\u627f\u539f\u751f\u7684\u6784\u9020\u51fd\u6570\u3002\u6bd4\u5982\uff0c<code>Array</code>\u6784\u9020\u51fd\u6570\u6709\u4e00\u4e2a\u5185\u90e8\u5c5e\u6027<code>[[DefineOwnProperty]]</code>\uff0c\u7528\u6765\u5b9a\u4e49\u65b0\u5c5e\u6027\u65f6\uff0c\u66f4\u65b0<code>length</code>\u5c5e\u6027\uff0c\u8fd9\u4e2a\u5185\u90e8\u5c5e\u6027\u65e0\u6cd5\u5728\u5b50\u7c7b\u83b7\u53d6\uff0c\u5bfc\u81f4\u5b50\u7c7b\u7684<code>length</code>\u5c5e\u6027\u884c\u4e3a\u4e0d\u6b63\u5e38\u3002</p>\n<p>\u4e0b\u9762\u7684\u4f8b\u5b50\u4e2d\uff0c\u6211\u4eec\u60f3\u8ba9\u4e00\u4e2a\u666e\u901a\u5bf9\u8c61\u7ee7\u627f<code>Error</code>\u5bf9\u8c61\u3002</p>\n<pre><code class="language-javascript">var e = {};\n\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>get<span class="hljs-constructor">OwnPropertyNames(Error.<span class="hljs-params">call</span>(<span class="hljs-params">e</span>)</span>)\n<span class="hljs-comment">// [ \'stack\' ]</span>\n\n<span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>get<span class="hljs-constructor">OwnPropertyNames(<span class="hljs-params">e</span>)</span>\n<span class="hljs-comment">// []</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6211\u4eec\u60f3\u901a\u8fc7<code>Error.call(e)</code>\u8fd9\u79cd\u5199\u6cd5\uff0c\u8ba9\u666e\u901a\u5bf9\u8c61<code>e</code>\u5177\u6709<code>Error</code>\u5bf9\u8c61\u7684\u5b9e\u4f8b\u5c5e\u6027\u3002\u4f46\u662f\uff0c<code>Error.call()</code>\u5b8c\u5168\u5ffd\u7565\u4f20\u5165\u7684\u7b2c\u4e00\u4e2a\u53c2\u6570\uff0c\u800c\u662f\u8fd4\u56de\u4e00\u4e2a\u65b0\u5bf9\u8c61\uff0c<code>e</code>\u672c\u8eab\u6ca1\u6709\u4efb\u4f55\u53d8\u5316\u3002\u8fd9\u8bc1\u660e\u4e86<code>Error.call(e)</code>\u8fd9\u79cd\u5199\u6cd5\uff0c\u65e0\u6cd5\u7ee7\u627f\u539f\u751f\u6784\u9020\u51fd\u6570\u3002</p>\n<p>ES6 \u5141\u8bb8\u7ee7\u627f\u539f\u751f\u6784\u9020\u51fd\u6570\u5b9a\u4e49\u5b50\u7c7b\uff0c\u56e0\u4e3a ES6 \u662f\u5148\u65b0\u5efa\u7236\u7c7b\u7684\u5b9e\u4f8b\u5bf9\u8c61<code>this</code>\uff0c\u7136\u540e\u518d\u7528\u5b50\u7c7b\u7684\u6784\u9020\u51fd\u6570\u4fee\u9970<code>this</code>\uff0c\u4f7f\u5f97\u7236\u7c7b\u7684\u6240\u6709\u884c\u4e3a\u90fd\u53ef\u4ee5\u7ee7\u627f\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u7ee7\u627f<code>Array</code>\u7684\u4f8b\u5b50\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">MyArray</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Array</span> </span>{\n  constructor(...args) {\n    <span class="hljs-keyword">super</span>(...args);\n  }\n}\n\n<span class="hljs-keyword">var</span> arr = <span class="hljs-keyword">new</span> <span class="hljs-type">MyArray</span>();\narr[<span class="hljs-number">0</span>] = <span class="hljs-number">12</span>;\narr.length <span class="hljs-comment">// 1</span>\n\narr.length = <span class="hljs-number">0</span>;\narr[<span class="hljs-number">0</span>] <span class="hljs-comment">// undefined</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u5b9a\u4e49\u4e86\u4e00\u4e2a<code>MyArray</code>\u7c7b\uff0c\u7ee7\u627f\u4e86<code>Array</code>\u6784\u9020\u51fd\u6570\uff0c\u56e0\u6b64\u5c31\u53ef\u4ee5\u4ece<code>MyArray</code>\u751f\u6210\u6570\u7ec4\u7684\u5b9e\u4f8b\u3002\u8fd9\u610f\u5473\u7740\uff0cES6 \u53ef\u4ee5\u81ea\u5b9a\u4e49\u539f\u751f\u6570\u636e\u7ed3\u6784\uff08\u6bd4\u5982<code>Array</code>\u3001<code>String</code>\u7b49\uff09\u7684\u5b50\u7c7b\uff0c\u8fd9\u662f ES5 \u65e0\u6cd5\u505a\u5230\u7684\u3002</p>\n<p>\u4e0a\u9762\u8fd9\u4e2a\u4f8b\u5b50\u4e5f\u8bf4\u660e\uff0c<code>extends</code>\u5173\u952e\u5b57\u4e0d\u4ec5\u53ef\u4ee5\u7528\u6765\u7ee7\u627f\u7c7b\uff0c\u8fd8\u53ef\u4ee5\u7528\u6765\u7ee7\u627f\u539f\u751f\u7684\u6784\u9020\u51fd\u6570\u3002\u56e0\u6b64\u53ef\u4ee5\u5728\u539f\u751f\u6570\u636e\u7ed3\u6784\u7684\u57fa\u7840\u4e0a\uff0c\u5b9a\u4e49\u81ea\u5df1\u7684\u6570\u636e\u7ed3\u6784\u3002\u4e0b\u9762\u5c31\u662f\u5b9a\u4e49\u4e86\u4e00\u4e2a\u5e26\u7248\u672c\u529f\u80fd\u7684\u6570\u7ec4\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">VersionedArray</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Array</span> </span>{\n  constructor() {\n    <span class="hljs-keyword">super</span>();\n    <span class="hljs-keyword">this</span>.history = [[]];\n  }\n  commit() {\n    <span class="hljs-keyword">this</span>.history.push(<span class="hljs-keyword">this</span>.slice());\n  }\n  revert() {\n    <span class="hljs-keyword">this</span>.splice(<span class="hljs-number">0</span>, <span class="hljs-keyword">this</span>.length, ...<span class="hljs-keyword">this</span>.history[<span class="hljs-keyword">this</span>.history.length - <span class="hljs-number">1</span>]);\n  }\n}\n\n<span class="hljs-keyword">var</span> x = <span class="hljs-keyword">new</span> <span class="hljs-type">VersionedArray</span>();\n\nx.push(<span class="hljs-number">1</span>);\nx.push(<span class="hljs-number">2</span>);\nx <span class="hljs-comment">// [1, 2]</span>\nx.history <span class="hljs-comment">// [[]]</span>\n\nx.commit();\nx.history <span class="hljs-comment">// [[], [1, 2]]</span>\n\nx.push(<span class="hljs-number">3</span>);\nx <span class="hljs-comment">// [1, 2, 3]</span>\nx.history <span class="hljs-comment">// [[], [1, 2]]</span>\n\nx.revert();\nx <span class="hljs-comment">// [1, 2]</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>VersionedArray</code>\u4f1a\u901a\u8fc7<code>commit</code>\u65b9\u6cd5\uff0c\u5c06\u81ea\u5df1\u7684\u5f53\u524d\u72b6\u6001\u751f\u6210\u4e00\u4e2a\u7248\u672c\u5feb\u7167\uff0c\u5b58\u5165<code>history</code>\u5c5e\u6027\u3002<code>revert</code>\u65b9\u6cd5\u7528\u6765\u5c06\u6570\u7ec4\u91cd\u7f6e\u4e3a\u6700\u65b0\u4e00\u6b21\u4fdd\u5b58\u7684\u7248\u672c\u3002\u9664\u6b64\u4e4b\u5916\uff0c<code>VersionedArray</code>\u4f9d\u7136\u662f\u4e00\u4e2a\u666e\u901a\u6570\u7ec4\uff0c\u6240\u6709\u539f\u751f\u7684\u6570\u7ec4\u65b9\u6cd5\u90fd\u53ef\u4ee5\u5728\u5b83\u4e0a\u9762\u8c03\u7528\u3002</p>\n<p>\u4e0b\u9762\u662f\u4e00\u4e2a\u81ea\u5b9a\u4e49<code>Error</code>\u5b50\u7c7b\u7684\u4f8b\u5b50\uff0c\u53ef\u4ee5\u7528\u6765\u5b9a\u5236\u62a5\u9519\u65f6\u7684\u884c\u4e3a\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">ExtendableError</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Error</span> </span>{\n  constructor(message) {\n    <span class="hljs-keyword">super</span>();\n    <span class="hljs-keyword">this</span>.message = message;\n    <span class="hljs-keyword">this</span>.stack = (<span class="hljs-keyword">new</span> <span class="hljs-type">Error</span>()).stack;\n    <span class="hljs-keyword">this</span>.name = <span class="hljs-keyword">this</span>.constructor.name;\n  }\n}\n\n<span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">MyError</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">ExtendableError</span> </span>{\n  constructor(m) {\n    <span class="hljs-keyword">super</span>(m);\n  }\n}\n\n<span class="hljs-keyword">var</span> myerror = <span class="hljs-keyword">new</span> <span class="hljs-type">MyError</span>(<span class="hljs-symbol">\'l</span>l\');\nmyerror.message <span class="hljs-comment">// "ll"</span>\nmyerror instanceof <span class="hljs-type">Error</span> <span class="hljs-comment">// true</span>\nmyerror.name <span class="hljs-comment">// "MyError"</span>\nmyerror.stack\n<span class="hljs-comment">// Error</span>\n<span class="hljs-comment">//     at MyError.ExtendableError</span>\n<span class="hljs-comment">//     ...</span></code></pre>\n<p>\u6ce8\u610f\uff0c\u7ee7\u627f<code>Object</code>\u7684\u5b50\u7c7b\uff0c\u6709\u4e00\u4e2a<a href="http://stackoverflow.com/questions/36203614/super-does-not-pass-arguments-when-instantiating-a-class-extended-from-object">\u884c\u4e3a\u5dee\u5f02</a>\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">NewObj</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">Object</span></span>{\n  constructor(){\n    <span class="hljs-keyword">super</span>(...arguments);\n  }\n}\n<span class="hljs-keyword">var</span> o = <span class="hljs-keyword">new</span> <span class="hljs-type">NewObj</span>({attr: <span class="hljs-literal">true</span>});\no.attr === <span class="hljs-literal">true</span>  <span class="hljs-comment">// false</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>NewObj</code>\u7ee7\u627f\u4e86<code>Object</code>\uff0c\u4f46\u662f\u65e0\u6cd5\u901a\u8fc7<code>super</code>\u65b9\u6cd5\u5411\u7236\u7c7b<code>Object</code>\u4f20\u53c2\u3002\u8fd9\u662f\u56e0\u4e3a ES6 \u6539\u53d8\u4e86<code>Object</code>\u6784\u9020\u51fd\u6570\u7684\u884c\u4e3a\uff0c\u4e00\u65e6\u53d1\u73b0<code>Object</code>\u65b9\u6cd5\u4e0d\u662f\u901a\u8fc7<code>new Object()</code>\u8fd9\u79cd\u5f62\u5f0f\u8c03\u7528\uff0cES6 \u89c4\u5b9a<code>Object</code>\u6784\u9020\u51fd\u6570\u4f1a\u5ffd\u7565\u53c2\u6570\u3002</p>\n<h2 id="mixin-\u6a21\u5f0f\u7684\u5b9e\u73b0">Mixin \u6a21\u5f0f\u7684\u5b9e\u73b0</h2>\n<p>Mixin \u6307\u7684\u662f\u591a\u4e2a\u5bf9\u8c61\u5408\u6210\u4e00\u4e2a\u65b0\u7684\u5bf9\u8c61\uff0c\u65b0\u5bf9\u8c61\u5177\u6709\u5404\u4e2a\u7ec4\u6210\u6210\u5458\u7684\u63a5\u53e3\u3002\u5b83\u7684\u6700\u7b80\u5355\u5b9e\u73b0\u5982\u4e0b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">const</span> a = <span class="hljs-comment">{\n  a: \'a\'\n}</span>;\n<span class="hljs-keyword">const</span> b = <span class="hljs-comment">{\n  b: \'b\'\n}</span>;\n<span class="hljs-keyword">const</span> c = <span class="hljs-comment">{...a, ...b}</span>; <span class="hljs-comment">// {a: \'a\', b: \'b\'}</span></code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>c</code>\u5bf9\u8c61\u662f<code>a</code>\u5bf9\u8c61\u548c<code>b</code>\u5bf9\u8c61\u7684\u5408\u6210\uff0c\u5177\u6709\u4e24\u8005\u7684\u63a5\u53e3\u3002</p>\n<p>\u4e0b\u9762\u662f\u4e00\u4e2a\u66f4\u5b8c\u5907\u7684\u5b9e\u73b0\uff0c\u5c06\u591a\u4e2a\u7c7b\u7684\u63a5\u53e3\u201c\u6df7\u5165\u201d\uff08mix in\uff09\u53e6\u4e00\u4e2a\u7c7b\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-keyword">function</span> mix(...mixins) {\n  <span class="hljs-keyword">class</span> Mix {}\n\n  for (<span class="hljs-keyword">let</span> mixin <span class="hljs-keyword">of</span> mixins) {\n    copy<span class="hljs-constructor">Properties(Mix, <span class="hljs-params">mixin</span>)</span>; <span class="hljs-comment">// \u62f7\u8d1d\u5b9e\u4f8b\u5c5e\u6027</span>\n    copy<span class="hljs-constructor">Properties(Mix.<span class="hljs-params">prototype</span>, <span class="hljs-params">mixin</span>.<span class="hljs-params">prototype</span>)</span>; <span class="hljs-comment">// \u62f7\u8d1d\u539f\u578b\u5c5e\u6027</span>\n  }\n\n  return Mix;\n}\n\n<span class="hljs-keyword">function</span> copy<span class="hljs-constructor">Properties(<span class="hljs-params">target</span>, <span class="hljs-params">source</span>)</span> {\n  for (<span class="hljs-keyword">let</span> key <span class="hljs-keyword">of</span> <span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Reflect</span>.</span></span>own<span class="hljs-constructor">Keys(<span class="hljs-params">source</span>)</span>) {\n    <span class="hljs-keyword">if</span> ( key !== <span class="hljs-string">"constructor"</span><span class="hljs-operator">\n      &amp;&amp; </span>key !== <span class="hljs-string">"prototype"</span><span class="hljs-operator">\n      &amp;&amp; </span>key !== <span class="hljs-string">"name"</span>\n    ) {\n      <span class="hljs-keyword">let</span> desc = <span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>get<span class="hljs-constructor">OwnPropertyDescriptor(<span class="hljs-params">source</span>, <span class="hljs-params">key</span>)</span>;\n      <span class="hljs-module-access"><span class="hljs-module"><span class="hljs-identifier">Object</span>.</span></span>define<span class="hljs-constructor">Property(<span class="hljs-params">target</span>, <span class="hljs-params">key</span>, <span class="hljs-params">desc</span>)</span>;\n    }\n  }\n}</code></pre>\n<p>\u4e0a\u9762\u4ee3\u7801\u7684<code>mix</code>\u51fd\u6570\uff0c\u53ef\u4ee5\u5c06\u591a\u4e2a\u5bf9\u8c61\u5408\u6210\u4e3a\u4e00\u4e2a\u7c7b\u3002\u4f7f\u7528\u7684\u65f6\u5019\uff0c\u53ea\u8981\u7ee7\u627f\u8fd9\u4e2a\u7c7b\u5373\u53ef\u3002</p>\n<pre><code class="language-javascript"><span class="hljs-class"><span class="hljs-keyword">class</span> <span class="hljs-title">DistributedEdit</span> <span class="hljs-keyword">extends</span> <span class="hljs-title">mix</span>(<span class="hljs-params"><span class="hljs-type">Loggable</span>, <span class="hljs-type">Serializable</span></span>) </span>{\n  <span class="hljs-comment">// ...</span>\n}</code></pre>\n'}}]);